INSERT INTO `oa_product` (`id`, `project_id`, `title`, `business_dept`, `subject_field`, `age`, `sales_price`, `peer_price`, `content`, `att_id`, `status`, `supplier`, `input_time`, `input_user`, `input_uname`, `audit_status`) VALUES
(243, 0, '中国科学院科学探索营之【西安濒危动物】科考', '2,27,36,45,48', 0, NULL, '', NULL, '', '491', 0, NULL, 1464138490, 20, '余晗', 1),
(244, 0, '2016暑期学趣班-地质', '35', 3, '1,2', '', NULL, '<p style=\"text-align:center\"><strong><span style=\"font-size:22px\">2016暑期学趣班-地质</span></strong></p>\r\n\r\n<p><strong>相关知识点：</strong></p>\r\n\r\n<p><strong>1、常见宝石：岩浆泡泡、梦幻之石、绿精灵与蓝精灵等相关知识的讲解。</strong></p>\r\n\r\n<p><strong>2、地质构造：小石头大皱纹、大陆运动的痕迹、水秘方、地下揪宝和立体沙盘等内容的学习。</strong></p>\r\n\r\n<p><strong>课程主要内容简介：</strong></p>\r\n\r\n<p><strong>1、宝石的沧桑历史：</strong>本节课主要认识一下造岩矿物中的宝石。探秘普通的石头蜕变成璀璨的宝石的沧桑经历。不同种类的石头有不同的经历。它们存在地壳的不同区域内。</p>\r\n\r\n<p><strong>2、探秘亿万光阴的地质奇观：</strong>&nbsp;详述几十亿年来北京地区的沧海桑田的地质演化与变迁的过程，介绍地质公园中所呈现的各式各样的地质遗迹。</p>\r\n\r\n<p><strong>3、揪宝活动：</strong>人走过会留下足迹，同样的道理，地壳运动也会留下它的痕迹。那么地壳运动会留下那些痕迹呢？通过矿产存在的场景假设，学生亲自动手找矿产，找到矿产的小组为获胜者。</p>\r\n\r\n<p><strong>4、基础课《诠释碧玺密码》：</strong>彩虹宝石----碧玺 ，为何能霸彩宝界。碧玺学名电气石，属于次宝石，也就是不属于严格意义上的宝石。颜色丰富、颗粒巨大，红、蓝宝石达不到那么大的粒度。因含有不同的元素成分显示不同的颜色。由于含锰而呈红到粉红色；由于含铁而呈蓝色；由于含铬和钒元素而呈绿色。不仅如此，它还是与中国人渊源深厚的宝石。</p>\r\n\r\n<p><strong>5、科学小实验&mdash;碧玺也疯狂​：</strong>进行溶液中重金属离子（主要是对Cu2+）的吸附性实验。研究影响吸附性的各种外部因素（如溶液浓度、样品粒度、吸附时间及温度等）。等量电气石球加入等浓度的CuSO4溶液中，根据氢氧化铜进行验证。等量的电气石粉加入等浓度的CuSO4溶液中，根据氢氧化铜沉淀进行验证。探讨样品粒度对电气石吸附效果的影响。</p>\r\n\r\n<p>动手小实验----自制净水器，探讨碧玺的应用前景。</p>\r\n\r\n<p><strong>6、《天然水晶的秘密》：</strong>天然水晶中含有大量的固态包裹体，这些包裹体不但具有较高的观赏价值和不同的经济价值，同时也有很高的科学价值，通过对水晶中不同包裹体的辨认，研究不同天然水晶的特性。比一比，猜一猜，看一看，谁最炫。</p>\r\n\r\n<p><strong>7、岩石、沙子和土壤：</strong>讲述岩石沙子和土壤与我们的关系。通过观察遥感影像图，制作室内立体沙盘。</p>\r\n\r\n<p><strong>8、疯狂的原始人玩转疯狂的石头外出活动。</strong></p>\r\n\r\n<p><strong>9、平谷大溶洞科考外出活动。</strong></p>\r\n', '497', 0, NULL, 1464224507, 17, '夏奎菊', 1),
(245, 0, '地质课程（5天地质室内课程）', '35', 3, '1,2', '', NULL, '<p>地质课程（2016学趣班6天地质）</p>\r\n\r\n<p>课程内容：</p>\r\n\r\n<p>1、普通的石头蜕变成璀璨的宝石的沧桑经历。</p>\r\n\r\n<p>2、宝石的母亲--岩浆泡泡（通过生辰记宝石、名贵宝石的认知及特征分析）</p>\r\n\r\n<p>3、彩虹宝石---碧玺</p>\r\n\r\n<p>4、天然宝石--水晶</p>\r\n\r\n<p>5、探秘地球深处</p>\r\n\r\n<p>不同种类的石头有不同的经历。它们存在地壳的不同区域内，它们要经历哪些沧桑才能变成美丽的风景（宝石和自然风光）。亿万光阴的地质奇观，&nbsp;详述几十亿年来北京地区的沧海桑田的地质演化与变迁的过程，介绍地质公园中所呈现的各式各样的地质遗迹。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>详见课表</p>\r\n\r\n<p>&nbsp;</p>\r\n', '553', 0, NULL, 1464228616, 17, '夏奎菊', 1),
(270, 0, '2016“塞北猎星”天文夏令营活动', '2,36', 3, '2,3,4', '', NULL, '<p><span style=\"font-size:18px\">2016&ldquo;塞北猎星&rdquo;天文夏令营活动</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>组织单位</strong></p>\r\n\r\n<p>主办：中科科学文化传播发展中心</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>活动内容</strong></p>\r\n\r\n<p>草原观星：深空天体摄影与系统教学，天文风光摄影探究</p>\r\n\r\n<p>科研台站：参访国家天文台明安图观测站、明安图天文馆</p>\r\n\r\n<p>人文地理：探访内蒙古自然人文地理与蒙元天文文化</p>\r\n\r\n<p>摄影教学：深空天体后期处理</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>行程和活动安排</strong></p>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\" style=\"width:631px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:89px\">\r\n			<p><strong>日期</strong></p>\r\n			</td>\r\n			<td style=\"width:372px\">\r\n			<p><strong>参考活动</strong></p>\r\n			</td>\r\n			<td style=\"width:76px\">\r\n			<p><strong>用餐</strong></p>\r\n			</td>\r\n			<td style=\"width:95px\">\r\n			<p><strong>住宿</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:89px\">\r\n			<p>第一天</p>\r\n\r\n			<p>7月28日</p>\r\n			</td>\r\n			<td style=\"width:372px\">\r\n			<p>上午：学校集合出发，赴塞罕坝国家森林公园（车程）。</p>\r\n\r\n			<p>下午：赴内蒙古乌兰布统草原，在草原活动，拍摄风光。</p>\r\n\r\n			<p>晚上：住宿公主湖蒙古包，拍摄星空。</p>\r\n\r\n			<p><strong>沙龙活动：深空天体的拍摄基础</strong></p>\r\n			</td>\r\n			<td style=\"width:76px\">\r\n			<p>晚</p>\r\n			</td>\r\n			<td style=\"width:95px\">\r\n			<p>乌兰布统</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:89px\">\r\n			<p>第二天</p>\r\n\r\n			<p>7月29日</p>\r\n			</td>\r\n			<td style=\"width:372px\">\r\n			<p>上午：早餐后，休息充电，在乌兰布统草原活动。</p>\r\n\r\n			<p>下午：赴克什克腾旗热水镇。</p>\r\n\r\n			<p>晚上：夜宿蒙古包观星、拍摄。</p>\r\n\r\n			<p><strong>沙龙活动：赤道仪操作与导星系统</strong></p>\r\n			</td>\r\n			<td style=\"width:76px\">\r\n			<p>早中晚</p>\r\n			</td>\r\n			<td style=\"width:95px\">\r\n			<p>克什克腾旗</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:89px\">\r\n			<p>第三天</p>\r\n\r\n			<p>7月30日</p>\r\n			</td>\r\n			<td style=\"width:372px\">\r\n			<p>上午：休息充电，整理照片。</p>\r\n\r\n			<p>下午：探访阿斯哈图世界地质公园，拍摄风光。</p>\r\n\r\n			<p>晚上：夜宿蒙古包观星、拍摄。</p>\r\n\r\n			<p><strong>沙龙活动：实际拍摄指导，深空天体观测</strong></p>\r\n			</td>\r\n			<td style=\"width:76px\">\r\n			<p>早中晚</p>\r\n			</td>\r\n			<td style=\"width:95px\">\r\n			<p>克什克腾旗</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:89px\">\r\n			<p>第四天</p>\r\n\r\n			<p>7月31日</p>\r\n			</td>\r\n			<td style=\"width:372px\">\r\n			<p>上午：休息充电，整理照片。</p>\r\n\r\n			<p>下午：探访白音敖包自然保护区、达里诺尔湖。</p>\r\n\r\n			<p>晚上：夜宿蒙古包观星、拍摄。</p>\r\n\r\n			<p><strong>沙龙活动：拍摄后期指导</strong><strong>1</strong></p>\r\n			</td>\r\n			<td style=\"width:76px\">\r\n			<p>早中晚</p>\r\n			</td>\r\n			<td style=\"width:95px\">\r\n			<p>克什克腾旗</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:89px\">\r\n			<p>第五天</p>\r\n\r\n			<p>8月1日</p>\r\n			</td>\r\n			<td style=\"width:372px\">\r\n			<p>上午：赴正蓝旗，参观世界遗产元大都遗址（含回回司天文台遗址）。</p>\r\n\r\n			<p>下午：赴正镶白旗（明安图镇）。</p>\r\n\r\n			<p>晚上：闭营仪式，总结收获。</p>\r\n\r\n			<p><strong>沙龙活动：拍摄后期指导</strong><strong>2</strong></p>\r\n			</td>\r\n			<td style=\"width:76px\">\r\n			<p>早中晚</p>\r\n			</td>\r\n			<td style=\"width:95px\">\r\n			<p>明安图</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:89px\">\r\n			<p>第六天</p>\r\n\r\n			<p>8月2日</p>\r\n			</td>\r\n			<td style=\"width:372px\">\r\n			<p>上午：参观明安图天文馆、中科院国家天文台明安图观测站。</p>\r\n\r\n			<p>下午：乘车返回北京。</p>\r\n			</td>\r\n			<td style=\"width:76px\">\r\n			<p>早中</p>\r\n			</td>\r\n			<td style=\"width:95px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n', '569', 0, NULL, 1468217594, 18, '戴明', 1),
(246, 0, '天文｜2016暑期学趣班', '35', 3, '1,2', '', NULL, '<p><strong>2016年暑期托管班&mdash;&mdash;天文</strong></p>\r\n\r\n<p>2016年计划再次开设暑期天文托管班，丰富&ldquo;学趣&rdquo;暑期课程的种类，让家长和孩子们有更多的选择。暑期天文托管班本着趣味至上、参与第一的宗旨，针对低年级孩子特别设计，集合了漫游星空课题、天文小制作、太阳系探索、望远镜操作与实际观测等项目，努力打造一个课程内容丰富多彩、家长放心孩子满意的暑期托管班。</p>\r\n\r\n<p><strong>主要特色：</strong></p>\r\n\r\n<p>1、针对低龄孩子设计的入门课程，适当控制理论知识比例，强调趣味性。</p>\r\n\r\n<p>2、丰富而多变的天文动手操作实验，让孩子们在玩儿中学习。</p>\r\n\r\n<p>3、互动PPT与影片，使课堂气氛更加活跃。</p>\r\n\r\n<p>4、延续性：孩子们通过天文课的学习打下基础，为将来学习天文摄影做铺垫。</p>\r\n', '500', 0, NULL, 1464244330, 18, '戴明', 1),
(247, 0, '天文｜暑期天文活动（一日版）', '35', 3, '1,2', '', NULL, '<p>可用于单日天文活动，也可用于夏令营的某一环节。</p>\r\n', '501', 0, NULL, 1464244674, 18, '戴明', 1),
(248, 0, '【科技节力学所项目】红外遥控小车', '11', 3, '2,3', '', NULL, '<p>涉及学科：物理</p>\r\n\r\n<p>操作模式：展示类</p>\r\n\r\n<p>功能：可以实现自动巡线，自动躲避障碍物，红外线控制，蓝牙控制等功能。</p>\r\n\r\n<p>原理：主要利用了Arduino单片机开发，多种传感器共同作用实现相关功能。</p>\r\n\r\n<p>尺寸：高25cm， 长15cm， 宽15cm</p>\r\n\r\n<p>个数：2个（或4个）</p>\r\n', '502', 0, NULL, 1464326524, 21, '付晓鑫', 1),
(249, 0, '【科技节力学所项目】涡流演示仪', '11', 3, '2,3', '', NULL, '<p>涉及学科：物理</p>\r\n\r\n<p>操作模式：展示类</p>\r\n\r\n<p>功能：分别为&ldquo;自由涡&rdquo;、&ldquo;强迫涡&rdquo;、&ldquo;蓝金组合涡&rdquo;。</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; 可以演示龙卷风（澡盆涡），强迫涡以及两者的结合。</p>\r\n\r\n<p>资源要求：需要至少2.5米长，0.8米宽的桌面。</p>\r\n\r\n<p>注意事项：需要供电，搬运一定要轻拿轻放</p>\r\n', '503', 0, NULL, 1464327754, 21, '付晓鑫', 1),
(250, 0, '【科技节力学所项目】自然能水泵（水锤扬水机）', '11', 3, '2,3', '', NULL, '<p>涉及学科：物理</p>\r\n\r\n<p>操作模式：展示类</p>\r\n\r\n<p>功能：可以演示如何在不适用电或者油等能源的情况下，将低处的水抽到高处，完全环保的可持续运作机械。</p>\r\n\r\n<p>尺寸：需要至少长1米，宽0.5米的桌面。</p>\r\n\r\n<p>要求：需要供电，搬运一定要轻拿轻放</p>\r\n', '504', 0, NULL, 1464328777, 21, '付晓鑫', 1),
(251, 0, '【科技节力学所项目】大型可测控模型火箭', '11', 3, '2,3', '', NULL, '<p>涉及学科：物理</p>\r\n\r\n<p>操作模式：展示类</p>\r\n\r\n<p>简介：火箭模型总长度1米，直径0.1米，底部装载5枚C系列固体火箭发动机，捆绑式并联方式发射，满负载飞行高度约为100~150米，接近40~50层楼高。</p>\r\n\r\n<p><span style=\"line-height:1.6em\">箭体构造完全模拟长征系列运载火箭，包括整流罩、返回舱、伞降舱、燃料仓等，辅助以发射架和遥控点火器，可以实现远程读秒操控发射，最后通过伞降舱中的降落伞实现火箭回收，不会损坏箭体和相关设备。</span></p>\r\n\r\n<p><span style=\"line-height:1.6em\">在返回舱内装备有各种传感器测量模块，能够实时监测火箭的高度、速度、加速度、角速度、温度、姿态等，并且通过搭载在返回舱内的射频模块，可以与地面的控制中心（笔记本操控台）实现远距离实时数据传输，我们在地面便可以获得火箭在飞行过程中的全部参数，相当于一个飞行控制中心。同时火箭箭体上安装的两个微型摄像头，也会记录飞行时的水平和垂直两个方向的视频录像。最终发射结束后，将所有的传感器实验数据整理，导入编辑好的软件程序，就可以将整个火箭的飞行过程复现出来。</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>注意事项：如果要现场发射，需要提前沟通，提前至少1周预约。如果仅仅演示结构和功能，需要一台笔记本和相关培训。</p>\r\n', '505', 0, NULL, 1464328960, 21, '付晓鑫', 1),
(252, 0, '【科技节力学所项目】实验级小型低速风洞', '11', 3, '2,3', '', NULL, '<p>涉及学科：物理</p>\r\n\r\n<p>操作模式：展示类</p>\r\n\r\n<p>功能：可以演示风洞的基本功能，测量流场，搭配烟油可以做圆柱扰流、烟线流场显示、PIV流场测量、翼型升阻力测量、飞行器模型动稳定测试等试验。</p>\r\n\r\n<p>资源要求：供电，且体型较大，长3米，宽0.8米</p>\r\n\r\n<p>&nbsp;</p>\r\n', '506', 0, NULL, 1464329146, 21, '付晓鑫', 1),
(253, 0, '【科技节力学所项目】CubePro 3D打印机', '11', 3, '2,3', '', NULL, '<p>涉及学科：物理</p>\r\n\r\n<p>操作模式：展示类</p>\r\n\r\n<p>功能：可以现场打印，并且有多种已经打印好的素材供展示使用。</p>\r\n\r\n<p>资源要求：供电，需要笔记本电脑并提前安装软件和调试</p>\r\n', '507', 0, NULL, 1464329300, 21, '付晓鑫', 1),
(254, 0, '【科技节力学所项目】拉伸试验机', '11', 3, '2,3', '', NULL, '<p>涉及学科：物理</p>\r\n\r\n<p>操作模式：展示类</p>\r\n\r\n<p>功能：可以测量不同材料的拉伸和承压特性，了解不同结构和不同材料的拉伸变形，测量不同破损位置的结构的在多大力的作用下会被拉断。</p>\r\n\r\n<p>资源要求：至少需要0.5米x0.5米的桌面</p>\r\n\r\n<p>&nbsp;</p>\r\n', '508', 0, NULL, 1464329462, 21, '付晓鑫', 1),
(255, 0, '学趣暑期班博物馆版', '35', 0, '1,2', '', NULL, '<p>学趣暑期班博物馆版</p>\r\n\r\n<p>一.时间</p>\r\n\r\n<p>五天</p>\r\n\r\n<p>二.课程安排</p>\r\n\r\n<p>每日课程安排：半天理论课程+动手实验+半天博物馆活动</p>\r\n\r\n<p>三.博物馆活动名单</p>\r\n\r\n<p>第一天----学科：航空航天-----博物馆：小汤山航空馆</p>\r\n\r\n<p>第二天----学科：植物---------博物馆：自然博物馆</p>\r\n\r\n<p>第三天----学科：地质---------博物馆：地质博物馆</p>\r\n\r\n<p>第四天----学科：环保---------博物馆：自来水博物馆</p>\r\n\r\n<p>第二天----学科：动物---------博物馆：国家动物博物馆</p>\r\n\r\n<p>四.材料</p>\r\n\r\n<p>初步按每班30人计算，后期按实际情况调整</p>\r\n', '509', 0, NULL, 1464575011, 20, '余晗', 1),
(256, 0, '房山昆虫采集', NULL, 1, NULL, '', NULL, '', '', 0, NULL, 1464583260, 16, '黄鑫磊', 1),
(257, 0, '【科技节项目】三棱镜分离彩虹', '11', 0, NULL, '215元', NULL, '<p>介绍：300多年以前，牛顿用三棱镜将白光分解成七色光之后，还发现七色光能继续分解成三原色光。我们将重复牛顿的伟大实验</p>\r\n\r\n<p>原理：因为同一种介质对各种单色光的折射率不同，所以通过三棱镜时，各单色光的偏折角不同。因此，白色光通过三棱镜会将各单色光分开，形成红.橙.黄.绿.蓝.靛.紫七种色光即色散。</p>\r\n\r\n<p>类型：体验类</p>\r\n', '', 0, NULL, 1465199760, 22, '胡逸萍', 1),
(258, 0, '【科技节项目】溶洞时光', '11', 0, NULL, '314元/20人', NULL, '<p>介绍：石头能生长，不能不说是神奇的，但人们又怎能感到或觉察到钟乳石和石笋的发育生长呢？利用化学试剂模拟溶洞，想不想知道是怎么做的？一起来试一下吧！</p>\r\n\r\n<p>原理：在特殊的环境下，才能开成溶洞，而溶洞里又因为重力水的作用下，生出了石钟乳、石笋、石柱。模拟小实验见证钟乳石生成过程。先是CaCO3+CO2+H2O=Ca(HCO3)2 碳酸氢钙是溶于水的，顺着石头往下滴 在即将滴下来的时候受热分解 Ca(HCO3)2 ====CaCO3&darr;+H2O+CO2&uarr; 这里受热可以是瞬时间温度小幅度上升，或者碰撞等 生成的沉淀就附在石头上，渐渐形成钟乳石</p>\r\n\r\n<p>类型：体验类</p>\r\n\r\n<p><img src=\"file:///C:UsersThinkDocumentsTencent Files599135490ImageC2C14II$6(0OOGUHD({SKH5Z%9.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n', '520,521,524', 0, NULL, 1465201225, 22, '胡逸萍', 1),
(260, 0, '【科技节项目】自制酸碱指示剂', '11', 0, NULL, '', NULL, '<p><span style=\"color:rgb(62, 62, 62); font-family:微软雅黑,sans-serif; font-size:16px\">介绍：那么如何巧妙地提取花瓣中的&ldquo;颜色&rdquo;对酸碱性进行鉴别呢？我们来探究一种方法。</span></p>\r\n\r\n<p><span style=\"color:rgb(62, 62, 62); font-family:微软雅黑,sans-serif; font-size:16px\">原理：</span><span style=\"font-size:16px\">许多植物的花、果、茎、叶中都含有色素，主要是花青素。花青素在不同的</span><span style=\"font-size:16px\">酸碱环境中，能呈现出不同的颜色，因此，可利用来做酸碱指示剂。</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\">利用紫甘蓝汁液制作酸碱指示剂</span></p>\r\n\r\n<p><span style=\"font-size:16px\">制作方法：将紫甘蓝挤压成黑紫色色汁液或将紫甘蓝叶在开水中浸泡数小时（泡出的液体呈暗紫色 ）</span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '', 0, NULL, 1465288313, 22, '胡逸萍', 1),
(259, 0, '【科技节项目】液氮冰淇淋', NULL, 0, NULL, '241元/20人', NULL, '<p>原理：氮的沸点为-196&deg;C，液氮是氮气在低温下形成的液体形态。在正常大气压下温度如果低于-196&deg;C就会形成液氮；如果加压，可以在更高的温度下得到液氮。当液氮接触冰淇淋原料后，由于液氮快速汽化而从原料和中快速吸收热量。超低温和高热交换率实现了真正的快速冷冻。</p>\r\n\r\n<p>步骤：</p>\r\n\r\n<p><strong>1.</strong>&nbsp;把除了液氮以外的东西搅拌均匀。</p>\r\n\r\n<p><strong>2.</strong>&nbsp;向碗里倒入液氮，一定要用防冻手套，露指手套不行。</p>\r\n\r\n<p><strong>3.</strong>&nbsp;连续搅动，直到形成一层硬壳。</p>\r\n\r\n<p><strong>4.</strong>&nbsp;当均匀无结块时，冰激凌就做好，可以吃了。</p>\r\n', '', 0, NULL, 1465201871, 22, '胡逸萍', 1),
(268, 0, '甲虫的故事课程', '3,38', 1, '1,2,99', '', NULL, '<p style=\"text-align:center\"><strong>甲虫世界</strong></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"upload/201606/13/575e0530722e8.jpg\" style=\"height:300px; width:200px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>课程里会介绍到：常见的鞘翅目昆虫，犀金龟科，花金龟科，锹甲科，吉丁虫科，天牛亚科，象甲科，叶甲科，步甲科等等。每个类群的甲虫都会一一介绍。甲虫的个体差异很大，从巨大的犀金龟，到微小的蛛甲。它们的体积差距好几百倍。甲虫的颜色是丰富多彩，各种颜色汇聚于不同的甲虫体表。</p>\r\n\r\n<p>观察甲虫的活体，了解它的习性。观察甲虫标本，学习甲虫的知识，</p>\r\n\r\n<p>自己动手制作一个甲虫标本。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>制作一只双叉犀金龟的标本，学习它的制作方法，仔细观察犀金龟的外形。看看它和别的昆虫有什么不同。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>整体讲座时间2个小时左右。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '532', 0, NULL, 1465779553, 16, '黄鑫磊', 1),
(269, 0, '昆虫采集活动', '2,27,31,37,38,39', 1, '1,2,3,4,99', '', NULL, '<p>昆虫采集需要的各种材料：</p>\r\n\r\n<p>昆虫网，昆虫瓶，三角包，硫酸纸，昆虫针等。</p>\r\n', '', 0, NULL, 1467179042, 16, '黄鑫磊', 1),
(261, 0, '【科技节项目】水果提取DNA', NULL, 0, NULL, '', NULL, '<p><span style=\"color:rgb(62, 62, 62); font-family:微软雅黑,sans-serif; font-size:16px\">介绍：想要了解生物的本质，当然离不开DNA。DNA作为生物的遗传物质，在&ldquo;龙生龙凤生凤老鼠的儿子会打洞&rdquo;这句谚语中得到了完美的体现。提取DNA听起来很高深，但只要愿意动手，人人都能轻松入门。</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\">以<span style=\"font-family:微软雅黑\">下转自果壳网，网址</span></span><span style=\"font-family:微软雅黑\"><span style=\"font-size:16px\">http://www.guokr.com/blog/745781/</span></span></p>\r\n\r\n<p><span style=\"font-family:microsoft yahei,wenquanyi micro hei,tahoma,sans-serif; font-size:16px\">【实验材料】</span><br />\r\n<span style=\"font-family:microsoft yahei,wenquanyi micro hei,tahoma,sans-serif; font-size:16px\">你需要准备：新鲜的草莓、香蕉、菠萝，保鲜袋、大汤匙、小勺子、95%浓度的酒精（药店有售）、冰块、矿泉水、玻璃杯、洗洁精、滤网、量杯、碗、牙签（或镊子）、厨房用电子称</span></p>\r\n\r\n<p><span style=\"font-family:microsoft yahei,wenquanyi micro hei,tahoma,sans-serif; font-size:16px\">注明：酒精属于易燃物，因此请在成年人的帮助下使用和存放酒精。</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:微软雅黑\"><span style=\"font-size:16px\">第一关：调制DNA提取液<br />\r\n请你用电子称称取3g食盐，将其加入80ml矿泉水中，搅拌至溶解。然后另取一个量杯，量取10ml洗涤剂，倒入盐水中，再滴加少量矿泉水，使液体的总体积达到100ml。轻轻搅拌混合液体，防止产生过量泡沫。眼前这杯看似其貌不扬的透明液体便是实验中的关键试剂&mdash;&mdash;DNA提取液。</span></span></p>\r\n\r\n<p><span style=\"font-family:微软雅黑\"><span style=\"font-size:16px\">原理：洗涤剂的主要成分是具有去垢作用的表面活性剂。这类兼具&ldquo;亲水性&rdquo;和&ldquo;亲油性&rdquo;的化学成分可以击破细胞的磷脂双分子层结构，把DNA从细胞核内释放出来。</span></span></p>\r\n\r\n<p><span style=\"font-family:microsoft yahei,wenquanyi micro hei,tahoma,sans-serif; font-size:16px\">第二关：制作果肉&ldquo;酱汁&rdquo;</span><br />\r\n<span style=\"font-family:microsoft yahei,wenquanyi micro hei,tahoma,sans-serif; font-size:16px\">剥去香蕉皮和草莓的果梗和蒂头，用电子称分别称量50g果肉，然后用大汤匙将它们碾压成黏黏糊糊的&ldquo;果酱&rdquo;。将香蕉酱和草莓酱倒入两个保鲜袋中保存。</span><br />\r\n<span style=\"font-family:microsoft yahei,wenquanyi micro hei,tahoma,sans-serif; font-size:16px\">把配置好的DNA提取液（每份100ml）倒入果酱中，搅拌均匀之后，让混合物先在大约60度的热水里泡15分钟&ldquo;温泉&rdquo;，再转移到冰水中，洗15分钟的&ldquo;冷水澡&rdquo;。</span></p>\r\n\r\n<p><span style=\"font-family:microsoft yahei,wenquanyi micro hei,tahoma,sans-serif; font-size:16px\">原理：在植物组织中，细胞通常会拥挤地聚集在一起。而在糊状的果酱里，它们的活动范围得以扩展，每个细胞都可以拥有自己独立的&ldquo;私人空间&rdquo;。这种分散的状态为洗涤剂提供了更多的&ldquo;进攻&rdquo;机会。同时，热水浴也进一步加快了反应的速率。需要注意的是，过度的高温加热会使DNA分子破碎分解。因此，一段时间之后，我们需要将细胞取出，投入冰水中冷却降温。</span></p>\r\n\r\n<p><span style=\"font-family:microsoft yahei,wenquanyi micro hei,tahoma,sans-serif; font-size:16px\">第三关：滤取DNA溶液</span><br />\r\n<span style=\"font-family:microsoft yahei,wenquanyi micro hei,tahoma,sans-serif; font-size:16px\">请你将筛网放在瓷碗上，缓缓倒入捣碎的&ldquo;果肉酱汁&rdquo;，将较为澄清的液体滤至碗中，让残存的颗粒滞留在筛网上。</span></p>\r\n\r\n<p><span style=\"font-family:微软雅黑\"><span style=\"font-size:16px\">原理：相对于固态的果肉颗粒，水果汁液明显更便于操作和提纯。因此，我们要筛去植物组织和其他的细胞残余，只保留着富含DNA的滤液。</span></span></p>\r\n\r\n<p><span style=\"font-family:微软雅黑\"><span style=\"font-size:16px\">第四关：去除组蛋白杂质<br />\r\n你可以通过挤压新鲜的菠萝果肉来获取汁液。等杯底大约积攒了5ml菠萝汁之后，将这些酸溜溜的液体倒入水果滤液中，缓缓地搅拌均匀。</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:微软雅黑\">原理：在细胞核中，DNA分子必须与组蛋白一起协同合作，才能完成的调控遗传讯息的艰巨使命。组蛋白会与DNA双螺旋结合，拼装成DNA-组蛋白复合物，并进一步构成复杂的染色体结构。而菠萝中含有丰富的菠萝蛋白酶，这种纯天然的植物蛋白酶能够水解组蛋白，从而使DNA提取物变得更加纯净。</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:微软雅黑\">第四步：提取&ldquo;实物&rdquo;DNA<br />\r\n用量杯分别量取15毫升冰酒精和等量的DNA溶液。沿着容器边缘将冰酒精缓缓倒入富含DNA的液体中。两种液体随即发生了分层，气味浓烈的酒精&ldquo;漂&rdquo;在上层，密度较大的DNA提取液&ldquo;沉&rdquo;在下层。</span></span></p>\r\n\r\n<p><span style=\"font-family:microsoft yahei,wenquanyi micro hei,tahoma,sans-serif; font-size:16px\">仔细观察上层清体。无色透明的液体中逐渐出现一些极微小的气泡和白色颗粒。大约2-3分钟之后，你便可以清楚地观察到有大量絮状物悬浮于酒精中。</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:微软雅黑\">凝聚的白色絮状物就是DNA哦~虽然其中还含有很多杂质，但好歹也是一坨肉眼可见的神奇物质~你可以用牙签或者镊子将&ldquo;战利品&rdquo;挑出，向父母和同学好好炫耀一下。</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:微软雅黑\">原理：DNA是可溶于水的极性分子，在红彤彤的滤液中，我们无法用肉眼直接观察到它们的踪影。而酒精则属于非极性的有机溶剂，根据相似相溶原理，DNA在其中不能溶解，且在低温状态下，物质的溶解度还会进一步降低。因此，当我们将酒精与滤液混合之后，DNA分子会沉淀析出，在上层清液中形成一片乳白色的絮状物。</span></span></p>\r\n\r\n<p><span style=\"color:rgb(102, 102, 102); font-family:arial,helvetica,sans-serif; font-size:12px\">以上由</span><a href=\"http://www.guokr.com/i/0997296078/\" style=\"color: rgb(0, 120, 182); text-decoration: none; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px;\">Jessie_H</a><span style=\"color:rgb(102, 102, 102); font-family:arial,helvetica,sans-serif; font-size:12px\">授权（</span><a href=\"http://www.guokr.com/\" style=\"color: rgb(0, 120, 182); text-decoration: none; font-family: Arial, Helvetica, sans-serif; font-size: 12px; line-height: 18px;\">果壳网</a><span style=\"color:rgb(102, 102, 102); font-family:arial,helvetica,sans-serif; font-size:12px\">）发表，文章著作权为原作者所有。</span></p>\r\n', '', 0, NULL, 1465289428, 22, '胡逸萍', 1),
(262, 0, '【科技节项目】扎染', '11', 0, NULL, '', NULL, '<p>介绍：扎染古称扎缬、绞缬、是中国民间传统而独特的染色工艺。织物在染色时部分扎结起来使之不能着色的一种染色方法，扎得越紧着色越淡，这也是扎染工艺的基本原理，是中国传统的手工染色技术之一。</p>\r\n\r\n<p>扎染工具：各种线、绳、带、网袋等；不同大小的缝衣针；不同形状的薄板；各种夹具等。</p>\r\n\r\n<p>制作步骤：</p>\r\n\r\n<p>1、将布扎成各种形状</p>\r\n\r\n<p>2、浸入煮开的染料锅内3-5分钟</p>\r\n\r\n<p>3、用清水洗净</p>\r\n\r\n<p>4、拆开</p>\r\n', '523', 0, NULL, 1465289461, 22, '胡逸萍', 1),
(263, 0, '【科技节项目】植物染色', '11', 0, NULL, '309', NULL, '<p><span style=\"font-size:16px\"><span style=\"font-family:微软雅黑\">介绍：</span></span><span style=\"color:rgb(62, 62, 62); font-family:微软雅黑,sans-serif; font-size:16px\">认识植物：植物谁不认识？可你真的够了解它吗？一起来学习植物的基本结构，认识不同的花、叶脉、叶形、根、茎！了解植物水分</span><span style=\"color:rgb(62, 62, 62); font-family:微软雅黑,sans-serif; font-size:16px\">输送途径及维管束相关知识，DIY彩色花朵。</span><span style=\"font-size:16px\"><span style=\"font-family:微软雅黑\">如彩虹般的花朵装饰着彩虹般的梦，每每看到花瓣从花的中心打开，一点一点环绕成成熟花朵的形状，都会被他们的美所融化，感受着来自神意的表达。今天教大家如何将白色玫瑰染成七彩的颜色，利用很简单的原料制作出意想不到的效果。</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:微软雅黑\">制作步骤：</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:微软雅黑\">1、 将所有的叶子都从花的根茎上拔掉，并将根茎底端斜着剪掉</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:微软雅黑\">2、 将食用色素或水彩颜料与水混合盛放在杯子里，颜料要放足，这样能够保证染出来的花颜色鲜艳</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:微软雅黑\">3、 若你想尝试轧染效果，可以将根茎切成两半，然后把两端分别放在不同的颜色杯里。你可以将根分成三份或四份，但这次我们只分了两份</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:微软雅黑\">4、 耐心等待，你慢慢会发现花朵的变化</span></span></p>\r\n', '', 0, NULL, 1465290548, 22, '胡逸萍', 1),
(264, 0, '【科技节项目】植物色素（外来项目）', '11', 0, NULL, '', NULL, '<p><span style=\"font-size:16px\"><span style=\"font-family:微软雅黑\">&nbsp;介绍：</span></span><span style=\"color:rgb(62, 62, 62); font-family:微软雅黑,sans-serif; font-size:16px\">动手制作植物扎染手帕，学习植物色素的相关知识。</span></p>\r\n\r\n<p>植物染色是指利用大自然中自然生长的各种含有色素的植物提取色素来对被染物进行染色的一种方法。是指使用天然染料染色，同时在染色过程中不使用或极少使用化学助剂，而使用从大自然中取得的天然染料，对产品进行染色的一种工艺。也称&ldquo;植物染色&rdquo;、&ldquo;草木染色&rdquo;。它包含植物性染料（如蓝靛）、动物性染料（如紫胶）及矿物性染料（如朱砂），其中以植物性染料的使用最为普遍，且可用的材料种类也最丰富。</p>\r\n\r\n<p>植物色素包括脂溶性的叶绿体色素和水溶性的细胞液色素，前者存在于叶绿体，与光合作用有关，如叶绿素；后者存在于液泡中，特别与花朵的颜色有关，如花青素属黄酮类物质。了解它们的性质有助于对其生理功能的理解。</p>\r\n\r\n<p>&nbsp;</p>\r\n', '', 0, NULL, 1465291029, 22, '胡逸萍', 1),
(265, 0, '【科技节项目】炫彩牛奶', '11', 0, NULL, '107', NULL, '<p><span style=\"color:rgb(62, 62, 62); font-family:微软雅黑,sans-serif; font-size:16px\">介绍：艺术美感在科学创造中往往能给人们意想不到的启迪。今天就让我们尝试着玩一把&ldquo;跨界混搭&rdquo;，用科学的方法表现抽象艺术，在香浓的牛奶画布上描绘出心中的美好愿景。</span></p>\r\n\r\n<p>原理：牛奶是一种复杂的混合物。这些看似&ldquo;单纯&rdquo;的白色液体中，实际上包含了水、脂肪、磷脂、蛋白质、乳糖及无机盐等众多成分。由于脂类物质与水不相溶，且密度较小，因此在牛奶中，脂肪是以一个个&ldquo;小乳滴&rdquo;的形式悬浮于上层水域，从而构成了一层比较稳定的&ldquo;油脂层&rdquo;。而我们在实验中所用到的食用色素属于水溶性物质，它的化学性质稳定，可以与水分子迅速&ldquo;打成一片&rdquo;。当色素被滴入液体画卷时，它们与上层的油脂类液滴较为&ldquo;疏远&rdquo;，不能快速溶解。且在牛奶表面液体张力的作用下，色素倾向于飘浮在最上层，维持自身固有的形态。当我们借助棉棒将洗洁精引入体系之后，粘稠的新朋友打破了画卷中原本平静的场景。洗洁精中主要成分&mdash;&mdash;表面活性剂能够破坏牛奶表层的张力，并与脂肪结成小的胶团颗粒，进一步改变分子外形，使液体内部发生变化。被搅动的牛奶带动色素翻滚运动，进而绘制出流畅的线条和曼妙的图样。生活中用洗涤剂去除污渍的原理也是如此。只是油污中没有彩色颜料标记，因此我们用肉眼难以察觉那些微妙的变化。</p>\r\n\r\n<p>1.开始实验，把牛奶倒进碗里。</p>\r\n\r\n<p>2.依次滴入红、黄、蓝、黑色素。</p>\r\n\r\n<p>3.在色素晕中间滴入一滴洗洁精，观察现象。</p>\r\n', '526', 0, NULL, 1465291651, 22, '胡逸萍', 1),
(266, 0, '【科技节项目】大象牙膏', NULL, 0, NULL, '207', NULL, '<p>介绍：我们平时使用的牙膏还不够大象一次刷牙的，那么咱们今天的任务就是帮大象设计一款足够大的牙膏&mdash;&mdash;大象牙膏！但是同学们要注意，这款牙膏不是真正的牙膏，甚至对牙齿还有腐蚀作用，所以千万不要拿来给大象或是自己刷牙哦！、</p>\r\n\r\n<p>原理：双氧水分解会产生氧气和水，但在常温并不容易自行反应，加入碘化钾可以催化反应的进行，让双氧水迅速产生氧气。</p>\r\n\r\n<p>步骤：</p>\r\n\r\n<p>1.在量筒中加入30ml浓度为3%的双氧水和适量洗洁精，并混入一定的色素。</p>\r\n\r\n<p>2. 将饱和碘化钾溶液（一匙的碘化鉀）迅速倒入盛着双氧水的细口瓶中，观察现象。</p>\r\n\r\n<p>3. 换上浓度高于30%的双氧水，重复上述步骤，观察实验现象。</p>\r\n\r\n<p>&nbsp;</p>\r\n', '525', 0, NULL, 1465291850, 22, '胡逸萍', 1),
(267, 0, '【科技节项目】热冰实验', '11', 0, NULL, '466', NULL, '<p><span style=\"color:rgb(62, 62, 62); font-family:微软雅黑,sans-serif; font-size:16px\">介绍：糖水与水有什么区别，从日常食品入手，介绍溶液、饱和溶液、不饱和溶液、过饱和溶液、结晶等的概念。冰是在温度较低的环境下产生的，如何利用厨房食品在炎热的夏天制作冰，而且是热乎乎的冰，一起来动动手吧！通过滴水成冰实验加深对过饱和溶液和结晶的理解。</span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:微软雅黑\">原理：</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:微软雅黑\">&bull;一定条件下，溶液中溶质的浓度已超过其溶解度，而溶质仍不析出的溶液称为过饱和溶液。</span></span></p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:微软雅黑\">&bull;过饱和溶液是不稳定的，只要引入一个结晶核或者剧烈震动都会使它快速结晶。</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:微软雅黑\">步骤：</span></span></p>\r\n\r\n<p>1、500 毫升烧杯中加入250 克未潮解的醋酸钠晶体和150 毫升蒸馏水。</p>\r\n\r\n<p>2、用微火加热，不断搅拌，使其完全溶解。</p>\r\n\r\n<p>3、趁热将溶液过滤到500 毫升洁净并干燥的烧杯中（注意！不能把溶液滴在烧瓶颈部）。</p>\r\n\r\n<p>4、在冰浴器或是冰箱中冷却30分钟至1小时。</p>\r\n\r\n<p>5、向冷却后的溶液中加少许苏打粉。也可以直接用手指或筷子点一下溶液。</p>\r\n\r\n<p>6、观察热冰的形成。</p>\r\n', '527', 0, NULL, 1465292553, 22, '胡逸萍', 1),
(271, 0, '海洋生物知识问答', '38,39,43', 1, '1,2,3,99', '', NULL, '<p style=\"text-align: center;\"><strong><span style=\"font-size:20px\">海洋生物知识问答</span></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\">选取和海洋生物有关的知识点，用提问抢答的方式进行，让小朋友在抢答的活动环节中学习和了解海洋生物的不同知识点，以及有趣的海洋生物特性。</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:16px\">活动需要对参与人员分组，进行答题卡式抢答。</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', '', 0, NULL, 1470705122, 16, '黄鑫磊', 1),
(272, 0, '天文｜独石口长城风光、人文与星空拍摄活动', '2,39', 3, '2,3,4', '', NULL, '<p><strong>活动日期：选择三天时间</strong></p>\r\n\r\n<p><strong>活动地点：河北赤城独石口镇</strong></p>\r\n\r\n<p><strong>活动内容：古建筑、风光、人文、星空</strong></p>\r\n\r\n<p><strong>活动组织：中科科学文化传播发展中心</strong></p>\r\n\r\n<p>独石口，明朝宣府镇上的一座重要关口，是&ldquo;上谷之咽喉，京师之右臂&rdquo;，位于赤城县西北、白河上游与沽源县接壤处。这里是古战场，历史上曾有多场战役发生于此，拿起你的相机，跟着我们去寻找一段沧桑的记忆，去领略一种沧桑之美&hellip;&hellip;</p>\r\n\r\n<p>拍点一：古长城和烽火台</p>\r\n\r\n<p>拍点二：冰川遗迹，山峦景色</p>\r\n\r\n<p>拍点三：日出日落和彩霞余晖、云海</p>\r\n\r\n<p>拍点四：天文风光摄影、深空天体摄影</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>行程安排：</strong></p>\r\n\r\n<p><strong>第一天</strong></p>\r\n\r\n<p>中午：13点学校集合出发，车程3小时，下午抵达独石口镇，安排住宿农家院（条件有限）。</p>\r\n\r\n<p>下午：前往独石口长城拍摄风光，回来后晚餐。</p>\r\n\r\n<p>晚上：（一）可前往独石口长城，扎营并拍摄星空，内容以星野摄影为主。（二）使用赤道仪拍摄深空的，可在农家院里驻扎拍摄。</p>\r\n\r\n<p><strong>第二天</strong></p>\r\n\r\n<p>上午：扎营的可以早起拍摄长城，如运气好可拍到云海，然后集中回到镇子吃早餐。上午休息，充电，或在镇子里自由活动。可以拍摄独石口镇里的人文、古城墙等。</p>\r\n\r\n<p>下午：午餐后处理照片、休息。三点钟出发，前往冰山梁景区创作，拍摄风光。</p>\r\n\r\n<p>晚上：按第一天晚上的安排。</p>\r\n\r\n<p><strong>第三天</strong></p>\r\n\r\n<p>上午：扎营组可早起在长城上拍摄风光，深空组可在镇里拍摄风光。集中早餐，收拾器材，集合返回北京。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>注：该行程避开了周末影友出行高峰，可安心拍摄。</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>温馨提示：</strong></p>\r\n\r\n<p>1、住宿饮食：地处山村，住宿条件较差，饮食一般，有特殊习惯者请自行准备。</p>\r\n\r\n<p>2、夜间拍摄必须使用红光手电照明，且在规定时间内不允许使用灯光。</p>\r\n\r\n<p>3、当地温度一般比北京低8-10度，昼夜温差大，晚上温度估计5度左右。常年有风，天气变化剧烈，建议羽绒服、冲锋衣裤等。</p>\r\n\r\n<p>4、选择在山上拍摄的同学要求必须带帐篷、防潮垫和睡袋，睡袋温标建议零度以下。建议购买自热米饭做夜宵，优点是无需生火，且余热可作为帐篷内的热源。</p>\r\n', '590', 0, NULL, 1471571512, 18, '戴明', 1),
(273, 0, '海外｜火山与星空——巴厘岛与布罗莫活火山摄影之旅', '2,41', 3, '3,4,99', '', NULL, '<p><strong>火山与星空｜巴厘岛与布罗莫活火山摄影之旅</strong><br />\r\n玩好、拍好、学好</p>\r\n\r\n<p>&nbsp;<br />\r\n<strong>独家私密海岛路线 （独家特色！）</strong><br />\r\n孤岛秘景，独一无二的自然风景地，拒绝千篇一律的景点创作。</p>\r\n\r\n<p><strong>海拔2329米，布罗莫活火山</strong><br />\r\n腾格尔山中心的一座活火山，有如月球表面般令人生畏的地貌，印尼最为壮观风景之一。</p>\r\n\r\n<p><strong>特色酒店&amp;名额限制</strong><br />\r\n舒适的住宿，专业摄影团，每期只招募10人，注重高品质行摄体验，确保高效摄影教学。</p>\r\n\r\n<p><strong>深入印尼本土文化特色拍摄</strong><br />\r\n穆斯林、火山守护者、本土纯朴人文。</p>\r\n\r\n<p><strong>理论、实践并进、实在特色的摄影指导</strong><br />\r\n基础摄影理论与构图<br />\r\n风光摄影与用光<br />\r\n星空摄影基础课程</p>\r\n\r\n<p><strong>行摄旅程（8天7晚）</strong><br />\r\n<strong>时间：自由选择（月相要合适）<br />\r\n地点：印尼-巴厘岛、布罗莫活火山（限名额10人）</strong></p>\r\n\r\n<p>Day 1 北京 China &ndash; 巴厘岛 Bali<br />\r\n各位成员抵达巴厘岛努拉莱国际机场，导游接机，每人分发一张印尼手机卡，内含4GB流量及10人民币话费，直送酒店休整歇息。<br />\r\n16：30酒店前台集合，一起步行20分钟前往库塔海滩拍摄逆光之落日余晖，携冲浪板回岸的浪人，沙滩慢跑的老外，与海嬉戏的儿童&hellip;&hellip;<br />\r\n18:30 库塔附近餐厅会餐，各位成员自我介绍。中科院老师对此次印尼游学行的摄影课程题纲做介绍，领队分享印尼风俗与趣事。<br />\r\n吃：含晚餐 &nbsp; &nbsp;住：库塔特色文艺酒店<br />\r\n&nbsp;</p>\r\n\r\n<p>Day 2 库塔 Kuta &ndash;乌布漂流 Ubud Rafting&ndash; 海神庙 Tanah Lot<br />\r\n乌布漂流、青绿梯田、巴厘岛最美的夕阳-海神庙。<br />\r\n风情餐厅，居水中央环桌盘腿而坐，丰盛海鲜大餐一慰味蕾。<br />\r\n吃：含早/午/晚餐 &nbsp; &nbsp; 住：Sembalun火山镇酒店<br />\r\n&nbsp;<br />\r\nDay 3 库塔 Kuta&nbsp;<br />\r\n海边人文风光摄影指导，慢门拍摄练习。<br />\r\n吃：含早/午/晚餐 &nbsp; &nbsp;住：库塔特色文艺酒店<br />\r\n&nbsp;</p>\r\n\r\n<p>Day 4 巴厘岛Bali &ndash; 布罗莫火山Bromo<br />\r\n布罗莫火山美不美，还是看照片吧，这真不是火星！<br />\r\n吃：含早/午/晚餐 &nbsp; &nbsp;住：布罗莫No.1酒店<br />\r\n&nbsp;</p>\r\n\r\n<p>Day 5 布罗莫火山Bromo&nbsp;<br />\r\n活火山、银河、星空、策马人、火山口拍摄。<br />\r\n吃：含早/午/晚餐 &nbsp; &nbsp;住：布罗莫No.1酒店<br />\r\n&nbsp;</p>\r\n\r\n<p>Day 6 布罗莫火山 Bromo （活火山壮景，熔岩沙平原，原始森林，瀑布）<br />\r\n穿越熔岩沙平原、草甸、雨林，一路美景不胜枚举，自然氧吧心旷神怡，一路抵达天然瀑布。<br />\r\n吃：含早/午/晚餐 &nbsp; &nbsp;住：布罗莫No.1酒店<br />\r\n&nbsp;</p>\r\n\r\n<p>Day 7 布罗莫火山 Bromo&ndash;诗都文罗Situbondo （潜水 Diving&ndash;夕阳 Sunset）<br />\r\n乘车抵达诗都文罗海滩，没有游人如织的海外游客，我们的科考队员们可尽情享受海滩美景。还可自主选择最深12米的体验潜水，与海底生物来一次亲密接触！傍晚时分，回顾海滩慢门拍摄技法，再来一组清丽日落片。<br />\r\n吃：含早/午/晚餐 &nbsp; &nbsp;住：诗都文罗海滩附近No.1酒店<br />\r\n&nbsp;<br />\r\nDay 8 诗都文罗 Situbondo &ndash; 泗水机场Surabaya<br />\r\n酒店早餐后，专车约4个小时抵达泗水机场，愉快结束本次印尼摄影游学行。</p>\r\n\r\n<p>&nbsp;</p>\r\n', '591', 0, NULL, 1471595429, 18, '戴明', 1),
(274, 0, '海外｜秋色贝加尔湖——梵高油画里的秋季星空之旅', '33,41', 3, '3,4,99', '', NULL, '<p>秋天的贝加尔湖是一首浪漫多情的歌，是一首彩色梦行的诗&hellip;&hellip;</p>\r\n\r\n<p><strong>活动特色</strong><br />\r\n&radic; 独一无二的行程日期，国内只有一期，选择在俄罗斯秋色最美的时间<br />\r\n&radic; 贝加尔湖星空摄影，独具魅力<br />\r\n&radic; 八次前往贝加尔湖带队创作的经验支持<br />\r\n&radic; 奥利洪岛南北双岛精彩不断<br />\r\n&radic; 海航北京直飞，省时省心<br />\r\n&radic; 环湖火车带您体验秋季贝加尔湖的魅力<br />\r\n&radic; 走近贝加尔湖，住在奥利洪岛，近距体验<br />\r\n&radic; 俄式建筑风情&mdash;教堂文化<br />\r\n&radic; 秋季的壮美白桦林<br />\r\n&radic; 拿起护照，免签证手续说走就走</p>\r\n\r\n<p><strong>摄影之旅成熟完善</strong><br />\r\n（只寻找属于摄影人的精彩，星空、风光+人文）<br />\r\n① 资深实战派摄影老师全天候指导<br />\r\n② 拍摄点精准到位&nbsp;<br />\r\n③ 日出日落精准把控<br />\r\n④ 俄餐、中餐合理搭配<br />\r\n⑤ 全含：往返机票＋食宿＋免签证（省时、省力、省心）<br />\r\n&nbsp;</p>\r\n\r\n<p><strong>途中授课：星空摄影、风光、人文与后期</strong><br />\r\n在每一次出行的过程中，我们力求将理论与实践相结合，从基础到提高延展，一一贴心指导，帮你在实战中快速提高。<br />\r\n● 秋季风光摄影技巧进阶<br />\r\n● 星空与地面景色的完美配合<br />\r\n● 人文实战技巧与后期<br />\r\n● 结束后线上作品点评交流会</p>\r\n', '592', 0, NULL, 1471616881, 18, '戴明', 1),
(275, 0, '海外｜“纳”不一样——纳米比亚的奇幻星空科考', '33,41', 3, '2,3,4', '', NULL, '<p><strong>&ldquo;纳&rdquo;不一样｜纳米比亚的奇幻星空科考</strong><br />\r\n毕生难忘的自然探索与星空之旅</p>\r\n\r\n<p>纳米比亚区别于很多国家而存在，在这里沙漠拥抱了海水、现代城市遇见原始部落、非洲基调碰撞德式风情。<br />\r\n这里也仿佛集合了所有独属于非洲的震撼之美，与其他非洲国家不同，这里的每一处都干净恬淡。<br />\r\n无边无际的草原、戈壁、沙漠，夕阳西下颜色千变万化的天空、土地与大片云朵，夜晚不经意的仰望便是如银河般的天空，那一刻也不知为何眼泪竟夺眶而出。心中埋藏玄幻、武侠世界的幻想竟在此地不断的放大再放大。</p>\r\n\r\n<p><br />\r\n<strong>产品特色</strong><br />\r\n<strong>星空摄影师的乐园</strong><br />\r\n这里不仅有着犹如珠宝盒一般的璀璨星空，星空下更有世界著名的箭袋树林；坐在世界上最古老的纳米布红沙漠里仰望星空，那无与伦比的景象永久留在了每一个人的脑海。纳米比亚有非洲唯一的&ldquo;夜空保护区&rdquo;，在这里射猎南天星空，为你留下最难忘的星空摄影之旅。<br />\r\n<strong>遗落的原始人部落</strong><br />\r\n纳米比亚作为非洲经济实力较强的非洲国家却保留着非洲大陆最后一个原始生态的民族辛巴红泥人部落，他们维持着500年前的生活方式和习俗，生活在远离现代文明的偏远之地；但随着现代文明的入侵，这一犹如历史书般的文明也终会有天湮没在历史中。所以文明尚在，有缘一看。<br />\r\n<strong>独一无二住宿体验</strong><br />\r\n全程入住纳米比亚当地最高级酒店，或许他们没有五星的评级。但独特的设计理念，与大自然融为一处的体验感是前所未有的。独享私密的营地木屋、纵览辽阔草原的山间别墅、银河夜空下的帐篷露营、远眺古老沙漠的独立别墅。这会让你意识到，奢华不是满眼金银的物质，而是穷尽一生从未体验过的灵魂的释放<br />\r\n<strong>无尽视野与兽狂欢</strong><br />\r\n飞驰在知名的埃托沙国家野生动物园里,远方绝美无尽的地平线,时常映于眼帘的斑马、跳羚、非洲大羚羊与角马等。偶遇非洲五霸更是让人激动不已。时而活泼狂野时而慵懒的各种动物伴随着非洲大地那千变万化的大自然奇景，&ldquo;生命于世界的可爱&rdquo;这个简单的命题，会在心中得到论证。</p>\r\n', '593', 0, NULL, 1471620136, 18, '戴明', 1),
(277, 0, '丰台一小科学课程——地质', '2,3,27', 2, '2,3,99', '', NULL, '', '614', 0, NULL, 1473659342, 16, '黄鑫磊', 1),
(278, 0, '丰台一小科学课程——南海综合课程', '2,3,25', 1, '2,3,99', '', NULL, '<p>课程安排：<br />\r\n课时&nbsp;&nbsp; &nbsp;课程安排<br />\r\n第一课&nbsp;&nbsp; &nbsp;海洋形成及分布、洋流、潮汐、海洋气候、海水、海洋的作用；<br />\r\n海洋与人类的关系。<br />\r\n第二课&nbsp;&nbsp; &nbsp;海洋生物的多样性及分类<br />\r\n第三课&nbsp;&nbsp; &nbsp;海洋植物、微生物及海洋动物<br />\r\n第四课&nbsp;&nbsp; &nbsp;海洋动物与海洋环境<br />\r\n第五课&nbsp;&nbsp; &nbsp;海洋动物研究方法简介；海洋动物与人类的关系<br />\r\n第六课&nbsp;&nbsp; &nbsp;地球的形成与构造；与海洋有关的地理地质基础知识（岛屿、岩礁、半岛、大陆架、海洋地貌等）<br />\r\n第七课&nbsp;&nbsp; &nbsp;火山与火山地质；矿物与宝石<br />\r\n第八课&nbsp;&nbsp; &nbsp;与亚热带植物有关的生物基础知识（植物结构、植物分布等）；亚热带植物特征及珍稀植物<br />\r\n第九课&nbsp;&nbsp; &nbsp;航海史；郑和下西洋及海上丝绸之路；海洋文学<br />\r\n第十课&nbsp;&nbsp; &nbsp;海洋科考报告、社会调查报告及科学小论文撰写</p>\r\n\r\n<p>&nbsp;</p>\r\n', '615', 0, NULL, 1473659428, 16, '黄鑫磊', 1),
