INSERT INTO `article` (`id`, `title`, `suoxie`, `content`, `time`, `gxtime`, `writer`, `come`, `dizhi`, `guanjianzi`, `suoyin`, `clicktime`, `classname`, `leixing`, `newsurl`, `remen`) VALUES
(221, '院网站科学传播栏目报道我中心科普活动', '院网站科学传播栏目报道我中心科普活动', '<DIV align=left><FONT face=宋体><A href=\"http://www.cas.cn/kxcb/kpdt/201308/t20130820_3914936.shtml\">http://www.cas.cn/kxcb/kpdt/201308/t20130820_3914936.shtml</A></FONT></DIV>\r\n<DIV align=center>“科学探索营”活动在兴凯湖湿地生态研究站举行</DIV>\r\n<DIV align=left>　8月12日至16日，由中国科学院兴凯湖湿地生态研究站与中国科学院行管局学趣科学实践教育共同承办的中学生“科学探索营”活动在兴凯湖湿地生态研究站隆重举行，来自北京中关村中学的20余位老师陪同78位中学生共同参加了本次科学探索营。 </DIV>\r\n<DIV align=left>　　8月12日下午2点举行的开营仪式，由中国科学院兴凯湖湿地生态研究站副站长张光致欢迎词，北京中关村中学范国庆主任致开营讲话，兴凯湖湿地生态研究站高级工程师赵志春以“湿地——地球之肾”为题作了本次探索营的主题报告，并播放了“兴凯湖国家地质公园”的宣传片。</DIV>\r\n<DIV align=left>　　兴凯湖湿地生态研究站的赵志春、杨桂生、乔田华和张新厚4位专家分别以“保护鸟类就是保护我们自己”、“环境学的若干问题”、“气象与生活”和“湿地植物多样性”为主题作了科普讲座，并和同学们一起进行了讨论，同学们踊跃地提出了很多科学问题。同学们根据研究兴趣分为了湿地动物、水文、气象和植物4个小组，在兴凯湖研究站4位老师的带领下，在兴凯湖及湖周边的湿地中分别进行了鸟类行为观察、兴凯湖水质调查、气象数据采集、植物群落调查和标本采集的野外工作。通过野外考察，同学们对相关专业的科研工作有了更深的认识和了解，对于科学研究的兴趣也进一步提高，带给了同学们了解大自然、亲近大自然的渴望，激发了同学们科学探索的灵感，并各自选取不同的主题撰写了论文。</DIV>\r\n<DIV align=left>　　8月16日下午，“科学探索营”圆满闭营。几位专家老师高度评价了各位同学在本次活动中的科学探索精神，并给优秀论文的撰写者颁发了奖状，并祝愿大家在以后的生活和学习中能一帆风顺，梦想成真！</DIV>\r\n<DIV align=left>&nbsp;</DIV>\r\n<DIV align=left>&nbsp;</DIV>\r\n<DIV align=left>&nbsp;</DIV>\r\n<DIV align=left><FONT face=宋体><A href=\"http://www.cas.cn/kxcb/kpdt/201308/t20130823_3917235.shtml\">http://www.cas.cn/kxcb/kpdt/201308/t20130823_3917235.shtml</A></FONT></DIV>\r\n<DIV align=center>触摸空间科技，点亮公众梦想<BR>——海淀区旅游委活动走进国家空间科学中心</DIV>\r\n<DIV align=left><FONT size=+0>&nbsp;&nbsp;&nbsp; 8月19日，“与院士专家面对面科技交流互动体验”活动在国家空间科学中心举行。此次活动由海淀区旅游发展委员会主办，国家空间科学中心协办，围绕“导师点燃科学梦想——认知、体验中国空间科技事业”主题，开展科普活动。来自中关村中学等学校的师生、家长及公众200余人参加了此次活动，海淀电视台等媒体进行了现场采访。</FONT> </DIV>\r\n<DIV align=left>　　 <FONT size=+0>来宾们参观了国家空间科学中心展厅、赵九章铜像、“东方红一号”卫星模型，了解了我国空间科学事业的发展历程。空间中心研究员、原中国载人航天应用系统总指挥张厚英带来了长达两小时的讲座，介绍了发展载人航天、空间科学事业的意义，科研人员艰苦奋斗的精神和科学成果让来宾赞叹不已。讲座十分注重科学知识的趣味性，会场不时爆发出阵阵掌声。</FONT></DIV>\r\n<DIV align=left>　　 <FONT size=+0>空间中心博士生钱航现场进行了空间科学实验，通过与太空中实验相比，形象地展现了空间科学的奇妙。</FONT></DIV>\r\n<DIV align=left>　　 <FONT size=+0>中科院空间环境研究预报中心也向来宾开放。来宾们聆听了科研人员关于太阳风和宇宙环境的讲解，在参观中被空间科学的神奇深深吸引。</FONT></DIV>\r\n<DIV align=left>　　 <FONT size=+0>国家空间科学中心始终将科学普及和科学传播作为国立科研机构的一项重要使命认真组织和落实，每年都坚持举办密集的科普活动，免费向社会开放，吸引了广大社会各界群众和大、中、小学生参观，获得社会各界广泛好评。</FONT></DIV>\r\n<P align=center><IMG alt=\"\" src=\"http://www.cas.cn/kxcb/kpdt/201308/W020130823334439866924.jpg\" oldsrc=\"W020130823334439866924.jpg\"></P>\r\n<DIV align=center><FONT size=+0>讲座现场</FONT></DIV>\r\n<P align=center><IMG alt=\"\" src=\"http://www.cas.cn/kxcb/kpdt/201308/W020130823334439874661.jpg\" oldsrc=\"W020130823334439874661.jpg\"></P>\r\n<DIV align=center><FONT size=+0>博士生钱航进行空间科学实验</FONT></DIV>\r\n<P align=center><IMG alt=\"\" src=\"http://www.cas.cn/kxcb/kpdt/201308/W020130823334439882700.jpg\" oldsrc=\"W020130823334439882700.jpg\"></P>\r\n<DIV align=center><FONT size=+0>参观中科院空间环境预报研究中心</FONT></DIV>', '1377594180', '1377596892', '', '', '', '', '', 0, 1, 0, '1377594180.html', 1),
(222, '天文秋季班上课地址', '天文秋季班上课地址', '<DIV>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 天文班秋季课程第一次课将于9月8日9：00-11：00开始上课，由国家天文台郭红锋老师授课，上课地址：中关村南三街8号（中关村体育文化中心附近），如需试听请电询010-57212420李老师</DIV>\r\n<DIV><IMG style=\"WIDTH: 500px; HEIGHT: 344px\" src=\"http://www.xuequ360.com/editor/uploadfile/201309051378360395.jpg\"></DIV>', '1378360429', '1378360429', '', '', '', '', '', 0, 1, 0, '1378360429.html', 1),
(225, '当今社会需要什么样的科普？', '当今社会需要什么样的科普？', '<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\" align=center><STRONG style=\"PADDING-BOTTOM: 0px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; WORD-WRAP: break-word; PADDING-TOP: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 28px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 19px; PADDING-TOP: 0px\">当今社会需要什么样的科普？</SPAN></STRONG></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\" align=center><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 28px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 19px; PADDING-TOP: 0px\">（中科院动物研究所研究员 王德华）</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 28px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 19px; PADDING-TOP: 0px\">&nbsp;</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">我想针对科普3个方面的问题，简单谈点我个人的一些认识。</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">1</SPAN><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">、社会需要什么样的科普？</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">2</SPAN><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">、我们科普的对象是哪些？</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">3</SPAN><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">、如何进行科普？</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">&nbsp;</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><STRONG style=\"PADDING-BOTTOM: 0px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; WORD-WRAP: break-word; PADDING-TOP: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">社会需要什么样的科普呢？</SPAN></STRONG></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">从范围上讲，可以说，我们国家当今需要各个方面的科普，数理化天地生，医药卫生、食品安全、环境保护等，各个方面都需要，也都很紧迫。</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">从内容上讲，社会需要“科学”的科普，这也是科普很重要的方面。需要正确的知识，需要科学的知识。我们要杜绝没有科学证据的知识，错误的“知识”，臆想杜撰的知识，哗众取宠的知识，欺骗大众的知识（其实这些都不能算知识）。在信息时代，网络时代，正确的科学知识如果不去占领科普阵地，那些错误的、杜撰的、毫无没有根据的所谓“科学”信息或者打着“科学”的外衣的信息，就会充斥网络，迷惑大众，欺骗大众。</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">&nbsp;</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><STRONG style=\"PADDING-BOTTOM: 0px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; WORD-WRAP: break-word; PADDING-TOP: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">我们科普的对象是哪些人群？</SPAN></STRONG></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">应该说所有人群，每一个公民都是科普的对象。不要局限在中小学生，也要包括大学生和研究生，工人和农民，广大农民是一个非常庞大继续科普的一个群体。还有科学家群体。科普不应该有年龄的限制，每个年龄段都是科普的对象。即使科学家，在一个领域是专家，在另一个领域就是被科普的对象。这就要求我们科普工作者需要针对不同的对象，制定不同的科普规划。</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">&nbsp;</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><STRONG style=\"PADDING-BOTTOM: 0px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; WORD-WRAP: break-word; PADDING-TOP: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">我们应该怎样进行科普活动？</SPAN></STRONG></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">国家要加大投入。国民素质的提高是国家的责任和义务。科普单位要有规划和计划，要有一个短期、中期和长期目标。科普是一个长久持续的任务，不是一个时期，不是某个群体，也不是一代人。</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">我们要号召科学家、专家、学者参与科普活动。这一点非常重要，从第一届培训班我就呼吁科学家要参与科普活动。科学家要去进行科普讲座，要进行科普写作，进行科普创作。</SPAN><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">科普创作应该作为科学家的责任。</SPAN><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">科学家要有这个责任，至少应该把自己的科研成果及其意义和应用前景让大众理解。用自己的所学所思，服务大众。</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">&nbsp;</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">要根据随时出现的各种社会问题，及时组织专家学者对大众进行科普，如前些年的三聚氰胺、毒大米等食品安全事件，日本地震核泄漏和我国部分地区地震等安全和防护措施，以及各种人类健康保健等问题等。</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">&nbsp;</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">社会需要科学家站出来的时候，科学家不能沉默，真正的专家要敢于站出来，敢于说真话，说科学的话，如转基因与食品安全问题，营养品和保健品问题。不能总让大众徘徊关于转基因食品的安全如否的问题上，更不能相信绿豆汤、长茄子包治百病的神话。</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">&nbsp;</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><A style=\"BORDER-BOTTOM: medium none; BORDER-LEFT: medium none; PADDING-BOTTOM: 0px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; WORD-WRAP: break-word; COLOR: rgb(47,73,111); BORDER-TOP: medium none; BORDER-RIGHT: medium none; TEXT-DECORATION: none; PADDING-TOP: 0px\" name=_GoBack></A><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">同时我们要注意，不同的人群，需要不同的科普内容和科普形式。如青少年，要挖掘和发现科学的乐趣，培养他们对科学的兴趣，树立追求科学、践行科学的理念，培养他们的科学精神。对于中老年人，需要消除他们脑海里一些固有的错误的老观念，树立科学的理念，健康理性科学地生活。</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">&nbsp;</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">科普不是局限于课本上的知识，不是局限于一些传统的知识。要把最新的科学发现介绍给大众。科学家在介绍新的科研成果和科学发现的时候，在对大众说明白科研成果的意义的同时，还要说明白其局限性。很多的科研成果离大众所期望的实际应用还很远很远，路还很长。科学家的实验对象可能是果蝇、老鼠、鱼、鸟或两栖爬行动物，实验过程也会有很多控制条件，实验结果和结论是有条件的，等等，要把科学的价值和科学的局限性告诉大众。科学是一种生活方式，但科学有极限，科学不是万能的。</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">&nbsp;</SPAN></DIV>\r\n<DIV style=\"TEXT-ALIGN: left; PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">所以，科普工作要牢记两个重要的方面：一是要传播科学知识，二是要传递科学精神和科学道德。在某种程度上，第二个方面当今社会更需要。科学精神的缺失，使得科学研究和科学发展变了样。科学知识和科学精神是不可分割的两个方面，两方面的统一可以帮助人们科学理智地分析和对待一些不科学甚至是打着科学的旗号进行欺骗的事情。</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">&nbsp;</SPAN></DIV>\r\n<DIV style=\"TEXT-ALIGN: left; PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">中国月球探测工程首席科学家、“嫦娥之父”欧阳自远说：“科学传播与科学家做研究工作是同样重要的事情，也是科学家的责任，是科学家不应该推托的一种义务。科学家不要把科学当成你欣赏的花瓶，而要让更多的公众理解，改变他们的未来，改变他们的生活，这也是科学家的责任。”</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">&nbsp;</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">我们要让大众知道和明白,科学是一种个人素养,是一种生活理念。科学可以帮助我们理性生活,健康生活,自信地生活，科学可以充实我们的生活,改变我们的生活， 但是科学是有局限性的，有时代性的，科学不是万能的。相信科学，利用科学，但不迷信科学。</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">科普工作在我国任重道远。</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">我们学界有责任，我们科学家有义务。</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 40px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 30px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 20px; PADDING-TOP: 0px\">&nbsp;</SPAN></DIV>\r\n<DIV style=\"PADDING-BOTTOM: 0px; WIDOWS: 2; TEXT-TRANSFORM: none; BACKGROUND-COLOR: rgb(255,255,255); TEXT-INDENT: 0px; MARGIN: 1.12em 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT: 14px/21px Tahoma, Helvetica, SimSun, sans-serif, Hei; WORD-WRAP: break-word; WHITE-SPACE: normal; ORPHANS: 2; LETTER-SPACING: normal; COLOR: rgb(51,51,51); WORD-SPACING: 0px; PADDING-TOP: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><SPAN style=\"PADDING-BOTTOM: 0px; LINE-HEIGHT: 28px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; FONT-FAMILY: 新宋体; WORD-WRAP: break-word; FLOAT: none; FONT-SIZE: 19px; PADDING-TOP: 0px\">（说明：第三届“<STRONG style=\"PADDING-BOTTOM: 0px; MARGIN: 0px; PADDING-LEFT: 0px; PADDING-RIGHT: 0px; WORD-WRAP: break-word; PADDING-TOP: 0px\">全国自然科学类场馆（动物学科）科普培训班</STRONG>”今天在中科院动物研究所开班。本文是开幕式上的应邀发言稿。）</SPAN></DIV>', '1380175781', '1380176037', '秦鸣', '科学网', '', '', '', 0, 2, 0, '1380175781.html', 1),
(224, '嫦娥三号年底“落月”科学家研讨月球与深空探测', '嫦娥三号年底“落月”科学家研讨月球与深空探测', '<p align=\"center\"><img  width=\"500\" src=\"http://www.cas.cn/xw/cmsm/201309/W020130905391520089186.jpg\"></p>\r\n<p align=\"center\"><span>图为中科院副院长阴和俊在开幕式上致辞。中新社发 孙自法 摄</span></p>\r\n<p align=\"center\"><span><span><img width=\"500\" src=\"http://www.cas.cn/xw/cmsm/201309/W020130905391520092757.jpg\"></span></span></p>\r\n<p align=\"center\"><span><span><span>图为中科院月球与深空探测总体部主任刘晓群发言。中新社发 孙自法 摄</span></span>&nbsp;</span></p>\r\n<p>　　备受瞩目的嫦娥三号今年底将首次开展“落月”探测，实现中国航天器第一次地外天体软着陆。首届“北京月球与深空探测国际论坛”9月4日在北京开幕，逾百位中外科学家相聚一堂，说嫦娥、话探月、议深空，共商月球与深空探测领域未来发展之计。</p>\r\n<p>　　此次论坛主题为“太阳系探测的科学与技术”，由中国科学院主办、中科院月球与深空探测总体部承办，旨在进一步加强中国月球与深空探测领域科学、技术和发展战略等方面的中外交流与合作，更好推动中国月球与深空探测科学研究和技术发展，不断提升中国在国际月球与深空探测领域的影响力。</p>\r\n<p>　　中科院副院长阴和俊致辞说，欢迎外国科学家参与中国的探月工程以及未来的深空探测项目，共同为人类探索宇宙奥秘、和平利用空间资源作贡献。他希望北京月球与深空探测国际论坛今后持续举办，成为中国在月球与深空探测领域常态化、规范化的学术交流平台以及国际合作窗口。</p>\r\n<p>　　“今年年底我们还将发射嫦娥三号探测器，实施月面软着陆探测与巡视勘察的科学探测任务。”中国探月工程总设计师吴伟仁表示，随着中国探月工程的逐步推进和中国航天事业的不断发展，开展月球以远包括火星、金星、小行星等深空探测，将是中国航天活动的必然趋势。“相信通过本次论坛，将增强中国在月球与深空探测领域上的国际合作”。</p>\r\n<p>　　中科院月球与深空探测总体部主任刘晓群介绍说，这次论坛会期3天，来自美国、德国、法国、奥地利、荷兰、日本和东道主中国大陆及港澳台共150多位科学家参加，主要研讨3方面内容：一是国际太阳系探测发展战略和规划；二是月球、火星、太阳和小行星探测的科学前沿和热点问题；三是太阳系探测相关技术发展。</p>\r\n<p>　　北京月球与深空探测国际论坛今后计划一至两年举办一次，本届论坛共收到74篇学术报告，其中，44篇主题为月球探测，30篇为火星、小行星、金星探测和深空探测的共性技术研究。</p>\r\n', '1378427011', '1387424950', '', '中国新闻网', 'D201312190345170.jpg', '嫦娥三号 月球 深空探测', '', 0, 5, 1, '1378427011.html', 1),
