INSERT INTO `article` (`id`, `title`, `suoxie`, `content`, `time`, `gxtime`, `writer`, `come`, `dizhi`, `guanjianzi`, `suoyin`, `clicktime`, `classname`, `leixing`, `newsurl`, `remen`) VALUES
(315, '学趣精彩活动重磅来袭：科学大爆炸科普一日游', '科学大爆炸科普一日游', '<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\">科学是不是很难？科学家是不是都是书呆子？</P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\">一个字回答你，no！</P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\">学趣带来有趣的科学实验，让我们变身趣味科学家，和小伙伴们一起来玩科学吧！这里有物理、化学、生物、环保等等很多学科的科学知识，还有各种奇妙的科学小实验，让我们一起来感受科学大爆炸的魅力吧（注意，名字叫“爆炸”，实际上没有任何危险）！</P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\">玩科学，是科学大爆炸的宗旨，喜欢“折腾”的小科学家们，一起做一次玩转科学的“熊孩子”吧！</P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\"><SPAN style=\"COLOR: rgb(255,0,0)\"><STRONG>◆活动特色：</STRONG></SPAN></P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\">带领学生走进中科院研究所，体验真实的实验。观摩研究所实验室设备，亲身感受科学的魅力。</P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\">中科院专家全程指导，让同学们认识不一样的科学知识。</P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\"><SPAN style=\"COLOR: rgb(255,0,0)\"><STRONG>◆活动费用：350/人</STRONG></SPAN></P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\"><STRONG><FONT color=#ff0000>◆活动时间</FONT></STRONG>：</P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\">2014.10.18(周六)</P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\">2014.10.26(周日)</P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\"><SPAN style=\"COLOR: rgb(255,0,0)\"><STRONG>◆活动地点:海淀科技中心、中国科学院力学研究所</STRONG></SPAN></P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\"><SPAN style=\"COLOR: rgb(255,0,0)\"><STRONG>◆本活动适合：9-12岁小朋友参加；</STRONG></SPAN></P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\">◆<SPAN style=\"COLOR: rgb(255,0,0)\"><STRONG>学科专家：</STRONG></SPAN><STRONG>杨海香</STRONG></P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\">中国科学院理学硕士，具备较厚的理化生知识贮备。曾多次参加中国科学院组织的科普活动，如作为科普专家，指导版纳、澳门、北京等地中学生进行科学探索实验。目前主要从事青少年学生的科普教育工作。</P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\"><SPAN style=\"COLOR: rgb(255,0,0)\"><STRONG>行程安排：</STRONG></SPAN></P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\"><SPAN style=\"COLOR: rgb(255,0,0)\"><STRONG>上午：海淀科技馆</STRONG></SPAN></P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\">非牛顿流体实验：淀粉溶液是一种非牛顿流体，而一般水溶液是牛顿流体。非牛顿流体的主要特征是：流体的粘度会因为受到的压力或速度而变化，压力越大，粘度会增加，甚至成为暂时性的固体。因此当用力搥打非牛顿流体时，接触面因为压力大而粘度增加，拳头就无法进入流体内部，也就不会沾上淀粉了。同样的，浸泡在淀粉溶液底部的手，快速抽离时，应力使得淀粉溶液的粘度增加，就会粘在手上，整个水盆就会跟着被提上来</P>\r\n<P style=\"TEXT-ALIGN: center; TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\"><IMG style=\"MAX-WIDTH: 100%; HEIGHT: auto !important\" src=\"https://mmbiz.qlogo.cn/mmbiz/JRaHicnSujdGUzsgkQN0lxibngX8JL2ribCTVHy8xDoIP9ekBMD98ic34q1A6ia4Ik9qzXarZkCT4L34nk6cliciaGFZQ/0\" _src=\"https://mmbiz.qlogo.cn/mmbiz/JRaHicnSujdGUzsgkQN0lxibngX8JL2ribCTVHy8xDoIP9ekBMD98ic34q1A6ia4Ik9qzXarZkCT4L34nk6cliciaGFZQ/0\" data-w=\"376\" data-ratio=\"0.7659574468085106\" data-s=\"300,640\"></P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\"><IMG style=\"WIDTH: 500px; MAX-WIDTH: 100%; HEIGHT: 366px\" src=\"https://mmbiz.qlogo.cn/mmbiz/JRaHicnSujdGUzsgkQN0lxibngX8JL2ribCjdup4Q4ye8ibjhkDGxKMWzhWo1tpnBiaz1ywibZOd5viaFbgXTlBEFL40A/0\" _src=\"https://mmbiz.qlogo.cn/mmbiz/JRaHicnSujdGUzsgkQN0lxibngX8JL2ribCjdup4Q4ye8ibjhkDGxKMWzhWo1tpnBiaz1ywibZOd5viaFbgXTlBEFL40A/0\" data-w=\"\" data-ratio=\"0.733201581027668\" data-s=\"300,640\"></P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\"><SPAN style=\"COLOR: rgb(255,0,0)\"><STRONG>午餐自理</STRONG></SPAN></P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\"><SPAN style=\"COLOR: rgb(255,0,0)\"><STRONG>下午</STRONG></SPAN>：老师的带领下参观中国科学院力学研究所，了解力学基本知识，亲手操作科普实验品，通过水、电、电磁等设备的运用，体验力学给我们生活带来的便利及各种有意思的应用。</P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\"><IMG style=\"WIDTH: 500px; MAX-WIDTH: 100%; HEIGHT: 666px\" src=\"https://mmbiz.qlogo.cn/mmbiz/JRaHicnSujdGUzsgkQN0lxibngX8JL2ribCE9jK5nL5CaUII2a7yK7H7e4MZ5AkCcPBfjibJttPbibAwIib3aE4vMicqA/0\" _src=\"https://mmbiz.qlogo.cn/mmbiz/JRaHicnSujdGUzsgkQN0lxibngX8JL2ribCE9jK5nL5CaUII2a7yK7H7e4MZ5AkCcPBfjibJttPbibAwIib3aE4vMicqA/0\" data-w=\"\" data-ratio=\"1.33201581027668\" data-s=\"300,640\"></P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\"><IMG style=\"MAX-WIDTH: 100%; HEIGHT: auto !important\" src=\"https://mmbiz.qlogo.cn/mmbiz/JRaHicnSujdGUzsgkQN0lxibngX8JL2ribCS254A1jNkYQT8T0XncNJ3BRz1rXyNxpRkNFxic0pNichXae53D3BrpJg/0\" _src=\"https://mmbiz.qlogo.cn/mmbiz/JRaHicnSujdGUzsgkQN0lxibngX8JL2ribCS254A1jNkYQT8T0XncNJ3BRz1rXyNxpRkNFxic0pNichXae53D3BrpJg/0\" data-w=\"500\" data-ratio=\"0.666\" data-s=\"300,640\"></P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\"><SPAN style=\"COLOR: rgb(255,0,0)\"><STRONG>海淀科技馆</STRONG></SPAN></P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\">纳米涂层：纳米涂层具有超疏水，高耐磨的特性。最新开发出的一种新型纳米涂层材料，能排斥上百种液体。用这种材料涂在纱网或织物上，其表面可形成一种对液体的弹力。用这种布料做成的衣物不仅超级抗污染，保护穿着者免受化学药品伤害，还可能开发出用于船舶的先进防水涂料，大大减少水流对船只的拖曳。</P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\"><IMG style=\"WIDTH: 500px; MAX-WIDTH: 100%; HEIGHT: 360px\" src=\"https://mmbiz.qlogo.cn/mmbiz/JRaHicnSujdGUzsgkQN0lxibngX8JL2ribCxmSAdAObWnCrwekCXhqzwCTtb3remcAeNnSkl8e8vwrPp7WNjm2j3g/0\" _src=\"https://mmbiz.qlogo.cn/mmbiz/JRaHicnSujdGUzsgkQN0lxibngX8JL2ribCxmSAdAObWnCrwekCXhqzwCTtb3remcAeNnSkl8e8vwrPp7WNjm2j3g/0\" data-w=\"\" data-ratio=\"0.7213438735177866\" data-s=\"300,640\"></P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\"><IMG style=\"WIDTH: 500px; MAX-WIDTH: 100%; HEIGHT: 352px\" src=\"https://mmbiz.qlogo.cn/mmbiz/JRaHicnSujdGUzsgkQN0lxibngX8JL2ribCHSplbic3eYmj5RuMPfrOaPxTpUs1YxcAv3oVIHo2StM8cWV4VhiafHYQ/0\" _src=\"https://mmbiz.qlogo.cn/mmbiz/JRaHicnSujdGUzsgkQN0lxibngX8JL2ribCHSplbic3eYmj5RuMPfrOaPxTpUs1YxcAv3oVIHo2StM8cWV4VhiafHYQ/0\" data-w=\"\" data-ratio=\"0.7035573122529645\" data-s=\"300,640\"></P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\"><SPAN style=\"COLOR: rgb(255,0,0)\"><STRONG>费用包含：</STRONG></SPAN></P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-serif; WHITE-SPACE: normal; LETTER-SPACING: normal; COLOR: rgb(0,0,0); WORD-SPACING: 0px; -webkit-text-stroke-width: 0px\">1、用车：汽车交通为专业车队空调大巴，保证每人一正座；</P>\r\n<P style=\"TEXT-TRANSFORM: none; MARGIN: 5px 0px; FONT: 16px sans-seri