INSERT INTO `reply` (`id`, `keywords`, `type`, `title`, `description`, `picurl`, `url`, `content`, `status`, `input_time`, `userid`) VALUES
(9, '1', 'news', '【精彩分享】第一期航空航天班圆满结束', '第一期航空航天班圆满结束活动精彩分享', 'Upload/201408/18/53f1bd55676b1.jpg', 'http://mp.weixin.qq.com/s?__biz=MjM5NjA1OTM5OA==&mid=200466856&idx=1&sn=4a7ce3030fb0bfcc2d1e3c1538b84133#rd', '', 0, 1408502148, 5),
(10, '3', 'news', '科学大爆炸班圆满结束', '物理、化学、生物、环保等等很多学科的科学知识充满了趣味，还有各种简单有趣的科学小实验，注意这就是科学大爆炸学趣班，虽然名字叫“爆炸”，实际上没有任何危险。面对这切，成人可能会不屑一顾，而孩子却会欢呼雀跃，用充满好奇的眼睛去观望，用充满灵性的耳朵去聆听，还会自己亲手锻炼。有趣的实验引发了孩子的好奇心，激发了孩子探索的欲望。', 'Upload/201408/18/53f1c0b333d2e.JPG', 'http://mp.weixin.qq.com/s?__biz=MjM5NjA1OTM5OA==&mid=200465466&idx=1&sn=b7d25a9ff9d4c32be6f5c5763a8734c8#rd', '', 0, 1408352472, 5),
(11, '2', 'news', '第一期恐龙与化石班圆满结束', '014年7月第一期恐龙与化石班四天活动：内容充实，形式多样。在中科院古动物馆专家全程指导，与专家们一起学习恐龙与化石知识；参观古动物馆，与恐龙化石亲密接触；观看3D电影，亲身体验史前景象；激发孩子的学习兴趣，还在老师的带领走出博物馆，亲手参与了野外化石活动和室内化石整理，体会野外化石挖掘的过程和发现化石乐趣。', 'Upload/201408/18/53f1bffbe602a.jpg', 'http://mp.weixin.qq.com/s?__biz=MjM5NjA1OTM5OA==&mid=200467017&idx=1&sn=4098ab9ff713f5bae100461419f727ab#rd', '', 0, 1408352290, 5),
(12, '5', 'text', '', '', '', '', '欢迎关注学趣网', 0, 1408350220, 5);
