INSERT INTO `o2o_article` VALUES ('363', '0', '', '联盟组织海淀少科院走进中科院率先成果展', null, null, null, null, null, null, '0', '0', '中科教', '海淀区教委', '', null, '', '', '0', '1514275640', '1514275640', '', 'upload/201712/26/5a41f91c54d68.png', '1000', null, '0', '0', '[97]', null, '', '17');
INSERT INTO `o2o_article` VALUES ('364', '0', '', '《关于在我院研究生教育中实施 科普活动学分制的通知》', null, null, null, null, null, null, '0', '0', '中科教', '中科教', '', null, '', '', '0', '0', '0', '', '', '0', null, '0', '0', null, null, '', '16');
INSERT INTO `o2o_article` VALUES ('366', '0', '', '中华人民共和国科学技术普及法', null, null, null, null, null, null, '0', '0', '中科教', '科技部', '', null, '', '<p align=\"center\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\"><strong>第一章　总　　则</strong></p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第一条　为了实施科教兴国战略和可持续发展战略，加强科学技术普及工作，提高公民的科学文化素质，推动经济发展和社会进步，根据宪法和有关法律，制定本法。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第二条　本法适用于国家和社会普及科学技术知识、倡导科学方法、传播科学思想、弘扬科学精神的活动。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　开展科学技术普及(以下称科普)，应当采取公众易于理解、接受、参与的方式。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第三条　国家机关、武装力量、社会团体、企业事业单位、农村基层组织及其他组织应当开展科普工作。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　公民有参与科普活动的权利。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第四条　科普是公益事业，是社会主义物质文明和精神文明建设的重要内容。发展科普事业是国家的长期任务。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　国家扶持少数民族地区、边远贫困地区的科普工作。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第五条　国家保护科普组织和科普工作者的合法权益，鼓励科普组织和科普工作者自主开展科普活动，依法兴办科普事业。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第六条　国家支持社会力量兴办科普事业。社会力量兴办科普事业可以按照市场机制运行。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第七条　科普工作应当坚持群众性、社会性和经常性，结合实际，因地制宜，采取多种形式。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第八条　科普工作应当坚持科学精神，反对和抵制伪科学。任何单位和个人不得以科普为名从事有损社会公共利益的活动。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第九条　国家支持和促进科普工作对外合作与交流。</p>\r\n\r\n<p align=\"center\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\"><strong>第二章　组织管理</strong></p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第十条　各级人民政府领导科普工作，应将科普工作纳入国民经济和社会发展计划，为开展科普工作创造良好的环境和条件。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　县级以上人民政府应当建立科普工作协调制度。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第十一条　国务院科学技术行政部门负责制定全国科普工作规划，实行政策引导，进行督促检查，推动科普工作发展。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　国务院其他行政部门按照各自的职责范围，负责有关的科普工作。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　县级以上地方人民政府科学技术行政部门及其他行政部门在同级人民政府领导下按照各自的职责范围，负责本地区有关的科普工作。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第十二条　科学技术协会是科普工作的主要社会力量。科学技术协会组织开展群众性、社会性、经常性的科普活动，支持有关社会组织和企业事业单位开展科普活动，协助政府制定科普工作规划，为政府科普工作决策提供建议。</p>\r\n\r\n<p align=\"center\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\"><strong>第三章　社会责任</strong></p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第十三条　科普是全社会的共同任务。社会各界都应当组织参加各类科普活动。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第十四条　各类学校及其他教育机构，应当把科普作为素质教育的重要内容，组织学生开展多种形式的科普活动。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　科技馆(站)、科技活动中心和其他科普教育基地，应当组织开展青少年校外科普教育活动。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第十五条　科学研究和技术开发机构、高等院校、自然科学和社会科学类社会团体，应当组织和支持科学技术工作者和教师开展科普活动，鼓励其结合本职工作进行科普宣传；有条件的，应当向公众开放实验室、陈列室和其他场地、设施，举办讲座和提供咨询。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　科学技术工作者和教师应当发挥自身优势和专长，积极参与和支持科普活动。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第十六条　新闻出版、广播影视、文化等机构和团体应当发挥各自优势做好科普宣传工作。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　综合类报纸、期刊应当开设科普专栏、专版；广播电台、电视台应当开设科普栏目或者转播科普节目；影视生产、发行和放映机构应当加强科普影视作品的制作、发行和放映；书刊出版、发行机构应当扶持科普书刊的出版、发行；综合性互联网站应当开设科普网页；科技馆(站)、图书馆、博物馆、文化馆等文化场所应当发挥科普教育的作用。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第十七条　医疗卫生、计划生育、环境保护、国土资源、体育、气象、地震、文物、旅游等国家机关、事业单位，应当结合各自的工作开展科普活动。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第十八条　工会、共产主义青年团、妇女联合会等社会团体应当结合各自工作对象的特点组织开展科普活动。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第十九条　企业应当结合技术创新和职工技能培训开展科普活动，有条件的可以设立向公众开放的科普场馆和设施。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第二十条　国家加强农村的科普工作。农村基层组织应当根据当地经济与社会发展的需要，围绕科学生产、文明生活，发挥乡镇科普组织、农村学校的作用，开展科普工作。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　各类农村经济组织、农业技术推广机构和农村专业技术协会，应当结合推广先进适用技术向农民普及科学技术知识。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第二十一条　城镇基层组织及社区应当利用所在地的科技、教育、文化、卫生、旅游等资源，结合居民的生活、学习、健康娱乐等需要开展科普活动。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第二十二条　公园、商场、机场、车站、码头等各类公共场所的经营管理单位，应当在所辖范围内加强科普宣传。</p>\r\n\r\n<p align=\"center\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\"><strong>第四章　保障措施</strong></p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第二十三条　各级人民政府应当将科普经费列入同级财政预算，逐步提高科普投入水平，保障科普工作顺利开展。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　各级人民政府有关部门应当安排一定的经费用于科普工作。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第二十四条　省、自治区、直辖市人民政府和其他有条件的地方人民政府，应当将科普场馆、设施建设纳入城乡建设规划和基本建设计划；对现有科普场馆、设施应当加强利用、维修和改造。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　以政府财政投资建设的科普场馆，应当配备必要的专职人员，常年向公众开放，对青少年实行优惠，并不得擅自改作他用；经费困难的，同级财政应当予以补贴，使其正常运行。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　尚无条件建立科普场馆的地方，可以利用现有的科技、教育、文化等设施开展科普活动，并设立科普画廊、橱窗等。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第二十五条　国家支持科普工作，依法对科普事业实行税收优惠。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　科普组织开展科普活动、兴办科普事业，可以依法获得资助和捐赠。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第二十六条　国家鼓励境内外的社会组织和个人设立科普基金，用于资助科普事业。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第二十七条　国家鼓励境内外的社会组织和个人捐赠财产资助科普事业；对捐赠财产用于科普事业或者投资建设科普场馆、设施的，依法给予优惠。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第二十八条　科普经费和社会组织、个人资助科普事业的财产，必须用于科普事业，任何单位或者个人不得克扣、截留、挪用。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第二十九条　各级人民政府、科学技术协会和有关单位都应当支持科普工作者开展科普工作，对在科普工作中做出重要贡献的组织和个人，予以表彰和奖励。</p>\r\n\r\n<p align=\"center\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\"><strong>第五章　法律责任</strong></p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第三十条　以科普为名进行有损社会公共利益的活动，扰乱社会秩序或者骗取财物，由有关主管部门给予批评教育，并予以制止；违反治安管理规定的，由公安机关依法给予治安管理处罚；构成犯罪的，依法追究刑事责任。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第三十一条　违反本法规定，克扣、截留、挪用科普财政经费或者贪污、挪用捐赠款物的，由有关主管部门责令限期归还；对负有责任的主管人员和其他直接责任人员依法给予行政处分；构成犯罪的，依法追究刑事责任。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第三十二条　擅自将政府财政投资建设的科普场馆改为他用的，由有关主管部门责令限期改正；情节严重的，对负有责任的主管人员和其他直接责任人员依法给予行政处分。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　扰乱科普场馆秩序或者毁损科普场馆、设施的，依法责令其停止侵害、恢复原状或者赔偿损失；构成犯罪的，依法追究刑事责任。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第三十三条　国家工作人员在科普工作中滥用职权、玩忽职守、徇私舞弊的，依法给予行政处分；构成犯罪的，依法追究刑事责任。</p>\r\n\r\n<p align=\"center\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\"><strong>第六章　附　　则</strong></p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　第三十四条　本法自公布之日起施行。</p>\r\n', '0', '1514339042', '1514339042', '', '', '0', null, '0', '0', '[100]', null, '', '15');
INSERT INTO `o2o_article` VALUES ('367', '0', '', '中国科学院科学传播中长期发展规划纲要（2006—2020年）', null, null, null, null, null, null, '0', '0', '中科教', '中国科学院', '', null, '', '<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">一、我院科学传播的历史使命与发展远景</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　未来5至15年是我国全面建设小康社会、实现社会主义现代化的关键时期。经济增长、社会进步更加依赖于科学发展、自主创新，依赖于劳动者素质的提高；科学技术不断取得重大突破，并孕育着新的科技革命，科学技术引领未来、推动社会进步的作用更加明显；我国中长期科学和技术发展规划的组织与实施，必将进一步推进我国科技事业的发展和中国特色国家创新体系的建设；随着物质文明的改善和繁荣，社会对精神文明需求将不断增长，中华民族的伟大复兴亟待科学文化的创造与普及。所有这一切都对科学传播提出了新的更高的要求。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　科学传播是以提高公民科学素质、实现个人、社会、自然和谐发展为目的的全民终身科学教育和互动过程，包括三个层面：科学知识的普及和技术技能的推广；科学方法、科学思想、科学精神和科学发展观的传播；使公众理解科学技术与社会的相互作用，具备参与有关科技公共事务的能力。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　（一）充分认识新时期科学传播的重要意义</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　加强科学传播是全面落实科学发展观的重要保证。只有在全社会大力宣传和普及科学发展观，使科学发展观深入人心，才能保证科学发展观成为广大公众的自觉行动，才能使科学发展观真正贯彻到经济社会发展和社会生活的各个领域和环节。落实科学发展观要求我们要在全社会进一步形成讲科学、爱科学、学科学、用科学的浓厚氛围和良好风尚，使我国广大人民群众树立正确的生活观和生产观，树立节约资源的意识，树立保护环境的意识，树立保护生物多样性的意识，爱护自然，关心环境，善待生命，保护动物，促进人与自然的和谐发展；使广大干部树立正确的政绩观，在工作中时刻贯彻科学发展观，并使得科学发展观贯穿于国家、地区的发展规划和决策中，从而使我们各个地区发展真正依靠科学统筹和科学决策。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　加强科学传播是发展先进生产力的客观要求。在科学技术迅猛发展的今天，一个国家和地区科学传播能力及其带来的公众科学素养提高的程度，是影响这个国家和地区生产力发展水平与民族创新能力的重要因素。科学传播是先进科学技术转化为现实生产力的重要基础。当前，我国公民的科技素养较低，经济增长主要还是依靠增加资源驱动和投资驱动，生产技术、管理水平和产业结构层次较低，造成经济增长投入大、消耗高，对资源、环境的压力加大。科学传播对于劳动者素质的提高，促进全体劳动者掌握先进科学技术，按照科学发展观的要求，在我国走出一条科技含量高、经济效益好、资源消耗低、人力资源优势得到充分发挥的新型工业化道路，具有重要的实践意义。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　加强科学传播是发展先进文化的重要保证。科学技术是人类创造性劳动的产物，是认识与合理改造世界的智慧结晶，是先进文化的重要方向。科学文化是人类文明的基石与先导，是人类文化宝库中最先进的文化内涵，引领和推动着人类文化的未来发展。科学传播对于发展先进文化、弘扬民族精神都发挥着不可替代的保证作用。加强科学传播，能够使先进的科学技术为公众所掌握，成为改造落后文化、抵制腐朽文化的锐利武器，使崇尚科学、崇尚理性、崇尚文明成为公众的精神品质；发展科学传播事业，有助于促进科学文化与人文文化的整合，推动科学伦理文化的传播，促进科学技术的健康发展，并使广大公众适应并受惠于科技发展；将科学的思想成为公众精神文化的支柱，有助于使中国传统文化焕发新的生机，推动中华民族的伟大复兴。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　加强科学传播是促进我国政治文明建设的必要条件。加强科学传播，有助于提高公众行使民主权利和参与公共事务的能力，促进决策的科学化和民主化，推进政治文明建设。科学传播越来越需要关注社会的现实需求和公众的兴趣态度，其作用也将越来越多地体现在协调科技与公众及社会发展的关系方面，加强科学传播能够为科技发展创造良好的社会环境。及时了解社会公众对科技发展的态度、关注点和实际需求，并在此基础上制定相关的政策措施，进而形成科技与社会之间的良性互动关系，已成为当今世界科学传播事业发展的新趋势。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　加强科学传播是提高全民族文化素质、促进人的全面发展的重要基础。每一个公民在处理个人日常生活、劳动就业和参与公共事务中，都离不开对科学技术的理解，需要不断学习科技知识，掌握先进技能，以改善物质生活，丰富精神生活，实现自身价值。公众在物质生活走向富裕的过程中，必然会对提高文化生活和健康水平产生日益增长的需求。科学传播可以帮助和引导公众开阔眼界，明辩真伪，追求真善美，树立科学的世界观、人生观和价值观；帮助和引导人们健身强体，预防疾病，保障生理和心理健康；科学传播有助于提高公众科技素养，为国家的经济社会发展，培养造就大批掌握先进科学技术的劳动者和高素质的科技后备人才。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　（二）紧紧把握新时期科学传播发展的机遇期</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　全面贯彻落实科学发展观对我国科学传播提出了新的要求。党中央根据世界现代化发展的启示和我国改革开放的经验，提出了以人为本、全面协调可持续发展的科学发展观。科学发展观指明了我国经济社会发展的方向和方式，必将对我国的经济增长和社会进步产生长远的影响。落实科学发展观，需要广大公众掌握树立科学的生产观、生活观和消费观，需要广大干部树立正确的政绩观和发展观。这一切都需要提高公众的科技素质，提高科学的思维和辨别能力，需要以普及科学发展观为核心的科学传播工作真正落到实处，收到实效。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　国家中长期科学和技术发展规划的组织与实施，将使我国的科技发展进入到一个新的发展阶段。中央已经确定我国未来15年的科技发展指导方针为&ldquo;自主创新，重点跨越，支撑发展，引领未来&rdquo;，明确必须坚定地把科技进步和创新作为经济社会发展的首要推动力，把提高自主创新能力作为调整经济结构、转变经济增长方式和提高国家竞争力的中心环节，把建设创新型国家作为面向未来的重大战略。国家中长期科学和技术发展规划的组织与实施，必将带动我国的科学技术的大发展，同时对科学传播提出新的更高要求，为科学传播事业的发展创造良好的机遇。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　《全民科学素质行动计划纲要》的颁布与组织实施，明确了我国未来5&mdash;15年科学传播工作的宏伟目标。国务院在《全民科学素质行动计划纲要》中提出了&ldquo;政府推动，全民参与，提升素质和促进和谐&rdquo;的方针，并且明确：到2010年，通过科学传播，促进科学发展观在全社会的树立与落实，以重点人群科学素质行动带动全民科学素质的整体提高，开发与共享科学教育与培训、科普资源，提高大众传媒科技传播能力，加强科普基础设施等公民科学素质建设的基础。随着提高全民科学素质成为政府的职责，经济和社会发展对科学技术的依赖增大，在很多省市，政府将科学传播列为促进经济发展和社会进步的重要内容，重视科学传播已成为全社会和广大公众的共识。《全民科学素质行动计划纲要》的组织实施，为我院科学传播工作指明了发展的方向，创造了难得的机遇。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　科普法律法规的不断完善，为科学传播事业的发展创造了良好的环境。《中华人民共和国科学技术普及法》的颁布，《中华人民共和国科技进步法》的修订，为我国的科学传播事业奠定坚实的法律基础与保障；中央和各级地方政府相继出台的一系列相关的政策与法规，对于科学传播事业的发展正在发挥着引导、规范的作用。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　我国社会主义现代化建设为科学传播的发展创造了良好的条件。我国经济的持续增长，为科学传播提供了可靠的物质保障，中央和地方都在不同程度上逐渐增加对科学传播的投入；科教兴国战略、可持续发展战略和人才强国战略的实施，为科学传播事业的发展与繁荣奠定了坚实的基础；随着人民物质生活的改善，人民的文化需求正在不断提升，文化产业正在蓬勃发展，这一切都对作为文化事业的科学传播的发展创造了有利条件。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　我院创新三期对科学传播提出了新的要求。在未来5年，我院确定了创新跨越、持续发展的主题，确定了科技发展、人才建设和科技体制改革的重点，并且强调要加强与地方的合作，共同推进中国特色国家创新体系的建设。创新三期的目标要求我院更加重视科学传播，重视将科学传播工作与我院创新工作的紧密结合，加强对科学传播的支持力度，建立有我院特色的科学传播体系，创立品牌，扩大影响，使公众更加理解我院的改革与发展，将我院的科技创新成果及时惠及公众，提高公众的科技素养，加强与地方的合作，推进区域创新体系建设。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　我院科学传播工作具有一定的基础。我院拥有我国最丰富的科学传播资源，包括一大批高水平的科研人员，特别是活跃在科学传播一线的院士、老科学家、科技人员和科学传播的专家，并拥有学科配套的研究所、与研究工作紧密结合的国家级科普基地、国内外知名的科普网站、以及数家科普传媒机构等，初步形成了连横合纵的科学传播组织体系。我院与国内其他科学传播机构和媒体建立了良好的科学传播工作关系与合作基础，主持参与了国内重要的科学传播活动，每年定期向社会开放，积累了丰富的科学传播的经验。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　但是，我院的科学传播工作还存在着一些亟待改进的地方。我院科学传播工作的整体设计仍有待加强，科普资源系统集成不够，科普资源效益尚未得到充分的发挥，开放和宣传还有进一步拓展的空间。组织管理体制相对松散，缺乏激励研究所重视科学传播的机制，没有形成科学研究与科学传播两翼并举的格局；科技人员的积极性有待进一步发挥，专业科学传播队伍规模偏小，科普创作与出版亟待加强，科学传播形式需要进一步创新等。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　（三）明确我院在国家科学传播中的定位与作用</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　未来5至15年，我院的科学传播必须坚持以邓小平理论和&ldquo;三个代表&rdquo;重要思想为指导，全面落实科学发展观，面向国家战略需求，面向世界科技前沿，围绕我院中长期发展目标和中心任务，将科学传播视为我院作为国家科研机构必须履行的社会责任，全面发挥我院的科学传播资源优势，发挥我院在我国科学传播中的骨干引领和示范带动作用，使我院成为我国科学知识的重要传播者、科学方法的典型示范者、科学思想和科学精神的积极倡导者；以创新和发展为主题，全面落实新时期办院方针，紧紧围绕知识创新工程，开展科学传播工作；发挥我院优势，力争在传播最新科技进展、公众关心的热点科技问题、科技与经济社会文化发展等方面发挥突出作用，创立我院科学传播的品牌；建设具有特色的科学传播体系，坚持制度创新，积极争取与社会力量的紧密结合，推进科学传播的理论研究与实践活动，为构建社会主义和谐社会，实现中华民族的伟大复兴奠定坚实的科学文化基础。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　2006年至2010年的主要目标是：形成具有我院特色的科学传播体系，科学传播创新能力明显提高，实现我院科学传播的可持续发展；建成若干国内一流、国际知名的科学传播基地，造就一支专兼结合的高素质的科学传播队伍，形成科学、合理、有利于科学传播事业持续发展、有利于与社会力量紧密结合的体制和机制，基本建成具有国际影响的国家科学思想库，在《全民科学素质行动计划纲要》的组织实施中，圆满完成我院承担的任务，开创我院科学传播事业的新局面。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　具体目标为：</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　体系建设：明确各机构的定位，建立整体协调、相互合作的体制和宏观管理机制，以及有助于我院科学传播工作的资源配置和评价体系；紧密结合我院知识创新工程，联合院内外力量，争取社会支持，重点建设30个左右国家级高水平科普基地，研究所要结合创新实践，采用多种形式，向社会传播科技前沿的最新进展，植物园、天文台和标本馆（包括博物馆）等专业科普基地要建设国内一流的设施，形成一流的管理，提供一流的公共产品，保证一流的服务，每年组织若干科学传播活动，并成为国家和地方最有影响的科学传播场所；研究所、重点实验室和大科学装置每年要定期向社会开放，接待人数逐年增加；建立专业科学传播研究与教学中心，研究教学水平在建成后的5年内全国领先。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　队伍建设：研究所每年参与科学传播的科技人员人数与次数有较大增加，所发表的科学传播作品有较大增加；参加科学传播工作的老科学家人数、演讲场次、科技服务次数与受众人数逐年增加；形成一支专业科学传播队伍，并有若干名全国知名的科学传播专家。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　媒体建设：加强科普期刊建设，打造2&mdash;3种国内外具有一定知名度的科学传播期刊。建成1个全国最具影响的科学传播网站，日访问量超过10万；专业科学传播基地都要将展示内容网络化，并保证日访问量不断增加；报纸上科学传播版面有所增加，影响进一步扩大；形成全国最知名的科学传播图书品牌，获国家奖的科学传播图书5年间明显增加；与中央和地方影视媒体合作，打基础，出精品，创品牌，共建科学传播栏目和频道。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　科学传播活动：依托学部科普与出版委员会等组织，积极开展院士科学传播活动，形成院士科学传播品牌，每年场次与听众有所增加，5年内覆盖全国内地所有省份；在继续搞好已有科学传播活动并进一步扩大影响的条件下，创立一些新的科学传播活动品牌；针对最新科技进展及其经济社会和文化影响，针对公众感兴趣的重要科技问题、配合国际组织科技活动，形成制度性、经常性和有影响性的科学传播活动品牌。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　展望2020年，我院科学传播的远景是：形成具有特色的科学传播体系，科学传播能力大幅度提升，建成一批世界知名的科学传播基地，具有一支高水平的科学传播队伍，形成有利于高水平科学传播持续开展的体制与机制。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　二、发展战略</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　（一）构建科学传播体系</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　整合已有科普资源，走集约发展之路。充分发挥研究所在科学传播中的主导作用，发挥植物园、天文台、标本馆、博物馆、网络科普联盟等科普组织的优势，建立研究机构、实验室定期向社会公众开放制度，加强科学传播的战略研究和顶层设计，合理布局，统筹组织重大科学传播活动，构建我院科学传播体系，形成科学传播合力。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　我院的科学传播体系应包括：院科学传播工作组织管理机构，负责全院科学传播工作战略、规划、政策的制订及宏观指导与管理；专业科学传播组织，负责院各学科领域的科学传播工作协调与组织；研究所负责向全社会传播最新科技进展及其科学技术对经济发展和社会进步的影响；植物园、标本馆、天文台、博物馆等主要从事科学传播的机构，长期从事面向社会公众的科学传播工作；各级各类科普基地（包括植物园、天文台和部分标本馆、研究所与重点实验室、图书馆、中国科技大学及中科院研究生院等），主要在国家科学传播工作中发挥骨干与示范作用；研究所、院校、重点实验室、大科学装置、野外台站等，每年定期向社会公众开放，并组织本所科技人员参加国家与所在地区的科学传播活动；科普期刊、报纸、网站、图书情报、出版、音像等科学传播媒体，主要负责我院科学传播的宣传与展示工作；广大院士、老科学家、科技人员、专门从事科学传播研究与教学工作的专业人员、志愿者等构成科学传播的宏大队伍。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　加强院部与学部结合，整合科学传播资源。将院士的社会影响、科学影响与我院研究所的创新实践结合起来，各有侧重，相互补充，充分结合，面向社会，面向公众，开展卓有影响和效果的科学传播活动。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　（二）夯实科学传播平台</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　建设一批重点科学传播基地。加强对我院一批重点科学传播基地的投入，切实加强科学传播场馆的软硬件建设，提升其科学传播能力，使我院的若干植物园、天文台、动物园、标本馆、博物馆成为我国一流、世界知名的科学传播基地。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　提升我院媒体的影响力。办好我院的报纸、科普类期刊，加强出版工作，培育若干国内最有影响的报刊品牌，与中央、地方电视台合办科学传播栏目和频道，运用影视科学传播工具，扩大科学传播的影响。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　大力发展网络科学传播。加大对网络科学传播的支持力度，加强科学传播网络的整合与链接，使之成为新时期科学传播的重点平台，丰富并及时更新网络科普内容，在我国的数字科学传播领域发挥引领作用。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　（三）造就科学传播人才</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　充分发挥院士在科学传播中的积极作用。加强学部的科学传播工作，整合院部与学部的科学传播资源，组织更多院士参与到科学传播工作中来，从整体上增强院士科学传播的系统性和社会影响力。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　充分发挥科学家在科学传播中的主体作用。我院广大科技工作者要树立自觉传播科学的社会责任感，重视科学传播，提高科学传播的能力，为社会贡献高水平、普及性的科技新知识，积极倡导科学方法、科学思想与科学精神。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　培养造就一批专业科学传播人才。加大培训力度，提高重点科普基地的专业科学传播人才的素质和能力，通过项目带动、交流与合作等措施，培养造就一批专门从事科学传播理论与战略、政策研究等方面的人才。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　形成一支科学传播志愿者队伍。以离退休科技人员和在读硕博士生为骨干，使之逐步成为我院科学传播中的生力军。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　（四）创新科学传播体制</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　加强整体规划。充分把握世界科技发展趋势和我国的科学传播需求，在战略研究基础上，制定科学传播总体规划和专项规划，强化规划的指导作用和动态管理。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　加强宏观调控。明确院所科学传播事权，加强全院科学传播的宏观调控和整体部署，完善新形势下院的宏观调控手段。充分发挥研究所和科学传播基地在科学传播中的主体作用。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　完善激励机制。择优支持发展快和发展好的科学传播基地，鼓励研究所和科技人员主动从事科学传播事业，将科学传播纳入研究所和科技人员的评价体系, 建立科学传播奖励机制。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　（五）加强对外合作交流</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　加强与部委、地方的协作。加强与中央及国务院有关部委的协作，积极参与国家重大科学传播活动和项目；以研究所和分院为主体，加强与地方的协作，积极支持地方科学传播事业的发展，使全社会可以共享我院的科学传播平台。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　加强与媒体合作。特别是加强与具有广泛社会影响的媒体合作，使我院的最新科技创新成就、先进的科学理念、实用的科学方法准确便捷地向社会大众传播。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　加强国际交流与合作。扩大与国际科学传播界的交流与合作，学习借鉴海外先进的科学传播手段和成功的科学传播经验与做法，加强与发达国家和地区之间的人才交流。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　三、重点任务与措施</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　（一）加强重点科学传播基地建设</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　通过政策引导等措施，支持研究所发挥科学传播的主渠道作用。研究所要结合创新实践，开展具有广泛社会影响的科学传播活动，及时向广大公众介绍科学技术的最新进展及其社会影响。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　通过加强投入、完善体制和机制、加强队伍建设和政策引导等方式，重点建设约30个左右综合实力强、社会影响大的高水平国家级科学传播基地，使之在硬件设施、管理体制和机制、人才队伍结构与整体水平、科学知识传播的系统性等方面，走在国家的前列，提高科学传播基地的整体水平和社会竞争力，增强我院科学传播基地的可持续发展能力。充分发挥各科学传播基地的自身优势和特色，成为国内一流、国际知名的科学传播基地，并在国家植物园、天文台、博物馆、标本馆等系列中发挥引领和示范作用。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　通过政策导向，积极鼓励研究所、特别是国家和院重点实验室、大型科学设施以不同方式向社会公众开放，及时宣传最新的科学发展动态，使科学技术在国民经济、社会进步和国家安全中的支撑与引领作用得到充分发挥，并为社会认知。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　依托我院已有的基础，充分发挥中国科技大学和中科院研究生院在科学传播研究和人才培养中的作用，加强对外合作，建设好专门从事科学传播研究、教学、培训的科学传播学科基地，使之成为我国重要的科学传播理论创新和人才培养的基地。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　加强各科学传播基地之间、研究所之间以及各个区域之间的科学传播合作与交流，通过宏观调控、激励机制、资源配置与评价等措施，构建我院集研究、展示、宣传、人才培养统合发展的科学传播体系。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　（二）建设一支高水平高素质的科学传播人才队伍</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　充分发挥院士在科学传播中的积极作用。组织院士开展具有针对性和影响力的科学传播活动。继续组织开展&ldquo;科学与中国&rdquo;院士巡讲活动，形成品牌；支持院士发表高水平的科学传播作品，鼓励院士通过个人网站或博客从事科学传播。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　通过政策导向和创新文化环境的营造，充分调动科研人员的主动性和积极性，使之成为我院科学传播的主体力量。要在广大科研人员中倡导正确的价值观和自觉传播科学知识、科学方法、科学思想与科学精神的社会意识，使他们认识到科学传播对于我国社会进步的重要意义，认识到作为科技工作者，有责任和义务使公众充分了解最新的科学进展，了解国家投入科研经费的产出与效益，了解科技发展可能带来的积极意义，以及不恰当使用科学技术成果可能产生的社会及伦理等方面的副作用，了解社会热点问题中的科学背景和科学知识。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　加大扶持力度，充分发挥老科学家在科学传播中的重要作用。动员和组织已经离开科技创新一线的老科学家积极投入到科学传播活动中去，面向社会，特别是面向青少年，根据自身特长，开展多种形式的科学传播活动，形成学科相对齐全、领域分布合理的专门从事科学传播活动的老科学家队伍，推动老科学家科学传播活动的系统化与规范化。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　通过对现有主要从事科学传播人员的培训及系统教育，通过培养造就专业科学传播人才，建设一支知识结构合理、素质优良、业有所长的高水平专兼结合的科学传播队伍。通过建立合理的评价体制和给予合理的待遇，稳定一支科学传播队伍，并建立合理的队伍动态调整机制。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　通过政策引导、机制激励和措施保障，逐步形成专职、兼职与业余相结合的科学传播队伍。发挥离退休人员管理部门、研究生管理部门和共青团组织的作用，使这支队伍有领导、有规范、有活动、有标识。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　（三）加强科学传播载体建设</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　建设并运营好我院现有的科学传播网站，并积极鼓励各专业性科研网站增加专门的科学传播栏目，择优支撑若干有特色、有影响的科学传播网站；进一步整合全院网络科学传播资源，加强网络科学传播的知识性、文化性、艺术性和娱乐性，提高网站的社会知名度和影响力；通过集约社会资源，建设功能多样、内容丰富、更新及时、国内一流的大型网络科学传播平台，加强共建共享；利用我院科普与科研网络，开辟公众科研实践平台；积极推进虚拟动物馆、古生物馆、微生物馆、病毒馆、天文馆、虚拟植物园等网上展示与互动平台的建设。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　重点扶持若干科普期刊办成在我国具有重要影响和知名品牌的科普期刊，对于条件比较成熟的科普期刊，积极推进其社会化和多元化发展的能力。力争5种以上的科普刊物期发行量超过10万份，至少1种科普期刊期发行量达到100万册。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　继续办好《科学与社会系列报告》，树立品牌，扩大影响力，注重内容和形式的创新，针对公众关心的问题进行深入浅出的讲解和分析，推进报告的系统性和长效性，为公众了解学科发展提供便捷而系统的指南。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　（四）开展有特色科学传播活动</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　继续开展和组织好我院的&ldquo;公众科学日&rdquo;研究院所开放日活动，让更多的公众参加和接受科学教育，提高科学素养。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　加强与中宣部、教育部、科技部、工程院、中国科协等部门的合作，继续搞好&ldquo;科学与中国&rdquo;院士巡讲活动，扩大学科领域和巡讲地区，注重针对性，增加影响力。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　与中宣部、科技部共同搞好&ldquo;院士专家科技西部行&rdquo;和&ldquo;科技老区行&rdquo;活动。注重实效，结合当地现实发展需要和未来发展需求，有针对性地传播科学技术知识，解决当地发展面临的重要科技问题。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　与科技部、科协密切合作，搞好每年的&ldquo;科技周&rdquo;和&ldquo;科普日&rdquo;活动，争取有更多的研究所参加到这些活动中去，并逐步提升我院在国家级科技传播活动中的作用与影响。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　继续办好&ldquo;老专家科普演讲团&rdquo;活动，丰富讲演内容和形式，扩大范围和听众人次，使这项活动成为我院科学传播活动的一个有影响力的品牌。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　继续搞好我院已有的&ldquo;创新论坛&rdquo;、&ldquo;求是论坛&rdquo;、&ldquo;科学人文论坛&rdquo;和&ldquo;中国科学院青年科学家报告团&rdquo;，传播基础性、战略性和前瞻性的科学技术知识、方法与思想，扩大影响力；与上海市合作办好&ldquo;东方科技论坛&rdquo;。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　结合我院科技创新基地建设和重大科技创新项目，及时向公众传播最新科技进展及其经济社会和文化影响；结合重要科技节日、国际组织科技活动年等，开展专项科学传播活动；针对公众关心的重要科学、技术和公共卫生、安全等问题，相关研究所要组织专家开展有针对性的科学传播活动；邀请海外科学家来华开展高水平科学传播活动。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　（五）支持科学传播作品创作</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　建立有效的激励机制，促进原创性科普作品的创作，以奖励等方式，加大对优秀原创性科普作品的扶持与奖励力度，调动科技工作者创作科普作品的积极性，每年投入一定经费，支持编写和出版若干科普精品著作。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　充分利用我院的科技创新资源和科学传播资源，加强与各大媒体的合作，拍摄制作一批优秀科普影视作品，尤其要重视影视制作的科学性、系统性和观赏性，力争制作一批代表性强、影响力大，融思想性、知识性、艺术性、欣赏性于一体并深受公众欢迎的科普影视精品。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　依托我院科学传播基地，推动科普实物的创新与制作，力争经过若干年时间，自主制作的科普实物不仅满足我院科学传播的需要，并积极吸纳社会资源，推动科普实物的社会化和市场化。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　通过项目带动、人才引进、对外交流与合作等措施，推动专门从事科学传播理论研究与教学于一体的科学传播研究基地建设，联合院内外力量，及时吸收国内外成功的科学传播理论与方法，提高我院的科学传播理论研究水平和教学能力，争取使我院成为我国具有很大影响的科学传播理论研究和人才培养基地。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　（六）加强对外合作与交流</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　加强与中央相关部委之间的合作，充分发挥我院优势，满足国家对科学传播的需求。继续加强与中宣部的合作，办好&ldquo;院士专家科技下乡&rdquo;活动，提高社会影响力，争取中宣部在出版、期刊、影视等方面对我院科学传播的更多支持；进一步密切与科技部、基金委和中国科协等部门的合作，推动科学传播平台共建和资源共享，形成国家级科学传播活动的联动；加强与团中央、少工委等社会团体之间的合作与交流，针对青少年群体，开展多种形式、经常化、系统化的科学传播活动。与社会媒体密切合作，开辟专门的科学传播平台、版面和系列图书，并与中央、地方电视台合办科学传播栏目和频道。通过各分院和研究所，加强与地方政府及社会组织的合作与交流，积极参与地方科学传播活动，并发挥骨干和引领作用；积极吸纳社会资源，适时推动我院适合市在市场环境中竞争发展的科学传播基地、载体等社会化和市场化进程。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　提升我院科学传播国际合作的水平，扩大国际科学传播合作交流的层次与渠道，学习海外科学传播的成功经验与做法；鼓励和支持研究所和科学传播基地与海外开展科学传播项目合作、人员交流等。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　（七）保证对科学传播的必要投入</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　十一五期间，院科学传播的经费要在现有基础上有明显增加，加强我院科普能力建设；完成《全民科学素质行动计划纲要》中我院承担的任务；支持科普基地建设和科普组织开展重大科学传播活动；支持我院科普创作和科普出版物的编著出版等。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　四、构建科学合理的管理体系与机制</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　加强宏观调控。加强各级党组（委）对科学传播工作的领导，重视科学传播战略研究以及在此基础上的规划制定工作。充分发挥院机关对全院科学传播工作的统筹协调和顶层设计作用。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　完善研究所为主体的体制。通过资源配置和评价导向等措施，充分发挥研究所和科学传播基地的主动性、自主性与积极性，使他们成为组织科学传播活动、吸纳社会资源、院地合作开展科学传播活动的主体，充分发挥各自优势和特色，自觉开展经常性、系统性的科学传播活动。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　合理配置资源。积极鼓励研究所和科学传播基地吸纳社会资源，对于吸纳多的单位予以奖励；各个研究所要拿出一定比例的创新经费，用于科学传播，定期对社会公众开放；院要加大对科学传播经费投入力度，用于科学传播能力建设，组织重大科学传播行动，支持各单位争取社会资源，开展科学传播研究和战略研究等事项。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　建立科学的评价体制。对于院科学传播基地，要根据科学传播的能力建设、可持续发展、队伍建设以及科学传播活动的社会影响等，建立一套符合科学传播发展规律和国情院情的科学传播考核指标体系；将科学传播指标纳入研究所的综合评价体系中；完善科学传播奖励制度，设立科学传播专项奖励基金，定期奖励科学传播工作出色的单位、个人及作品。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; background-color: rgb(255, 255, 255);\">　　稳步推进科普的市场化。对植物园、博物馆、天文台和报纸、杂志、期刊、网站等可以进行市场化经营的单位，引入市场机制，放手让其经营，经营所得可以投入到科普能力建设中。</p>\r\n', '0', '1514339127', '1514339127', '', '', '0', null, '0', '0', '[100]', null, '', '15');
INSERT INTO `o2o_article` VALUES ('368', '0', '', '全民科学素质行动计划纲要（2006-2010-2020年）', null, null, null, null, null, null, '0', '0', '中科教', '科技部', '', null, '', '<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">根据党的十六大和十六届三中、四中、五中全会精神，依照《中华人民共和国科学技术普及法》和《国家中长期科学和技术发展规划纲要(2006-2020年)》(国发〔2005〕44号〕，制定并实施《全民科学素质行动计划纲 要(2006-2010-2020年)》(以下简称《纲要》)。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　一、前言</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　科学素质是公民素质的重要组成部分。公民具备基本科学素质一般指了解必要的科学技术知识，掌握基本的科学方法，树立科学思想，崇尚科学精神，并具有一定的应用它们处理实际问题、参与公共事务的能力。提高公民科学素质，对于增强公民获取和运用科技知识的能力、改善生活质量、实现全面发展，对于提高国家自主创新能力、建设创新型国家、实现经济社会全面协调可持续发展、构建社会主义和谐社会，都具有十分重要的意义。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　全民科学素质行动计划旨在全面推动我国公民科学素质建设，通过发展科学技术教育、传播与普及，尽快使全民科学素质在整体上有大幅度的提高，实现到本世纪中叶我国成年公民具备基本科学素质的长远目标。本《纲要》提出了全民科学素质行动计划在&rdquo;十一五&rdquo;期间的主要目标、任务与措施和到2020年的阶段性目标。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　二、方针和目标</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　指导方针：</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　以邓小平理论和&rdquo;三个代表&rdquo;重要思想为指导，坚持科学发展观，发挥政府主导作用，充分调动全社会力量共同参与，大力加强公民科学素质建设，促进经济社会和人的全面发展，为提升自主创新能力和综合国力、全面建设小康社会和实现现代化建设第三步战略目标打下雄厚的人力资源基础。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　今后15年，实施全民科学素质行动计划的方针是&rdquo;政府推动，全民参与，提升素质，促进和谐&rdquo;。政府推动一一各级政府将公民科学素质建设作为全面</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　建设小康社会的重要工作，加强领导。各级政府将《纲要》纳入有关规划计划，制定政策法规，加大公共投入，推动《纲要》的实施。社会各界各负其责，加强协作。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　全民参与――公民是科学素质建设的参与主体和受益者，要充分调动全体公民参与实施《纲要》的积极性和主动性，在全社会形成崇尚科学、鼓励创新、尊重知识、尊重人才的良好风尚。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　提升素质――提高公民科学素质是《纲要》的出发点和落脚点。通过实施《纲要》，推动形成全民学习、终身学习的学习型社会，促进人的全面发展。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　促进和谐――认真落实科学发展观，以人为本，实现科学技术教育、传播与普及等公共服务的公平普惠，促进社会主义物质文明、政治文明、精神文明建设与和谐社会建设全面发展。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　目标：</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　到2020年，科学技术教育、传播与普及有长足发展，形成比较完善的公民科学素质建设的组织实施、基础设施、条件保障、监测评估等体系，公民科学素质在整体上有大幅度的提高，达到世界主要发达国家21世纪初的水平。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　到2010年，科学技术教育、传播与普及有较大发展，公民科学素质明显提高，达到世界主要发达国家20世纪80年代末的水平。围绕公民科学素质建设最关键、最具基础性的问题，实现以下目标：</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―促进科学发展观在全社会的树立和落实。重点宣传普及节约资源、保护生态、改善环境、安全生产、应急避险、健康生活、合理消费、循环经济等观念和知识，倡导建立资源节约型、环境友好型社会，形成科学、文明、</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　健康的生活方式和工作方式。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―以重点人群科学素质行动带动全民科学素质的整体提高。未成年人对科学的兴趣明显提高，创新意识和实践能力有较大增强――农民和城镇劳动人口的科学素质有显著提高，城乡居民科学素质水平差距逐步缩小――领导干部和公务员的科学素质在各类职业人群中位居前列。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―科学教育与培训、科普资源开发与共享、大众传媒科技传播能力、科普基础设施等公民科学素质建设的基础得到加强，公民提高自身科学素质的机会与途径明显增</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　三、主要行动</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　根据指导方针和目标，在&rdquo;十一五&rdquo;期间实施以下主要行动：</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　一)未成年人科学素质行动。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　任务：</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―宣传科学发展观，重点宣传我国人口众多、资源有限、人均占有资源远低于世界平均水平的基本国情，使未成年人从小树立人与自然和谐相处和可持续发展的意识。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―完善基础教育阶段的科学教育，提高学校科学教育质量，使中小学生掌握必要和基本的科学知识与技能，体验科学探究活动的过程与方法，培养良好的科学态度、情感与价值观，发展初步的科学探究能力，增强创新意识而配和实践能力。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―普及农村义务教育，切实提高农村中小学科学教育质量。为农村未成年人提供更多参与科普活动的机会，培养改善生存状况、提高生活质量和自我发展的能力。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―开展多种形式的科普活动和社会实践，增强未成年人对科学技术的兴趣和爱好，初步认识科学的本质以及科学技术与社会的关系，培养社会责任感以及交流合作、综合运用知识解决问题的能力。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　措施：</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―通过实施新世纪素质教育工程，推进新科学课程的全面实施。针对不同年龄段学生特点，注重课程的综合性与连贯性――开展学龄前科学启蒙教育，采取有效措施，积极推广义务教育阶段综合性科学课程，逐步推进高中科学课程改革――深化中小学科学课程教材、教学内容和教学 方法改革，充分发挥现代教育技术的作用，改革科学教育 评价制度，定期监测科学教育质量。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―提高农村未成年人科学教育水平和质量。结合农村实际，加强农村中小学现代远程教育的科学教育资源建设，发展针对农村校外未成年人的非正规教育，开展生活能力和生产技能培训等科普活动。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―开展课外科技活动，引导未成年人增强创新意识和实践能力。普及保护生态环境、节约资源能源、心理生理健康、安全避险等知识。加强&rdquo;珍爱生命、远离毒品&rdquo; 和崇尚科学文明、反对愚昧迷信的宣传教育。发挥未成年 人在家庭和社区科普宣传中对成年人的独特影响作用。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―通过&rdquo;大手拉小手科技传播行动&rdquo;、科技专家进校园(社区、科普基地)、中学生进科研院所(实验室) 等活动，组织科技工作者与未成年人开展面对面的科普活动。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―提高母亲的科学素质，重视家庭教育在提高未成年人科学素质中的重要作用。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―新闻出版、广播电视、文化等机构和团体加大面向未成年人的科技传播力度，用优秀、有益、生动的科普作品吸引未成年人，为未成年人的健康成长营造良好的舆论环境。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―整合校外科学教育资源，建立校外科技活动场所与学校科学课程相衔接的有效机制。利用科技类博物馆、科研院所等科普教育基地和青少年科技教育基地的教育资源，为提高未成年人科学素质服务；加强现有青少年宫、儿童活动中心等综合性未成年人校外活动场所的科普教育功能，在有条件的地区建设青少年科技活动中心等专门的科普活动场所。发挥社区教育在未成年人校外教育中的作用。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　二)农民科学素质行动。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　任务：</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―面向农民宣传科学发展观，重点开展保护生态环境、节约水资源、保护耕地、防灾减灾，倡导健康卫生、移风易俗和反对愚昧迷信、陈规陋习等内容的宣传教育，促进在广大农村形成讲科学、爱科学、学科学、用科学的良好风尚，促进社会主义新农村建设。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―围绕科学生产和增效增收，激发广大农民参与科学素质建设的积极性，增强科技意识，提高获取科技知识和依靠科技脱贫致富、发展生产和改善生活质量的能力，并将推广实用技术与提高农民科学素质结合起来，着力培养有文化、懂技术、会经营的新型农民。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―提高农村富余劳动力向非农产业和城镇转移就业的能力。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―提高农村妇女及西部欠发达地区、民族地区、贫困地区、革命老区农民的科学文化素质。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　措施：</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―逐步建立内容丰富、形式多样、适应需求的农村科学教育、宣传和培训体系。制定《农民科技教育培训体系建设规划》和《中国农民科学素质教育大纲》，指导面向农民的各类科学教育活动。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―大力开展农民科技培训。结合实施全国农村党员干部现代远程教育、农村党员基层干部适用技术和市场经济知识培训计划、绿色证书工程、星火科技培训专项行动、双学双比、巾帼科技致富工程等，开展针对性强、务实有效、通俗易懂的农业科技培训，多渠道加大培训力度。使参加绿色证书培训达1000万人；重点培育100万个科技示范户，辐射带动2000万个农户。发挥好农业广播电视学校二农村成人文化技术学校、农村致富技术函授大学、农业科教与网络联盟、有关大中专院校和其他农村成人教育机构在农村科技培训中的作用。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―广泛开展各种形式的科技下乡和群众性、社会性、经常性科普活动。深入开展文化科技卫生&rdquo;三下乡&rdquo;、科技活动周、全国科普日等活动，总结推广科技特派员、科技入户、科技110、科普之冬(春)、科普大集、专家大院、科技咨询服务站、科技大王下乡、科教兴村等行之有效的做法，探索科技人员与农民互动的科技咨询服务长效机制。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―开展农村科技、科普示范活动，建立和完善示范体系。深入开展全国科技进步示范市(县、区)和全国科普示范县(市、区)、乡(镇)、村、户等建设活动，大力发展科技、科普示范基地，发挥好它们的示范作用。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―开展农村富余劳动力转移就业科技培训。建立健全农村劳动力转移培训机制，按照《2003-2010年全国农民工培训规划》要求，积极开展农民工的引导性培训、职业技能培训和岗位培训。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―建立健全农村科技教育、传播与普及服务组织网络和人才队伍。发展农业技术推广机构、农村基层科普组织和农民合作经济组织，重点扶持l万个农村专业技术协会。组织专家咨询服务和志愿者队伍，形成动员科技人员为&rdquo;三农&rdquo;服务的有效机制；培养农民技术员队伍，提高农村实用人才的学习能力、实践能力和传播能力。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―加强农村基层科普能力建设。依托农村中小学、村党员活动室、农村成人文化技术学校、文化站和有条件的乡镇企业飞农村专业技术协会等农民合作组织，发展乡村科普活动场所。推动乡村科普橱窗、宣传栏等建设，开发和充实适应需求、富有特色的展示教育内容。加强民族地区科普工作队建设，提高西部地区特别是边疆民族地区基层的科普能力。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　三)城镇劳动人口科学素质行动。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　任务：</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―在广大城镇宣传科学发展观，重点倡导和普及节约资源、保护环境、节能降耗、安全生产、健康生活等观念和知识，促进经济增长方式的转变和科学文明健康生活方式的形成。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―围绕走新型工业化道路和发展现代服务业的需求，以学习能力、职业技能和技术创新能力为重点，提高第二、第三产业从业人员科学素质，更好地适应经济社会和自身发展的要求。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―围绕城镇化进程的要求，提高进城务工人员的职业技能水平和适应城市生活的能力。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―提高失业人员的就业能力、创业能为和适应职业变化的能力。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　措施：</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―加强对劳动者科技教育培训的宏观管理，进行专门的规划、组织和监督实施。统筹协调各相关部门的关系，合理分工、加强合作。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―将劳动人口应具备的基本科学素质内容纳入各级各类职业教育和成人教育的课程内容和培训教材，将有关科学素质的要求纳入国家职业标准，作为各类职业培训、考核和鉴定的内容。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―开展各种形式的劳动预备制培训、再就业培训、创业培训、农民工培训和各类从业人员的在岗培训和继续教育。城镇职工在职培训达到2.5亿人次，失业人员再就 业培训1500万人，农民工培训2亿人。使新增劳动力接受劳动预备制培训的比例由目前的70%提高到90%。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―在企业广泛开展科普宣传、技能培训和创建学习型组织、争做知识型职工等活动，着力加强科学方法、科学思想和科学精神教育，提高职工的科学文化素质。鼓励群众性技术创新和发明活动。充分发挥企业科协、职工技协、研发中心等组织和机构的作用。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―建立企业事业单位从业人员带薪学习制度，鼓励职工在职学习，形成用人单位和从业人员共同投资职业培训的机制。在职业培训中，加大有关科学知识的内容。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―优化整合各种教育培训资源，实现资源共享，形成广覆盖、多层次的教育培训网络，为劳动者提高科学素质提供更多机会和途径。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―以城镇社区为依托，通过社区科普活动室、科普学校、科普画廊等机构和设施，开展多种形式的科普宣传，建设学习型社区，发挥社区在提高劳动者科学素质方面的作用。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　四)领导干部和公务员科学素质行动。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　任务：</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―在面向领导干部普及科学技术知识的同时，突出弘扬科学精神，提倡科学态度，讲究科学方法，增强领导干部贯彻落实科学发展观的自觉性和科学决策的能力。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―围绕贯彻落实科学发展观和建设学习型机关，调动公务员提高自身科学素质的积极性和主动性，增强终身学习和科学管理的能力。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　措施：</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―将提高科学素质列为公务员和事业单位、固有企业负责人培训教育规划和相关计划的重要内容。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―各级机关在创建学习型机关中，其学习培训制度应体现提高领导干部和公务员科学素质的要求。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―各级行政院校和干部学院将提高学员科学素质列入教学计划，采取切实措施加以落实。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―举办讲座、报告会等科普活动，编辑出版相关的科普读物，向领导干部和公务员介绍现代科技知识及发展趋势，传播科学思想、科学方法、科学精神。组织公务员参与科普活动。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―报刊、电台、电视台和各级政府网站创办有关提高领导干部和公务员科学素质的栏目和节目。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―在公务员录用考试大纲及题库中，列入与科学素质要求有关的具体内容。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　四、基础工程</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　配合上述行动计划，&rdquo;十一五&rdquo;期间重点实施以下基础工程：</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　一〉科学教育与培训基础工程。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　任务：</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―加强教师队伍建设，培养一支专兼结合、结构合理、素质优良、胜任各类科学教育与培训的教师队伍。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―加强教材建设，改革教学方法，形成适应不同对象需求、满足科学教育与培训要求的教材教法。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―加强教学基础设施建设，充分利用现有的教育培训场所、基地，配备必要的教学仪器和设备，为开展科学教育与培训提供基础条件支持。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　措施：</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　加强中小学科学教育教师队伍建设。采取多种途径，开展中小学和农村成人文化技术学校科学教育教师培训工作，尤其重视县以下中小学科学教育教师的培训，提高学历层次和实施科学教育的能力和水平。鼓励师范院校设置科学教育专业，培养具有较高专业水平和职业能力的科学教育教师。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―建立科技界和教育界合作推动科学教育发展的有效机制。动员组织高等院校、科研院所的科技专家参与中小学科学课程教材建设、教学方法改革和科学教师培训。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―加强科学教育与培训志愿者队伍建设。发挥老科技工作者协会、老教授协会的作用，动员组织离退休科技工作者、教育工作者、公务员和企业事业单位管理者参与科学教育与培训。发展青少年科技辅导员队伍，提高辅导员的素质和能力。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―加强科学教育研究，按照普及性、基础立性、发展性的要求，促进科学课程的完善与发展，更新课程内容，提高中小学科学课程的教材质量，改进教学方法。以创新意识和实践能力的培养为重点，促进学习方式的变革。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―加强职业教育、成人教育和各类培训中科学教育的教材建设。根据农民、城镇劳动人口、领导干部和公务员的特点和需求，以科学发展观、先进适用技术、职挫技能、现代科技知识为主要内容编写教材。重视少数民族文字的教材编写和音像类教材的开发制作。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―加强中小学特别是农村中小学科学教育基础设施建设。根据科学课程的需要，建立健全实验室、图书室，充实实验仪器、教具、音像设备、计算机等教学器材，并面向社会提供服务。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―增强行政院校和干部学院，高等院校、科研院所，职业学校、函授学校、广播电视学校等机构的科学教育和培训功能。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―利用社会资源开展科学教育和培训。鼓励和支持科技馆等科普场馆、社区学校、成人文化技术学校等开展科学教育与培训。构建不同职业、不同工种、布局合理的职业技能培训基地。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　二)科普资源开发与共享工程。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　任务：</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―引导、鼓励和支持科普产品和信息资源的开发，繁荣科普创作。围绕宣传落实科学发展观，创作出一批紧扣时代发展脉搏、适应市场需求、公众喜闻乐见的优秀作品，并推向国际市场，改变目前科普作品&rdquo;单向引进&rdquo;的局面。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―集成国内外科普信息资源，建立全国科普信息资源共享和交流平台，为社会和公众提供资源支持和公共科普服务。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　措施：</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―建立有效激励机制，促进原创性科普作品的创作。以评奖、作品征集等方式，加大对优秀原创科普作品的扶持、奖励力度，吸引和鼓励社会各界参与科普作品创作；调动科技工作者科普创作的积极性，把科普作品纳入业绩考核范围；建立将科学技术研究开发的新成果及时转化为科学教育、传播与普及资源的机制；鼓励和支持科普创作、科技传播专业团体发挥作用；制定优惠政策和相关规范，鼓励和吸引更多社会力量参与科普资源开发。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―加强合作与交流。推动科普、科技、教育、传媒界的有效合作，引进国外优秀作品，借鉴国际先进创作理念和方法，促进我国科普创作整体水平的提高。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―集成国内外现有科普图书、期刊、挂图、音像制品、展教品、文艺作品以及图片、科普志愿者等各类科普信息，建成数字化科普信息资源库和共享交流平台，通过互联网为社会和公众提供资源支持和公共科普服务。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―开展优秀科普作品的推介、展演、展映、展播和展示活动，扩大科普信息资源的共享范围。针对公众生产生活的实际需求，组织编制简明生动的科普资</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―制定相关法规、规章和标准，充分保护知识产权，创造公共科普信息资源公平使用的法制环境。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　三)大众传媒科技传播能力建设工程。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　任务：</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―加大各类媒体的科技传播力度。电视台、广播电台科技节目的播出时间，各类科普出版物的品种和发行量，综合性报纸科技专栏的数目和版面，科普网站和门户网站的科技专栏等大幅度增加。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―打造科技传播媒体品牌。提高科技频道、专栏制作传播质量，培育一批读者量大、知名度高的综合性报纸科技专栏、专版和科普图书、报刊、音像制品、电子出版物，形成一批在业内有一定规模和影响力的科普出版机构。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―发挥互联网等新型媒体的科技传播功能，培育、扶持若干对网民有较强吸引力的品牌科普网站和虚拟博物馆、科技馆。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　措施：</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―鼓励、支持&rdquo;科技博览&rdquo;、&rdquo;科技之光&rdquo;、&rdquo;科普大篷车&rdquo;等电视科技栏目进一步提高质量，使其成为有广泛影响的媒体精品。择优扶持若干有特色、覆盖率高的知名科普网站。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―制定优惠政策和相关规范，积极培育市场，推动科普文化产业发展。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―建立与市场、公众需求相适应的管理体制与运行机制，树立以消费者为中心的经营理念。引进现代营销模式与先进编创技术，注重市场调研，提高播出和编辑出版质量。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―建立与市场经济相适应的科普出版物发行渠道，加强网点建设，大力扶持科普出版物在农村和边远地区、民族地区的发行工作。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　--提高各类媒体对公共卫生事件和重大自然灾害等突发事件的反应能力，指导公众以科学的行为和方式应对突发事件。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―研究开发网络科普的新技术和新形式。开辟具有实时、动态、交互等特点的网络科普新途径，开发一批内容健康、形式活泼的科普教育、游戏软件。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　四)科普基础设施工程。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　任务：</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―拓展和完善现有基础设施的科普教育功能。对现有科普设施进行机制改革和更新改造，充实内容、改进服务、激发活力，满足公众参与科普活动的需求。整合利用社会相关资源，充分发挥科研基础设施的资源优势，发展青少年科技教育基地和科普教育基地。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―多渠道筹集资金，在充分研究论证的前提下，新建一批科技馆、自然博物馆等科技类博物馆。各直辖市和省会城市、自治区首府至少拥有1座大中型科技馆，城区常住人口100万人以上的大城市至少拥有1座科技类博物馆，全国科技类博物馆的接待能力有显著增长。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―发展基层科普设施。在城乡社区建设科普画廊、科普活动室、运用网络进行远程科普宣传教育的终端设备等设施；增强综合性未成年人校外活动场所的科普教育功能，有条件的市(地)和县(市、区)可建设科技馆等专门科普场馆；在一些市(州、盟和县)配备科普大篷车，以&ldquo;流动科技馆&rdquo;的形式为城乡社区、学校特别是贫困、边远地区提供科普服务。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　措施:</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―突出社会公益性，加强对科普基础设施建设的宏观指导。制定科普设施的发展规划、建设标准、认定办法和管理条例，规范科普设施的建设与管理。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―科普基础设施建设纳入国民经济和社会事业发展总体规划及基本建设计划，加大对公益性科普设施建设和运行经费的公共投入。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―对科普教育功能薄弱的设施进行更新改造，完善基层科普设施的功能；引进和开发适应公众需求的活动项目，创新活动方式，增强吸引力，提高管理水平和服务质量。增强社区科普设施为老年人服务的功能，为他们老有所学、老有所乐、老有所为提供条件和机会。落实科普场馆对未成年人和老年人的优惠措施</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―鼓励社会力量参与科普基础设施建设。落实有关优惠政策，鼓励社会各界对公益性科普设施建设提供捐赠、资助；吸引境内外资本投资兴建和参与经营科普场馆；鼓励有条件的企业事业单位根据自身特点建立专业科普场馆；落实有关鼓励科普事业发展的税收优惠政策，鼓励社会力量参与科普基础设施建设。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―国家级青少年科技教育基地和科普教育基地总数由目前的300余座增加至500座，省部级青少年科技教育基地和科普教育基地总数由目前的1000余座增加至2000 座，定期对公众免费或优惠开放。有条件的科研院所、高等院校、自然科学和社会科学类团体向公众开放实验室、陈列室和其他场地设施；鼓励高新技术企业对公众开放研发机构和生产车间。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―培育科普展览、展品市场，推动设计制作社会化；制定技术规范和设计制作机构的资质认定办法；择优扶持一批设计制作机构，提高设计制作水平。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　五、保障条件</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　一)政策法规。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　完善有关公民科学素质建设的政策法规，明确政府、社会组织、企业及公民个人在公民科学素质建设中的责任、权利和义务。根据形势发展需要，对现有政策法规进行修订、补充和调整。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―在国民经济和社会发展计划和有关科学技术教育、传播与普及的法律法规中，体现公民科学素质建设的 目标和要求。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―制定《中华人民共和国科学技术普及法》实施细则。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―制定鼓励和吸引境内外机构、个人独资或合作兴办科学技术教育、传播与普及机构的政策。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―制定表彰和奖励政策。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　二)经费投入。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　采取多种措施，加大政府和社会投入，形成多渠道投入机制，为《纲要》的实施提供资金保障。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―加大财政保障力度。切实执行《中华人民共和国教育法》和《中华人民共和国科学技术普及法》的有关规定，各级政府根据财力情况和公民科学素质建设发展的实际需要，逐步提高教育、科普经费的增长速度，并将科普经费列入同级财政预算，保障《纲要》的顺利实施。中央财政根据财力状况，逐步加大对地方的转移支付力度。各级政府要从中央财政的财力性转移支付资金中安排一定的经费用于公民科学素质建设。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―落实各相关部门实施经费。各有关部门、事业单位和人民团体根据承担的《纲要》实施任务，按照国家预算管理的规定和现行资金渠道，统筹考虑和落实所需经费。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―鼓励捐赠，广辟社会资金投入渠道。进一步完善捐赠公益性科普事业个人所得税减免政策和相关实施办法，广泛吸纳境内外机构、个人的资金支持公民科学素质建设。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　三)队伍建设。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　培养专业化人才，发掘兼职人才，建立志愿者队伍，加强理论研究，为公民科学素质建设提供人才保障和智力支撑。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―开展多种形式的培训和进修活动，加强业务学习，全面提升在职科学技术教育、传播与普及人员的科学素质和业务水平。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―通过高等院校和有关研究机构培养大批科学技术传播与普及专门人才；改革文博专业课程内容，为不同类型科普场馆培养适应性广泛的专业人才。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―建立有效机制和相应激励措施，充分调动在职科技工作者、大学生、研究生和离退休科技、教育、传媒工作者等各界人士参加公民科学素质建设的积极性，发挥他们的专业和技术特长，形成一支规模宏大飞素质较高的兼职人才队伍和志愿者队伍。对在公民科学素质建设中作出重要贡献的个人和组织予以表彰和奖励。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―增强科技界的责任感，支持科技专家主动参与科学教育、传播与普及，促进科学前沿知识的传播。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―开展公民科学素质建设理论研究，加强国内外学术交流，把握基本规律和国际发展趋势，为公民科学素质建设的实践提供指导。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　六、组织实施</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　一)组织领导。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―国务院负责领导《纲要》的实施工作，成立《纲要》实施领导小组，进行统一动员部署和检查监督。各有关部门、事业单位和人民团体按照《纲要》的要求，将有关任务纳入相应工作规划和计划，充分履行相关工作职责，发挥各自优势，密切配合，形成合力，切实推进公民科学素质建设。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―地方各级政府将公民科学素质建设纳入当地国民经济和社会发展的总体计划，将《纲要》的实施纳入政府的议事日程，纳入业绩考核。一一一建立和完善实施《纲要》的工作机制。《纲要》实施领导小组办公室设在中国科学技术协会，承担领导小组的日常工作，并定期向领导小组汇报。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　二)监测评估。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―制定《中国公民科学素质基准》。根据社会主义现代化建设的战略目标，结合我国国情，借鉴国外相关经验和成果，围绕公民生活和工作的实际需求，提出公民应具备的基本科学素质内容，为公民提高自身科学素质提供衡量尺度和指导，并为《纲要》的实施和监测评估提供依据。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―建立公民科学素质状况和《纲要》实施的监测指标体系，并纳入国家社会发展指标体系。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　―委托有关监测评估机构对公民科学素质状况和《纲要》实施情况进行监测评估，并提出相应对策和建议。</p>\r\n', '0', '1514339195', '1514339195', '', '', '0', null, '0', '0', '[100]', null, '', '15');
INSERT INTO `o2o_article` VALUES ('369', '0', '', '教育部关于培育和践行社会主义核心价值观进一步加强中小学德育工作的意见', null, null, null, null, null, null, '0', '0', '中科教', '教育部', '', null, '', '<p align=\"left\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">各省、自治区、直辖市教育厅（教委），新疆生产建设兵团教育局：</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　社会主义核心价值观是中国特色社会主义的本质体现。培育和践行社会主义核心价值观、加强中小学德育是推进中国特色社会主义事业的必然要求，是深化教育领域综合改革、促进学生健康成长的现实选择。为贯彻党的十八大和十八届二中、三中全会精神，落实《中共中央办公厅关于培育和践行社会主义核心价值观的意见》（中办发〔2013〕24号），切实把立德树人作为教育的根本任务，针对当前的新形势新要求，现就培育和践行社会主义核心价值观，进一步增强中小学德育的时代性、规律性、实效性，提出如下意见。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　一、充分体现时代性，加强中小学德育的薄弱环节</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　1．加强中华优秀传统文化教育。各级教育部门和中小学校要深入开展中华优秀传统文化教育，弘扬以爱国主义为核心的民族精神和以改革创新为核心的时代精神，引导学生增强民族文化自信和价值观自信。要深入浅出地讲清楚中华优秀传统文化的历史渊源、发展脉络、基本走向，让学生逐步明白中华文化的独特创造、价值理念、鲜明特色。要加强中国特色社会主义宣传教育和中国梦主题教育活动，探索形成爱学习、爱劳动、爱祖国教育活动的有效形式和长效机制。改善时事教育，举办中小学时事课堂展示活动，用鲜活事例教育广大学生，引导他们逐步树立中国特色社会主义的道路自信、理论自信、制度自信。尊重学生个性发展，帮助学生树立积极向上的个人理想，引导他们自觉将个人理想与祖国发展紧密联系起来，为个人幸福、社会进步、国家富强而不断成长。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　2．加强公民意识教育。各级教育部门和中小学校要大力开展公民意识教育，培养公民美德，发扬社会公德，增强国家认同，引导广大学生了解公民的基本权利与义务。要认真落实《中小学法制教育指导纲要》，促进学生树立社会主义民主法治、自由平等、公平正义理念，养成遵纪守法、遵守规则的意识和行为习惯。认真落实《中小学文明礼仪教育指导纲要》，引导学生养成诚实守信、孝敬感恩、团结友善、文明礼貌的行为习惯。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　3. 加强生态文明教育。各级教育部门和中小学校要普遍开展生态文明教育，以节约资源和保护环境为主要内容，引导学生养成勤俭节约、低碳环保的行为习惯，形成健康文明的生活方式。要深入推进节粮节水节电活动，持续开展&ldquo;光盘行动&rdquo;。加强大气、土地、水、粮食等资源的基本国情教育，组织学生开展调查体验活动，参与环境保护宣传，使他们认识到环境污染的危害性，增强保护环境的自觉性。加强海洋知识和海洋生态保护宣传教育，引导学生树立现代海洋观念。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　4．加强心理健康教育。各级教育部门和中小学校要认真落实《中小学心理健康教育指导纲要（2012年修订）》，全面推进心理健康教育。加强制度建设，建立健全心理健康教育的各项规章制度，规范和促进学校心理健康教育工作。加强课程建设，保证心理健康教育时间，合理安排教育内容，创新活动形式，科学有效开展心理健康教育。加强场所建设，有条件的学校要设立中小学心理辅导室，保证心理健康教育必要的活动空间。加强队伍建设，每所学校至少配备一名专兼职心理健康教育教师，关心其生活条件与专业发展。加强心理健康教育教师专业培训，同时要提高全体教师特别是班主任开展心理健康教育的能力，培养学生积极健康的心理品质。加强生命教育和青春期教育，促进学生身心和谐发展。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　5．加强网络环境下的德育工作。各级教育部门和中小学校要不断探索网络环境下德育工作的有效途径，引导学生正确对待网络虚拟世界，合理使用互联网、手机以及微博、微信等新媒体。加强网络道德教育，引导学生文明上网，树立网络责任意识，增强对不良信息的辨别能力，防止网络沉迷或受到不良影响。加强网络法制教育，培养学生依法使用网络的意识，自觉抵制网络不法行为。加强网络正面引导，推进德育工作信息化建设，充分利用国家教育资源公共服务平台和积极健康的网络教育资源，凝聚广大师生，形成良好互动。鼓励开展积极向上的校园网络文化活动，组织以&ldquo;中国梦&rdquo;&ldquo;三爱&rdquo;&ldquo;三节&rdquo;为主题的微视频创作展示。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　二、准确把握规律性，改进中小学德育的关键载体</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　6. 改进课程育人。各级教育部门和中小学校要充分发挥课程的德育功能，将社会主义核心价值观的内容和要求细化落实到各学科课程的德育目标之中。加强品德与生活、品德与社会、思想品德、思想政治课程的教育教学。推动学科统筹，特别是加强德育、语文、历史、体育、艺术等课程教学的管理和评价，提升综合育人效果。开发有效的地方课程和学校课程，丰富学校德育资源。开展学科德育精品课程展示活动，引导各学科教师依据课程标准和学生实际情况，设计相应的教学活动，在传授知识和培养能力的同时，将积极的情感、端正的态度、正确的价值观自然融入课程教学全过程。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　7．改进实践育人。各级教育部门和中小学校要广泛开展社会实践活动，充分体现&ldquo;德育在行动&rdquo;，要将社会主义核心价值观细化为贴近学生的具体要求，转化为实实在在的行动。要普遍开展以诚实守信、文明礼貌、遵纪守法、勤劳好学、节约环保、团结友爱等为主题的系列行动；组织学生广泛参加&ldquo;学雷锋&rdquo;等志愿服务和社会公益活动；教育学生主动承担家务劳动；组织学生在每个学段至少参加1次学工学农生产体验劳动，农村学校应普及适当形式的种植或养殖。要广泛利用博物馆、美术馆、科技馆等社会资源，充分发挥各类社会实践基地、青少年活动中心（宫、家、站）等校外活动场所的作用，组织学生定期开展参观体验、专题调查、研学旅行、红色旅游等活动。逐步完善中小学生开展社会实践的体制机制，把学生参加社会实践活动的情况和成效纳入中小学教育质量综合评价和学生综合素质评价。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　8．改进文化育人。各级教育部门和中小学校要挖掘地域历史文化传统，因地制宜开展校园文化建设，将社会主义核心价值观融入校园物质文化、精神文化、制度文化、行为文化之中。要加强图书馆建设，提升藏书质量，开展经常性的读书活动。学校要张贴社会主义核心价值观24字或书写上墙，让学生熟练背诵。要利用升国旗、入党入团入队等仪式和重大纪念日、民族传统节日等契机，开展主题教育活动，传播主流价值。要加强校风班风学风建设，组织开展丰富多彩、生动活泼的文艺活动、体育活动、科技活动，支持学生社团活动，充分利用板报、橱窗、走廊、校史陈列室、广播电视网络等设施，营造体现主流意识、时代特征、学校特色的校园文化氛围。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　9．改进管理育人。各级教育部门和中小学校要积极推进学校治理现代化，将社会主义核心价值观的要求贯穿于学校管理制度的每一个细节之中。学生的行为规范管理、班级民主管理和各种面向学生制定的规章制度，都要充分体现友善、平等、和谐。要明确学校各个岗位教职员工的育人责任，把育人要求和岗位职责统一起来，将学生的全面发展作为学校一切工作的出发点和落脚点。要加强班主任培训，提高班主任工作能力，探索推行德育导师制。加强师德建设，落实《教育部关于建立健全中小学师德建设长效机制的意见》，引导广大教师自觉践行社会主义核心价值观，爱岗敬业，教书育人，做学生健康成长的指导者和引路人。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　三、大力增强实效性，夯实中小学德育的基本保障</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　10．改进方式方法。各级教育部门和中小学校要加强德育规律研究，从中小学生的身心特点和思想实际出发，注重循序渐进、注重因材施教，润物细无声，真正把德育工作做到学生心坎上。要突出知行结合，着力培养学生养成良好的行为习惯，客观真实记录学生行为表现情况，引导学生将道德认知转化为道德实践。要勇于改革创新，探索德育工作的新途径、新方法，定期开展德育教研活动，提升教师德育专业能力。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　11．加强组织领导。各级教育部门和中小学校要将德育工作纳入教育发展规划和学校工作计划，确立年度德育工作目标和任务，明确相关责任主体。保障德育工作经费，纳入教育经费年度预算，满足学校德育工作需求。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　12．强化协同配合。各级教育部门和中小学校要主动联系综治、公安、民政、共青团、妇联、关工委等相关部门，切实加强对进城务工人员随迁子女、农村留守儿童的关爱和教育。开展流浪未成年人、有严重不良行为未成年人的教育帮扶，做好预防青少年违法犯罪工作。大力推动家庭教育，普及中小学家长委员会和家长学校，改进家访制度，鼓励家长参与学校管理，树立科学观念，运用良好家风，促进子女成长成才。要积极争取当地党委政府支持，整合社会资源，净化社会环境，形成育人合力，共同发挥正能量。</p>\r\n\r\n<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　13．完善督导评价。各级教育督导部门要加强对中小学德育工作的督导检查，将其纳入教育综合督导的重要内容及责任区督学的工作范畴，使之制度化、规范化。督导评价结果要以适当方式向社会公布。教育部将定期表彰全国中小学优秀德育课教师、优秀班主任、优秀德育工作者和德育工作先进集体，探索品德优秀学生表彰激励机制。各地教育部门和中小学校也要完善相应的表彰激励机制，发挥榜样示范作用，努力促进年轻一代全面发展和健康成长。</p>\r\n', '0', '1514339252', '1514339252', '', '', '0', null, '0', '0', '[100]', null, '', '15');
INSERT INTO `o2o_article` VALUES ('370', '0', '', '北京市中小学培育和践行社会主义核心价值观实施意见', null, null, null, null, null, null, '0', '0', '中科教', '北京市人民政府办公厅', '', null, '', '<p><span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">按照党中央、国务院和市委、市政府关于培育和践行社会主义核心价值观的部署和要求，现就在本市中小学大力培育和践行社会主义核心价值观提出如下实施意见：</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　一、培育和践行社会主义核心价值观的重要意义和总体要求</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　1.充分认识培育和践行社会主义核心价值观的重要意义。核心价值观承载着一个民族、一个国家的精神追求，是最持久、最深层的力量。在本市中小学培育和践行社会主义核心价值观，是推进中国特色社会主义伟大事业、实现中华民族伟大复兴中国梦的战略任务；是坚持和强化首都城市战略定位，建设国际一流的和谐宜居之都的必然要求；是全面贯彻党的教育方针，坚持立德树人，进一步深化基础教育综合改革，全面实施素质教育的重要保证；是促进中小学生健康成长，成为德智体美全面发展的社会主义事业可靠接班人和合格建设者的内在需要。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　2.培育和践行社会主义核心价值观的总体要求。深入贯彻落实党的十八大、十八届三中全会和习近平总书记系列重要讲话特别是考察北京工作时的重要讲话精神，全面落实《中共北京市委关于培育和践行社会主义核心价值观的实施意见》(京发〔2014〕11号)，遵循教育规律、学生成长规律和社会主义核心价值观传播规律，以&ldquo;富强、民主、文明、和谐，自由、平等、公正、法治，爱国、敬业、诚信、友善&rdquo;为基本内容，以理想信念为核心，以养成良好行为习惯为重点，注重教育引导、舆论宣传、实践养成、文化熏陶、制度保障相结合，将培育和践行社会主义核心价值观融入到教育教学全过程。创新育人理念和手段，运用学生喜闻乐见的方式，搭建实践平台，拓展教育渠道，积极争取社会各方面力量协同育人，使社会主义核心价值观成为每一名学生的精神追求和自觉行动。切实将习近平总书记提出的&ldquo;记住要求、心有榜样、从小做起、接受帮助&rdquo;16字要求落到实处。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　二、注重宣传教育，引导学生记住要求</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　3.人人知晓、熟记熟背。用通俗易懂的方式让学生记住社会主义核心价值观的基本内容和实践要求。通过宣传橱窗、电子屏幕、宣传画、黑板报、广播电视、校园网、微博、微信等方式，加大宣传力度，使社会主义核心价值观基本内容在校园处处可见、人人知晓，营造浓厚的宣传教育氛围。开展童谣传唱、主题班队会、时事论坛、报告会等教育活动，深化宣传教育效果，使小学生逐步做到熟记熟背、养成习惯；初中生理解要求，学会践行；高中生立志成才，学会做人。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　4.进教材、进课堂，入脑入心。发挥课堂教学主渠道作用，使中小学生逐步理解社会主义核心价值观的丰富内涵，准确把握培育和践行社会主义核心价值观的具体要求。修订中小学学科德育指导纲要和各学科教学指导意见，融入社会主义核心价值观的基本内容和要求。将德育课程和少先队活动课作为系统传播社会主义核心价值观的重要途径，改进教学方法，增强针对性、实效性。加强学科间相互配合，发挥综合育人作用。鼓励和支持有条件的区县和学校开发地方课程和校本课程，不断丰富教育教学资源。开展学科德育精品课程展示活动，引导教师依据课程标准和学生实际情况，设计相应的教学活动，努力做到每堂课不仅传播知识，而且传授美德，让社会主义核心价值观的种子在学生心中生根发芽。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　5.传承中华优秀传统文化。教育引导中小学生弘扬民族精神，传承民族文化，发扬传统美德，增强民族文化自信和价值观自信。分阶段有序推进中华优秀传统文化教育，小学阶段培养学生热爱中华优秀传统文化的感情，引导学生了解中华优秀传统文化的丰富多彩；初中阶段引导学生认识我国统一多民族国家的文化传统和基本国情；高中阶段引导学生感悟中华优秀传统文化的精神内涵，使中华优秀传统文化成为涵养社会主义核心价值观的重要源泉。充分发挥首都丰富的文化资源优势，建立中华优秀传统文化教育基地。深入开展中华优秀传统文化艺术传承校创建活动，邀请千名传统文化名家、非物质文化遗产传承人等进校园、进课堂；开展民族艺术进校园、京剧进课堂等工作，举办国学讲座、经典诵读、民族体育艺术等丰富多彩的传统文化活动；开展&ldquo;我们的节日&rdquo;主题活动，挖掘和利用民族传统节日、国际性节日、政治性节日以及党史国史上重大事件、重要人物纪念日等蕴藏的巨大教育资源，组织开展纪念活动。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　6.注重学校文化熏陶浸润。进一步加强学校文化建设，强化以文育人、以文化人功能。继续开展文化建设示范校创建活动，将社会主义核心价值观融入校园物质文化、精神文化、制度文化之中，建设体现社会主义特点、时代特征、首都特质和学校特色的学校文化。完善学校制度，加强校风、教风、学风建设，坚持以人为本，依法治校，规范办学，尊重学生主体地位，营造平等和谐的学校文化氛围。利用升国旗、入党入团入队、开学典礼、毕业典礼、十四岁迈入青春门、十八岁成人礼等仪式，传播主流价值观。用社会主义核心价值观引领网络文化，营造积极向上的网络环境。开展经常性的读书活动和丰富多彩的文艺、体育、科技等活动，支持学生社团活动，努力做到每次活动不仅健康身心，而且陶冶情操。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　三、坚持典型引路，引导学生心有榜样</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　7.学习英雄人物和先进人物。把社会主义核心价值观的要求转化为学生可学、可模仿的人物形象，用鲜活的事例引导学生理解和践行社会主义核心价值观。组织学生参观纪念馆、烈士陵园、名人故居、英雄和先进人物事迹展览等活动，引导学生广泛阅读励志书籍、连环画等读物，学习了解英雄人物、先进人物的光辉事迹和崇高品德。邀请&ldquo;北京榜样&rdquo;、航天英雄、劳动模范、道德模范、奥运冠军、科学家、青年志愿者，以及助人为乐、诚实守信、敬业奉献、孝亲敬老等先进典型代表走进校园，帮助学生学会欣赏、学习和借鉴美好事物，引导学生见贤思齐、崇德向善，帮助学生树立远大志向、培育美好心灵。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　8.发挥教师示范引领作用。教师是学生健康成长的指导者和引路人，要做到爱岗敬业、严谨治学、教书育人，以高尚情操、丰富学识和人格力量影响学生。加强师德建设，把社会主义核心价值观纳入教师教育课程体系，融入教师职前培养准入、职后培训和管理的全过程，把师德表现作为教师聘任、考核和评价的首要内容，构建师德师风建设长效机制，引导教师将社会主义核心价值观落实到教学、管理、服务各项工作中。开展学生喜爱的教师、优秀教师和优秀班主任评选工作，大力宣传先进典型和先进事迹。加强教师队伍建设，重视德育教师、班主任的配备和选拔，不断提升广大教师思想道德素质和育人能力。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　9.树立同伴榜样。发挥身边榜样的示范作用，引导学生发现榜样、学习榜样、争做榜样。坚持开展寻找&ldquo;身边道德榜样&rdquo;、北京市优秀中学生、北京市优秀少先队员、最美孝心少年等评选表彰，举办优秀学生事迹报告会、&ldquo;发现身边的美&rdquo;微作品展示等活动，加大对先进人物、事迹的宣传力度，树立一批市、区县和学校的同伴榜样。通过身边人、身边事，在学生中营造比志气、比学习、比劳动、比健康、比奉献的良好氛围，凝聚和传递社会正能量。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　四、强化实践体验，引导学生从小做起</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　10.引导学生从我做起。教育学生从自己做起，从身边做起，从小事做起，一点一滴积累，养成好思想、好品德。开展&ldquo;爱学习、爱劳动、爱祖国&rdquo;、&ldquo;节粮、节水、节电、节约钱物&rdquo;和&ldquo;美丽中国、绿色北京&rdquo;、&ldquo;清洁空气蓝天行动&rdquo;等专题教育活动，探索活动的有效形式和长效机制。引导学生自己的事情自己做、集体的事情争着做、公益的事情积极做，主动承担家务劳动和学校劳动，积极参加学工学农生产体验劳动。开展多种形式的道德实践和法制教育活动，培养中小学生公民意识。通过教育引导，使学生从做好小事、管好小节起步，逐步树立社会主义民主法制、自由平等、公平正义理念，养成遵纪守法、遵守规则、诚实守信、孝敬感恩、团结友善、文明礼貌的行为习惯。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　11.引导学生积极参加志愿服务。教育学生友善待人、助人为乐，增强社会责任感，在服务他人、奉献社会中践行社会主义核心价值观。大力弘扬雷锋精神，广泛开展&ldquo;续写雷锋日记&rdquo;等形式多样的学雷锋活动，推动学雷锋活动常态化。制定落实《中小学开展志愿服务指导意见》，倡导志愿者实名注册，建立健全志愿服务政策保障，完善评价和鼓励学生长期坚持参加志愿服务的激励措施。培育校园志愿服务组织，设立中小学志愿服务岗，开发和拓展校级、班级志愿服务项目，引导学生积极参加校内、社区以及孝亲敬老等志愿服务活动。将参加志愿服务情况记入成长记录，纳入学生综合素质评价，培养学生志愿服务意识和能力。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　12.引导学生知行统一。加强实践体验活动载体和平台建设，实施&ldquo;一十百千工程&rdquo;，引导学生在认知理解基础上自觉践行，做到知行统一。每个学生在中小学学习期间至少参加一次天安门广场升旗仪式，分别走进一次国家博物馆、首都博物馆、抗日战争纪念馆；至少参加十次集体组织的社会公益活动，观看百部优秀影视作品、阅读百本优秀图书，学习了解百位中外英雄人物、先进人物的典型事迹和优秀品格；市、区县教育主管部门和有关单位要共同完善社会大课堂建设机制，通过政府购买服务等方式在图书馆、博物馆等千余个具备相应社会资源的单位培养和聘用千名课外辅导教师。学校各学科平均应有不低于10%的课时在社会大课堂辅导完成。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　五、加强指导服务，引导学生接受帮助</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　13.激励学生在最好的方面做最好的自己。教育引导学生正确认识自己，善于把握和发扬好自己的优点，发现和认识自己的缺点和不足，虚心接受教师、家长和同学的意见和批评，不断完善自我、追求进步，树立信心，努力使自己做到越来越好。教育引导学生正确对待他人，善于学习和借鉴他人的长处，学会谦虚、学会宽容，能够更好容纳、接受、帮助他人。学校和教师要树立科学的育人理念，强化多元评价和发展性评价，全面加强心理健康教育，关爱和帮助每一位学生，促进每一位学生健康成长和全面发展。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　14.推进协作育人。形成学校、家庭和社会三结合的教育网络。学校要建立健全协作育人机制，争取家庭和社会对学校育人的支持和配合。学校要建立家长委员会制度，充分发挥家长在育人过程中的重要作用。加强家长学校建设，通过网上家长学校和&ldquo;父母学院&rdquo;工程等途径为家长提供家庭教育指导服务。家长要时时处处给孩子做榜样，用正确行动、正确思想、正确方法教育引导孩子，注重家风建设，为青少年健康成长营造良好家庭环境。社区应为学校开展活动提供必要的资源和场所，协调辖区各方面力量配合学校开展教育教学活动。社会各方面要关心、支持、参与协作育人，形成推进育人工作的合力。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　15.发挥好共青团和少先队组织的作用。加强入队入团教育，开展理论学习和形式多样的实践教育活动。依托红领巾学校、少年先锋团校、中学生业余党校，大力加强对学生骨干人才的理想信念教育，发挥他们的示范引领作用。加强中学生社团、红领巾社团、首都少年先锋岗、少年科学院、七色光鼓号队等组织建设，不断丰富少先队员、共青团员培育和践行社会主义核心价值观的实践平台。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　六、确保培育和践行社会主义核心价值观各项任务落到实处</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　16.加强组织领导。各区县政府、市政府各部门和各有关单位，要高度重视中小学培育和践行社会主义核心价值观工作，纳入总体工作安排，加强统筹谋划，及时研究解决工作中的重点难点问题，明确职责分工，形成齐抓共管、全面推进的工作格局。市教育主管部门要将培育和践行社会主义核心价值观作为立德树人、全面深化教育综合改革的一项重要工作，纳入教育规划和年度工作计划，加强对区县教育主管部门和学校的工作指导。市宣传、精神文明、人力社保、文化、新闻出版广电、文物和工会、共青团、妇联等有关部门和单位，要按照职责分工，发挥各自优势，积极支持、全面保障在中小学培育和践行社会主义核心价值观各项工作有效开展。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　17.强化责任落实。各区县教育主管部门要统筹落实好本地区中小学培育和践行社会主义核心价值观各项任务，加强人员、经费和制度保障。各学校要把德育工作放在更加重要的位置，将培育和践行社会主义核心价值观纳入整体发展规划，贯穿于教学、管理、服务全过程，在抓紧、抓实、抓细上下功夫。校长作为第一责任人，要结合本校实际，组织制定落实方案，做到有目标、有要求、有措施、有进度、有保障、有评价。学校各级党组织要充分发挥政治核心和战斗堡垒作用，落实上级党组织要求，加强党员教育管理，引导党员教师在培育和践行社会主义核心价值观中发挥先锋模范作用。学校教职工代表大会要发挥好民主管理和民主监督作用，推动社会主义核心价值观在学校各项工作中落到实处。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\">　　18.强化监督检查。市政府各有关部门和单位要根据各自职责建立监督检查机制和制度，检查各项责任和任务落实情况。将培育和践行社会主义核心价值观工作纳入教育工作和学校育人工作进行综合督导，保证市、区县教育主管部门和学校落实主体责任，切实将工作抓实抓好。加强总结和交流，建立领导干部联系学校制度，深入学校开展实地调研和督查，及时发现先进经验和先进典型，研究解决工作中的困难和问题，定期开展培育和践行社会主义核心价值观工作研讨交流，总结经验，改进工作，把握规律，努力构建长效机制。</span></p>\r\n', '0', '1514339313', '1514339313', '', '', '0', null, '0', '0', '[100]', null, '', '15');
INSERT INTO `o2o_article` VALUES ('371', '0', '', '北京市科学技术普及条例', null, null, null, null, null, null, '0', '0', '中科教', '市科学技术行政部门', '', null, '', '<p><strong style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第一章　总则</strong><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第一条　为实施科教兴国和可持续发展战略，加强科学技术普及工作，提高公众的科学文化素质，根据本市的实际情况，制定本条例。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第二条　本条例所称的科学技术普及（以下简称科普），是指采用公众易于理解和接受的方式向其传播科学技术知识、科学方法和科学思想。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第三条　本条例适用于本市行政区域内的国家机关、社会团体、企业、事 业单位、其他组织以及公民的科普活动。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第四条　科普活动应当坚持经常性和群众性的原则，结合首都经济、社会 发展的需要以及群众工作、生活的实际，充分利用各种现代化手段，通过多种 渠道、多种形式、多个层次开展。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第五条　科普工作应当坚持科学态度。在科普活动中不得将违背科学原则 和科学精神或者尚无科学定论的主张或者意见，作为科学知识传播和推广。禁止以科学为名从事封建迷信、反科学、伪科学的活动。禁止以科学为名传播不健康、不文明的生活方式和有损社会公共利益的内容。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第六条　市和区、县人民政府应当制定政策，鼓励和支持社会力量兴办科普事业，促进科普工作国内、国际间的合作与交流。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第七条　各单位应当组织或者支持本单位人员接受科普教育。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第八条　本市公民有依法接受科普教育，参加科普活动的权利。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">未成年人的监护人有责任使未成年人接受科普教育。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<strong style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第二章　管理与组织</strong><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第九条　市和区、县人民政府应当加强对科普工作的领导，将科普工作纳 入国民经济和社会发展计划。市和区、县人民政府建立科普工作联席会议制度，加强对科普工作的指导和协调。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第十条　乡、镇人民政府应当组织开展辖区内的科普活动。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">街道办事处应当支持和协调辖区内的社区科普活动。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第十一条　市科学技术行政部门负责全市科普工作，其职责是制定科普工 作总体规划和年度计划，并进行政策引导、组织管理和监督检查。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第十二条　市人民政府有关部门应当根据各自职责，做好本市科普工作规 划和计划的实施工作。　</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第十三条　科学技术协会是科普工作的主要社会力量。市科学技术协会应 当组织所属学会、协会、研究会开展日常性、群众性科普活动，协助制定科普 工作的规划、计划，对各部门、各单位的科普工作进行业务指导。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第十四条　工会、共青团、妇联等群众团体应当结合职工、青少年、妇女 的特点，开展多种形式的科普活动。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<strong style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第三章　社会责任</strong><br />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第十五条　各单位应当建立领导干部学习现代科学知识、接受科普教育的 制度，并把学习情况纳入对其考核的范围。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第十六条　中小学校应当开展多种形式的科普活动，把提高青少年的科学 技术素质作为全面实施素质教育的重要内容。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">市和区、县青少年科技馆、少年宫等校外教育机构，应当组织开展青少年 专题科普活动和日常校外科普教育，并做好中小学科技辅导教师的培训工作。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第十七条　农村地区应当根据当地经济与社会发展的需要，围绕科学生产、文明生活，面向农民开展科普工作。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">各类农业技术推广（培训）机构、农业技术学校和农村专业技术协会，应当结合技术推广、技术培训向农民普及科学知识。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">乡、镇和村文化站、广播站等应当向农民宣传科学、文明的生产和生活方式。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第十八条　综合类和自然科学类报纸、刊物应当开设专栏，刊登科普文章；电影制作、发行、放映单位应当制作、发行、放映科普电影；广播电台、电视台应当开办固定科普宣传栏目，增加科普节目的播出时间。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第十九条　博物馆、图书馆、文化馆等社会公益场所应当结合自身业务， 增加科普宣传的内容。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">动物园、植物园、自然保护区等场所应当结合各自特点，开展有关环境、 生态和生物多样性保护等方面的科普活动。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第二十条　本市大型医院、公园、旅游景点、影剧院、体育场馆、商场、 车站、机场等公共场所的经营管理单位，应当根据各自特点，开展面向公众的 科普宣传。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第二十一条　适宜向公众开展科普宣传的科研机构、高等院校和企业的实 验室或者产车间等应当有组织地向社会开放。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第二十二条　本市于每年五月举办&ldquo;北京科技周&rdquo;活动，社会各界应当根 据&ldquo;北京科技周&rdquo;确定的主题开展科普活动。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<strong style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第四章　科普场所</strong><br />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第二十三条　天文馆、自然科学类博物馆、科技馆、青少年科技馆（活动 中心）等专业科普场所，应当充分发挥科普教育功能，面向公众开展科普活动，对中小学生给予优先和优惠。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第二十四条　国家投资兴建的科普场所、设施，禁止出租、出借或者以其 他形式改作他用。如有特殊情况需要临时改作他用的，须经上级主管部门批准，但不得改变其科普场所的基本性质，妨碍开展科普活动。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<strong style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第五章　科普工作者</strong><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第二十五条　本条例所称的科普工作者，包括专业和非专业科普工作者。 在科普场所中从事科普工作的人员和中小学科技辅导教师为专业科普工作者，其他从事科普工作的人员为非专业科普工作者。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第二十六条　科普工作者享有下列权利：</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">（一）依法创办或者参加科普组织，自主开展科普活动；</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">（二）从事科普研究、创作，参加科普学术交流；</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">（三）申请科普项目经费，获得科普创作、出版资金资助；</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">（四）评定专业技术职称并享受相应待遇；</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">（五）接受专业技术培训；</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">（六）对科普工作提出意见和建议。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第二十七条　科普工作者应当履行下列义务：</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">（一）坚持科学精神，遵守职业道德；</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">（二）按照本条例的规定，面向公众开展科普工作，传播科学技术知识、 科学方法和科学思想；</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">（三）抵制反科学、伪科学，同封建迷信、愚昧落后现象作斗争；</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">（四）加强现代科学知识的学习，提高自身业务水平。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第二十八条　科普工作者依法抵制反科学、伪科学，同封建迷信、愚昧落后现象作斗争受法律保护。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第二十九条　申请进行市级科技成果鉴定或者申报科技进步奖的科技人员在提交科技成果的同时，应当提供介绍该成果或与该成果相关的科普文章，在鉴定通过或者获奖以后，以多种形式向公众进行宣传。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第三十条　科普工作者在参加相应系列的专业技术职称评审时，其所完成 的科普方面的作品和直接参与指导的科普竞赛成绩以及获得的科普奖励，应当作为晋升专业技术职称的依据之一。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<strong style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第六章　保障措施</strong><br />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第三十一条　市和区、县人民政府应当保证科普经费的投入，科普经费应 当列入同级财政预算，专款专用。市科普活动经费应当在科学事业费中列项，逐年增长，其增长幅度应当不 低于科学事业费的增长幅度。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">区、县科普活动经费应当按照本辖区常住人口每人每年0.5元的标准由区、县财政予以保证，并逐年增长。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第三十二条　本市以发展科学、教育为宗旨的基金会可以设立科普发展专项资金，用于资助科普读物的创作、出版、科普影视制作、科普理论研究以及 贫困地区的科普活动。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第三十三条　出版科技类图书、期刊，依照国家有关规定，可以享受税收优惠政策。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第三十四条　本市各级人民政府应当将科普基础设施建设纳入城市规划和基本建设计划，加快对科技馆、青少年科技馆、科普画廊等科普场所、设施的建设、改造、充实和利用。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第三十五条　本市各级人民政府应当采取措施，鼓励境内外组织和个人捐 助或者投资建设科普设施，开展科普活动。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第三十六条　本市各级人民政府和有关部门应当采取措施，逐步提高科普 工作者的待遇，改善科普工作者的工作和生活条件。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<strong style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第七章　奖励与处罚</strong><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第三十七条　本市有关行政部门和社会团体对在科普工作中作出突出贡献的集体和个人，给予表彰和奖励。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第三十八条　违反本条例第五条第二款规定，以科学为名从事封建迷信活 动，扰乱社会秩序，危害公共利益或者骗取财物尚不够刑事处罚的，由公安机 关依照《中华人民共和国治安管理处罚条例》予以处罚；构成犯罪的，依法追 究刑事责任。 　　</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第四十条　违反本条例第三十一条规定，挪用、克扣、截留科普经费的， 由上级机关责令限期改正，并对直接责任人员给予行政处分；构成犯罪的，依 法追究刑事责任。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第四十一条　违反本条例规定，侵害科普工作者和公民的合法权益并造成 损失的，依法承担民事责任。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<strong style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第八章　附　则</strong><br />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第四十二条　本条例具体应用中的问题，由市科学技术行政部门负责解释。</span><br style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\" />\r\n<span style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; text-align: justify; widows: 1; background-color: rgb(255, 255, 255);\">第四十三条　本条例自1999年1月1日起施行。</span></p>\r\n', '0', '1514339371', '1514339371', '', '', '0', null, '0', '0', '[100]', null, '', '15');
INSERT INTO `o2o_article` VALUES ('372', '0', '', '北京市教育委员会关于在义务教育阶段推行中小学生课外活动计划的通知', null, null, null, null, null, null, '0', '0', '中科教', '北京市教育委员会', '', null, '', '<p align=\"justify\" style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\"><span style=\"font-size: 10.5pt; line-height: 17.5px;\">各区县教委：<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></span></font></font></font></font><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\">　　</font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　（一）通过在全市中小学义务教育阶段推行课外活动计划，实施教育部在义务教育阶段体育、艺术&ldquo;<span style=\"font-size: 10.5pt; line-height: 17.5px;\">2+1</span>项目&rdquo;工程，培养我市中小学生在体育、艺术、科技等方面的兴趣和素养，进一步推进中小学生素质教育的全面实施。<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　（二）全面提高我市中小学生体质健康水平，不断提高《国家学生体质健康标准》达标率，中小学生逐步掌握至少两项体育技能。<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　（三）提升我市中小学生人文、艺术和科学素养，为学生终身发展和整体素质提高奠定良好基础。<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　（四）增强学生社会责任感、创新精神和实践能力，培养德智体美全面发展的社会主义建设者和接班人。<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　二、工作原则<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　（一）按照&ldquo;政府主导、社会支持、学校组织、学生自愿&rdquo;的原则，提倡通过政府&ldquo;购买社会服务&rdquo;的形式，开展体育、文艺、科普等形式多样的社团活动。<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　（二）遵循面向全体学生的原则，惠及义务教育阶段的中小学生，为满足学生个性发展的需求提供必要的条件和保障，促进学生健康快乐成长。<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　（三）坚持统筹协调的原则，与学校特色发展相结合，与课内外、校内外教育相结合、与素质提升工程和社会大课堂相结合。<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　（四）坚持资源共享的原则，区县通过统筹本区域校内外体育、艺术、科技场馆，以及各类资源，通过&ldquo;学区联合&rdquo;等形式，在发展本区域特色项目的基础上，协调引领学校开展活动。<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　三、实施办法<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　（一）时间安排<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　各区县、学校可在星期一至星期五<span style=\"font-size: 10.5pt; line-height: 17.5px;\">15:30</span>至<span style=\"font-size: 10.5pt; line-height: 17.5px;\">17:00</span>的课外时间安排活动，每周不少于<span style=\"font-size: 10.5pt; line-height: 17.5px;\">3</span>天，每天不低于<span style=\"font-size: 10.5pt; line-height: 17.5px;\">1</span>小时，具体时间安排由区县与学校自行制定。<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　（二）课外活动辅导教师的构成<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　<span style=\"font-size: 10.5pt; line-height: 17.5px;\">1.</span>符合学校开展活动需求的高等学校、具有资质的民办教育机构的教师；<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　<span style=\"font-size: 10.5pt; line-height: 17.5px;\">2.</span>体育俱乐部、少年宫、科技馆等校外机构的教练员及教师；<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　<span style=\"font-size: 10.5pt; line-height: 17.5px;\">3.</span>具有专业特长的各类人才，例如运动员、教练员、艺术家、科学家等各领域的专业人才；<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　<span style=\"font-size: 10.5pt; line-height: 17.5px;\">4.</span>符合条件的志愿者等。<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　（三）活动内容与形式<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　课外活动安排应形式多样，丰富多彩。活动内容与形式应与课程方案设置、学生综合实践活动，以及&ldquo;每天锻炼一小时&rdquo;有机结合。具体活动形式及内容包括：<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　<span style=\"font-size: 10.5pt; line-height: 17.5px;\">1.</span>组织学校或学区范围的活动和比赛<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　各区县可参照市级体育、艺术、科技比赛活动内容，组织班级、学校或学区范围的各类活动和比赛。<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　<span style=\"font-size: 10.5pt; line-height: 17.5px;\">2.</span>开展&ldquo;科学家进校园&rdquo;、&ldquo;体育明星进校园&rdquo;、&ldquo;艺术家进校园&rdquo;等系列活动<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　通过与科学家、运动员、艺术家的近距离接触，培养学生在体育、艺术、科技等方面的兴趣。<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　<span style=\"font-size: 10.5pt; line-height: 17.5px;\">3.</span>开设各类社团活动<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　创造条件让学生广泛参与各类体育比赛，艺术培训、艺术实践、观看演出，以及科技培训、体验等活动，培养学生的实践能力，提高各项技能水平。<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　四、职责要求<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　（一）由市教委牵头，联合市体育局、市文化局、市科协等相关委办局宏观指导、建立机制、投入经费、搭建平台，充分发挥区县和学校的积极性，指导各区县、学校大力推进本计划的实施。<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　（二）各区县教委牵头，统筹协调区体育局、区文化局、区科协等相关委办局，根据本区县实际情况及特色发展需要，因地制宜，制定本区县课外活动计划及实施方案，并报市教委备案。各区县要精心设计，认真组织实施，在本区域内合理统筹资源，建立校外教育兼职教师资源库，鼓励区县建立学区和区域特色联盟，实现资源共享。<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　（三）各学校要成立课外活动工作小组。按照市教委和所在区县的要求，自行制定本校课外活动计划的具体方案，并向家长公布。学校负责课外活动计划的具体实施，要充分利用校内教师的优势及特长，担任组织管理、协助授课等辅助性工作。<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　（四）各区县和学校要建立完善的校外人员聘用制度，可采取区县或学区与校外教育机构、学校与校外兼职教师签订相关协议的方式开展工作。<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　（五）各区县结合中小学实际研究制定实施方案，并于<span style=\"font-size: 10.5pt; line-height: 17.5px;\">2014</span>年<span style=\"font-size: 10.5pt; line-height: 17.5px;\">2</span>月<span style=\"font-size: 10.5pt; line-height: 17.5px;\">20</span>日前报市教委体卫艺处备案。<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　五、经费使用<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　（一）拨款方式<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　市财政按照义务教育阶段实际在校生人数，城区生均每年<span style=\"font-size: 10.5pt; line-height: 17.5px;\">400</span>元，远郊区县生均每年<span style=\"font-size: 10.5pt; line-height: 17.5px;\">500</span>元标准将经费拨至各区县，由区县统筹安排使用。<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　（二）使用范围<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　经费使用主要用于学生体育、艺术、科技等活动。包括外聘教师费用、场地租用费、低值易耗品购置费，以及购买社会服务产生的费用等。<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　（三）经费管理<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　各区县、学校要严格按照市教委和市财政局制定的《北京市中小学生课外活动计划资金使用管理办法》（另行下发）和财务制度支出，专款专用，不得截留、挤占和挪用，努力提高资金使用效益。<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　六、安全保障<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　各区县、学校要树立&ldquo;安全第一&rdquo;的指导思想，加强安全教育和管理工作，建立课外活动安全管理制度。各学校要做好活动安全预案，有效利用校方责任保险和校方无过失责任保险，保障课外活动的顺利实施。要明确课外活动辅导教师的聘任程序和法律责任，依法守法，确保安全。<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　七、督导检查<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　为确保中小学生课外活动计划落到实处，学校要因地制宜制定详细的工作规划、活动计划，并科学设置开展活动的项目。各区县要定期进行检查，并将检查结果上报市教委备案。市教委将对各区县和学校开展情况进行检查督导，并将此项工作纳入督导范围，全程跟踪，检查实效。<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\"><font style=\"font-size: 10.5pt;\">　　八、舆论宣传<span style=\"font-size: 10.5pt; line-height: 17.5px;\">&nbsp;</span></font></font></font></font></p>\r\n\r\n<p style=\"color: rgb(66, 66, 66); font-family: 微软雅黑; font-size: 14px; line-height: 26px; widows: 1; background-color: rgb(255, 255, 255);\"><font face=\"宋体\"><font style=\"font-size: 10.5pt;\"><font style=\"line-height: 17.5px;\">　　宣传部门要积极做好课外活动计划的宣传工作，努力营造良好的舆论环境，引导社会和家长积极配合此项工作的开展，确保此项工作顺利推进。</font></font></font></p>\r\n', '0', '1514339444', '1514339444', '', '', '0', null, '0', '0', '[100]', null, '', '15');
