INSERT INTO `o2o_goods_days` VALUES ('3398', '140', '', '第一课', '初步了解晶体、准晶体与非晶质体，空间格子的概念等。以及晶体的六大基本性质：自限性，均一性，异向性，对称性，最小内能，稳定性。晶体对称与双晶。', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3399', '140', '', '第二课', '晶体的分类：原子晶体，分子晶体，离子晶体，金属晶体。分类的标准，以及不同晶体类别的鉴定特征，和在生活中的举例。', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3400', '140', '', '第三课', '晶体生长模型：1）晶体生长的3种途径（一向生长、二向生长、三向生长）；2）层生长模型；螺旋生长模型；3）影响晶体生长的外因 4）研究晶体生长的意义；观看视频，看晶体生长方式。', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3401', '140', '', '第四课', '了解晶系的分类，等轴晶系和非等轴晶系（三斜、单斜晶系），以及不同晶系的识别标志，如晶型，晶轴，轴夹角等。不同晶系对应的矿物，如黄铁矿为等轴晶系，云母属于非等轴晶系。利用矿标区分等轴矿物和非等轴矿物。', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3402', '140', '', '第五课', '了解矿物与矿物学的概念；矿物的形态；矿物的物理性质；矿物的化学成分； 矿物的分类与命名。观察各种各样的矿物照片', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3403', '140', '', '第六课', '形成矿物的地质作用：内生作用，外生作用，变质作用。各种地质作用的概念，特征，识别标志，对岩石矿物的影响，以及地壳的化学组成，矿物的变化', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3404', '140', '', '第七课', '变质作用，三大类岩石的互相转化；矿物的变化化学成分的变化，矿物结构的变化，矿物外表和形态上的变化。', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3405', '140', '', '第八课', '矿物分类之自然元素矿物大类：金属元素，半金属元素，非金属元素。自然金，铜，铁的识别。硫化物及其类似化合物矿物大类', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3406', '140', '', '第九课', '氧化物与氢氧化物矿物大类：宝石类，磁铁矿等。卤化物矿物大类：萤石，石盐。有机矿物及准矿物大类：地蜡，琥珀，沥青，煤等', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3407', '140', '', '第十课', '含氧盐：硅酸盐（分类：岛；环；链；层；架），粘土矿物，滑石，云母，石榴子石等，杂盐。不同矿物的鉴别特征，以及珍贵的矿物哪些可以当做宝石。', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3408', '141', '', '第一课', '岩石、矿物和我们', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3409', '141', '', '第二课', '岩石的认知', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3410', '141', '', '第三课', '岩石标本制作', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3411', '141', '', '第四课', '岩石会变质吗？', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3412', '141', '', '第五课', '岩石会改变模样吗？', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3413', '141', '', '第六课', '走进石头王国', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3414', '141', '', '第七课', '矿物的真色和假色', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3415', '141', '', '第八课', '野外考察知识', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3416', '141', '', '第九课', '化石的认知', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3417', '141', '', '第十课', '琥珀的知识', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3418', '142', '', '第一课', '了解地球在宇宙中的位置与特殊性（唯一有人居住的地方），地球内部圈层结构（地核、地幔、地壳），板块的分类（七大板块）和运动状态（俯冲和碰撞作用），以及地质学、地质作用、地质奇观的概念。利用超轻粘土制作地球内部圈层结构。', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3419', '142', '', '第二课', '“沧海桑田-地球历史变迁”：地球存在了46亿年，按地质年代表了解地球的演化过程和地球中生命的演化历程，旨在让学生掌握地质年代表和特殊的古生物化石种类。', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3420', '142', '', '第三课', '板块俯冲作用会产生岩浆，岩浆喷发会形成火山，了解火山的概念、成因、结构（岩浆房、火山通道、火山口、火山锥），火山的利与弊，火山致命的几种原因，火山地质灾害的预防。', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3421', '142', '', '第四课', '“花岗岩地质景观”：花岗岩、山地、丘陵、岩石的概念；以及我国花岗岩地质景观的分布；及著名的景观，如黄山、华山等。', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3422', '142', '', '第五课', '“多彩的水晶世界”：晶洞的概念、成因（岩浆热液在空旷的空间中生成美丽的矿物），用途（摆件，科研用途），以及水晶晶洞中水晶的各种分类（粉晶、发晶、水胆水晶、玛瑙等）。', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3423', '142', '', '第六课', '板块之间在不停的运动，从而产生了褶皱，本次课旨在了解褶皱的概念及分类，以及褶皱的作用（为各种金属矿产、晶洞、石油的形成储藏的地方）。', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3424', '142', '', '第七课', '板块之间在不停的运动、从而产生了断层、本次课旨在了解断层的概念及分类（正断层，逆断层），以及断层的作用（产生地震等地质灾害）。', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3425', '142', '', '第八课', '地震、震源、震中、震中距、震级、烈度的概念，地震的产生原因（构造成因、火山成因、地壳塌陷、人为因素），地震的危害，地震的预防。', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3426', '142', '', '第九课', '“变质岩地质景观”：形成褶皱和断层的过程中会诱发岩石发生变化，形成变质岩本次课程旨在了解变质岩、变质作用的概念；以及变质岩地质景观的成因，和我国变质岩景观的分布；及著名的景观，如泰山。', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3427', '142', '', '第十课', '“美味的石头”：火山冷却会形成岩浆岩，岩浆岩经风化沉积作用形成沉积岩，岩浆岩和沉积岩经变质作用形成变质岩，本次课程旨在了解三大岩类的区别，以及详细讲述人为加工后制造的“石头宴”。', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3428', '143', '', '第一课', '了解地质外动力地质作用的概念，各种地貌的特征及识别标志，以及简单了解各种地质景观，如丹霞地质景观、砂岩峰林地质景观、喀斯特地质景观、其它地质景观（雅丹地质景观、土林地质景观、石窟、岩画及摩崖景观、陨石坠落遗址、重要地质构造遗迹、古人类遗迹、地质灾害遗迹）。', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3429', '143', '', '第二课', '地表水的特点与分类，地表水的下蚀作用和侧蚀作用的概念，以及河流对河床的改造作用（凹岸侵蚀、凸岸堆积和截弯取直的概念，形成牛轭湖或者月牙泉的成因）。', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3430', '143', '', '第三课', '“神奇的峡谷地质景观”：古人云“水滴石穿”，本次课旨在了解瀑布、湖泊、峡谷成因，以及分别认识中国最美五大湖泊，六大瀑布和十大峡谷。', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3431', '143', '', '第四课', '地下水的特点与分类，地下水的潜蚀和溶蚀作用，地下水对地下的改造作用，形成溶洞等。', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3432', '143', '', '第五课', '“神奇的喀斯特地貌”：溶洞的概念，溶洞的成因，钟乳石的形成过程，以及溶洞的组成（落水洞、石钟乳、石笋、石柱、石芽、溶沟、溶斗等），多层溶洞的成因等。', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3433', '143', '', '第六课', '“来自雪山的召唤”：积雪与冰川的关系，冰川的结构（冰斗、刃脊、角峰），冰川的利（刺激的户外活动，如登雪山，滑雪；冰川美景等）与弊（冰川裂缝、雪崩等），古冰川的识别标志（羊背石、冰川擦痕等），冰川遗址，以及古冰川和现今冰川的分布。', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3434', '143', '', '第七课', '风蚀作用（吹扬作用和磨蚀作用）、风化作用、球形风化的概念，平衡石的概念以及举例，矿物抗风化的能力，进而引出矿物摩氏硬度的概念，以及熟练掌握十种摩氏硬度典型矿物名称及鉴别特征，最后介绍干旱地区形成的矿物-石膏，美丽者为沙漠玫瑰。', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3435', '143', '', '第八课', '“神奇的雅丹地貌和丹霞地貌”：了解雅丹地貌和丹霞地貌的概念及二者的区别，了解全球各种地貌的美景，如陕北靖边波浪谷，以及干旱地区风的地质作用形成的神奇景观（风蚀柱、风蚀蘑菇、蜂窝石、风蚀湖等）。', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3436', '143', '', '第九课', '海洋的剥蚀作用概念，基岩海岸的海蚀地形的分类（海蚀穴、海蚀凹槽、海蚀崖、海蚀柱等）与识别标志，海岸线前进后退的识别标志。', null, '1');
INSERT INTO `o2o_goods_days` VALUES ('3437', '143', '', '第十课', '“神奇的像形岩、像形山”：了解大自然景观奇特的美，如南斯拉夫境内的睡猫，或者纳维亚半岛的狮身人面像，或者西伯利亚海域的“月亮和星星”岛，印度的睡佛山，贵州贞丰的双乳峰等。', null, '1');
