INSERT INTO `o2o_member` VALUES ('375', '陈丽华', 'b9d0a2487200c498f731df3d273a9c77', '18761838807', null, '1523839645', '1523839661', '61.155.110.130', '0', null, null, null, null, null);
INSERT INTO `o2o_member_apply_service` VALUES ('36', '湖北省赤壁市实验中学', '余先德', '湖北省赤壁市金鸡山路', '湖北省', 'http://cbsz.30edu.com.cn/', '赤壁市实验中学', '黄新阶', '男', '党总支书记', '07155222567', '13197421080', '944164187@qq.com', '谢七生', '男', '政教副主任', '07155222567', '13986639931', '2095341190@qq.com', '赤壁市实验中学创办于1974年,学校位于赤壁市城区金鸡山路51号，占地面积60亩。现有教职工142人，中共党员63人，特级教师1人，湖北名师1人，国家、省骨干教师8人，省教育科研学术带头人1人，中小学正高教师1人，高级教师42人，科技辅导员15人，本科学历82人，研究生3人。28个教学班，1812名学生。我校坚持以组织学生开展小发明、小论文等科技创新活动为重要载体，通过邀请中科院教授来校向学生进行科普报告，邀请省集邮专家来校向学生进行集邮知识讲座，组织学生参加市科普活动周等，着力培养学生创新能力、实践能力和学习能力。学校被评为赤壁市“市长杯”青少年科技创新优秀组织单位、赤壁市青少年科技创新实践基地、湖北科普示范学校、湖北省创新示范学校、全国科技教育创新优秀学校。\r\n以往主要成绩。近几年学生的7个项目获全国青少年科技创新大赛一、二、三等奖和“周培源青少年创新奖”“专项人才奖”，60多项获湖北省青少年科技创新大赛一、二、三等奖。其中2010年8月李辉等同学的“硫酸根离子干扰检验氯离子的实证研究”在第25届全国青少年科技创新大赛青少年科技创新成果终评展示活动中获得全国二等奖，2011年8月15日，国际化学年全国趣味', '1512703016', '259', '1', '中科教', '', '1512724816');
