INSERT INTO `o2o_op` VALUES ('214', '2016年学趣寒假冬令营“玩转尖端科技、享受无穷乐趣”', '201512110001', 'JQXW20160125-D', null, null, '20', '2016-01-25', '5', '待定', '1449800329', '1', '', '京区校外合作机构“旅途”是一家以户外科考为主的教育机构 合作过程中确定以合作的形式研发一期全托管冬令营项目 类似暑假学趣“五项全能”夏令营 ', null, null, '1', '冬令营方案已初步成型 需要研发部专家针对方案可实施程度进行评判 研发部徐恒根据活动方案中的项目找寻适合的专家讲解活动 其中石花洞当天由夏奎菊担任专家 鸟的抓握当天项目由胡逸萍担任专家最好  其他几天可自由安排专业、细心、亲和力好的老师 辅导老师需要两名 一男一女  活动过程中需一间可容纳40人的会议室 需用三天半 另需方餐车材料核价   ', '28', '张雪萍', null, null, null, null, '35', '1', '张雪萍', ' 旅途', '2015-12-11', '京区校外中心', '52', '43185.75', '', '55000', '', '55000', '', null);
