INSERT INTO `o2o_op` VALUES ('950', '中国科技馆一日社会实践活动', '201612300002', 'JQXN20170117A', null, null, '581', '2017-01-17', '1', '北京市', '1483075891', '1', '', '中关村中学清华园校区', null, null, '1', '1.任务单学校出，学生580左右，教师40左右。2.车17辆，45座以上车型。每车有纸质牌，上书：中关村中学X号车。当天每三车接一趟，每车配辅导员，辅导员穿戴统一标识。3.提前了解当天场馆接待学校数量，协调分门进行安检，与场馆协商简化安检程序。4.辅导员需要学物理化学相关专业的，能给学生讲解。咱们自己能出5人，还需要14个辅导员。5.当天学生自备午餐，老师在馆内安排盒饭，要求热的干净的，就餐环境温暖，提供热水。6.早八点二十出发，下午两点返回。', '68', '张曦', null, null, null, null, '28', '1', '张曦', '北京市中关村中学', '2016-12-30', '京区校内中心', '556', '39260.00', '', '43000', '', '45200', '', null);
