INSERT INTO `o2o_op` VALUES ('948', '沈阳师范大学附属学校--求真科学营北京行', '201612290001', 'JWYW20170220', null, null, '450', '2017-02-20', '5', '北京', '1482977325', '1', '', '此客户为第一次合作，并且来团人数巨多，超级vip客户，请多关照！\r\n此次来团人员构成为小学，初中，高中联合组团，同批抵达，人数比例为100:200:100，请研发部根据学龄段特性安排行程及院所活动。\r\n由于人数众多，住宿和餐食的压力巨大，请妥善安排好接待资源，把关细节，共同做好此次接待。\r\n感谢！', null, null, '1', '', '44', '许世伟', null, null, null, null, '44', '1', '许世伟', '沈阳师范大学附属学校', '2016-12-29', '京外业务中心', '543', '727452.00', '', '904300', '', '931100', '', '1488430693');
