INSERT INTO `o2o_op` VALUES ('1123', '求真科学营之周末营', '201705030003', null, null, null, '300', '2017-05-12', '3', '北京', '1493804149', '0', null, '2016年11月底12月初的周末活动在山东省影响较大，现准备于5月12日开启2017年的周末活动，预计每周都有', null, null, '1', '5月份准备开展三期，日期分别为5月12日---14日，5月19日---21日，5月26日---28日。每期分为小学、中学两个方案，每个方案150人，请研发部协调好院所活动、专家讲座、科技馆的通关文牒的设计及辅导员的安排。并请计调部协调好房、餐、车', '45', '赵鹏', null, null, null, null, '46', '1', '赵鹏', '山东省青少年科技活动中心', '2017-05-03', '京外业务中心', null, '0.00', null, null, null, null, null, null);
INSERT INTO `o2o_op` VALUES ('1124', '昆明求真科学营', '201705030004', null, null, null, '40', '2017-07-10', '6', '昆明', '1493804447', '0', null, '西安活动已经开展3年了，每年的活动都以做课题为主', null, null, '1', '昆明活动希望去掉昆明动物博物馆，增加课题性活动，在4天3晚的基础上变为6天5晚的活动，可否增加动手或体验的科技项目甚至是做成课题\r\n', '45', '赵鹏', null, null, null, null, '48', '1', '赵鹏', '西安新探索', '2017-05-03', '京外业务中心', null, '0.00', null, null, null, null, null, null);
INSERT INTO `o2o_op` VALUES ('1125', '福建求真科学营', '201705030005', null, null, null, '40', '2017-07-10', '5', '福州、厦门', '1493804840', '0', null, '西安活动已经开展3年，须进一步提升方案内容', null, null, '1', '希望能凸显福建的特色比如茶文化参观茶园、采茶、制茶另外厦门植物园也挺不错的，有全国最大的多肉植物馆，去年在三亚时有海洋保护、海滩垃圾分类、处理，我们也觉得挺好，我记得去厦门时发现退潮的现象非常明显，退潮时能够看到很多垃圾排放管道伸到海里的情景，让人触目惊心！        总之希望多做些实践性的课程', '45', '赵鹏', null, null, null, null, '48', '1', '赵鹏', '西安新探索', '2017-05-03', '京外业务中心', null, '0.00', null, null, null, null, null, null);
