INSERT INTO `o2o_op` VALUES ('1323', '三家店铁路中学中国科学院烟台生物4日考察课程', '201708100002', null, null, null, '10', '2017-10-13', '4', '烟台', '1502342927', '0', null, '门头沟区生物名师工作室主要是培养全区来自各个中高中的生物骨干教师，这些学校的老师对学校的影响非常的大，希望此次培训带领的为负责该区的销售，能够将门头沟的各个学校业务进行全面开展', null, null, '1', '10人左右教师培训经费是4万左右，线路是4天高铁往返，1.安排生物方向的院所参观或者一些小实验的操作、2.安排一天当地中学的交流（上午去学校听课、下午和学校老师进行深度交流）3.一天当地人文之类的考察（但是不能有景点和博物馆的设计体现在方案中，一定要是课程的形式）学校留言：十月中旬外出计划你先帮我设计一下，人数大概10左右，四天左右，最好有学校交流和实践活动内容，价格在4万以内', '100', '石曼', null, null, null, null, '45', '1', '石曼', '三家店铁路中学', '2017-08-10', '京区校内中心', null, '0.00', null, null, null, null, null, null);
