INSERT INTO `o2o_op` VALUES ('1347', '南京雏鹰小队活动--国家安全厅爱国主义教育', '201708220001', '20170826-1', null, null, '10', '2017-08-26', '0', '南京', '1503369950', '1', '', '雏鹰小队活动是南京小学较为推崇的班级小队活动，一般以爱国主义教育 绿色环保 公益出行等为主题的小队活动；一般操作模式是学校发通知，家委会自行组织。', null, null, '1', '1.活动内容：无字天书 实施行程：过程一 参观国家安全博物馆，南京项目部会安排参观和讲解部分，过程二 动手体验无字天书活动，南京项目部负责执行；\r\n2.需求：a.无字天书课程课件  b.活动执行老师的培训和指导  c. 活动物资的采购和配发（柠檬、酒精灯、三脚架、石棉网、棉签、滤纸\r\n）', '109', '徐娜', null, null, null, null, '47', '1', '徐娜', '南师附中仙林分校小学部2015级家委会', '2017-08-22', '南京项目部经理', '756', '568.66', '', '0', '', '0', '', null);
