INSERT INTO `o2o_op` VALUES ('1736', '南京古生物博物馆一日活动', '201801260001', 'NJXM20180206', null, null, '30', '2018-02-06', '1', '南京', '1516936621', '1', '', '金陵晚报乐童亲子营 首次合作一日活动  为后续活动推广打下基础。', null, null, '1', '【行程安排】\r\n上午\r\n08:50—09:00集合签到\r\n 09:00—09：20开营仪式\r\n09:30—10:00 观看3D电影\r\n 10:00—10:30 揭秘澄江动物群\r\n 10:30—11:20 实践探究：谁是澄江动物群之王\r\n11:20—11:40 实践知识汇总 \r\n 11:40—13:00午休 休息\r\n下午         \r\n13:00—15:40 游览博物馆，寻找不同年代出现的不同生物\r\n13:50—14:30 探索爬行动物之最—恐龙的传奇\r\n14:30—15:00 实践活动：现代技术与恐龙', '111', '乔娜丽', null, null, null, null, '47', '1', '乔娜丽', '金陵晚报乐童亲子营', '2018-01-26', '南京项目部主管', '1001', '2156.50', '', '0', '', '0', '', null);
