INSERT INTO `o2o_op` VALUES ('1654', '科技教师培训可行性调研 项目', '201712200001', null, null, null, '30', '2017-07-26', '30', '西安、南京', '1513741358', '0', null, '一、项目意义\r\n充分发挥老科学家科普演讲团在科普及科学教育方面的优势，为地方一线科学课教师提供有针对性的科学素养、科学实施能力等方面的提升培训课程及教学方案、配套教辅材料等。力图通过相关培训，更新教师的科学知识、扩展教师的科学视野、提升教师的科学素养，使他们能更好地掌握科学思维工具、养成科学思维习惯、增强科学教育水平，进而探索青少年科学教育新机制。\r\n', null, null, '1', '', '100', '石曼', null, null, null, null, '32', '1', '石曼', '中国科学院行政管理局', '2017-12-20', '京区校内中心经理', '375', '9158.00', '', '11905', '', '13737', '', null);
