INSERT INTO `o2o_op` VALUES ('1693', '南京光华东街小学寒假活动', '201801080004', 'NJXM20180126', null, null, '30', '2018-01-26', '3', '南京', '1515404762', '1', '', '光华东街小学筹建于一九七五年，一九七八年落成并招生开班。学校占地面积8744平方米，校舍面积4568平方米。\r\n近几年来，学校坚持“实施校园大艺术教育，使每一个学生都得到全面、和谐、富有个性的发展”的教育理念，坚持育人为本，遵循“科研兴教，科研兴校”，采用科学规范化的管理，高效灵活的运行机制，全新的教育模式，形成了学校“大艺术教育”的办学特色，学校先后获得了国家级课题实验研究先进学校、中国少年科学院科普基地、江苏省实验小学、江苏省红旗大队、江苏省绿色学校、江苏省少科院建设与发展研究实验学校、南京市德育先进学校、南京市教育科学研究基地等20多项以学校命名的荣誉称号。', null, null, '1', '寒假植物为主题的科学冬令营；分专家讲座+科学小课堂+户外实践三方面', '109', '徐娜', null, null, null, null, '47', '1', '徐娜', '南京光华东街小学', '2018-01-08', '南京项目部副经理', '971', '2190.00', '', '0', '', '0', '', null);
