INSERT INTO `o2o_op` VALUES ('1747', '南京地质古生物半日活动', '201802050002', null, null, null, '15', '2018-02-10', '1', '南京', '1517823065', '0', null, '与仙小班级家长合作；【发现之旅】地球这么大，除了恐龙还有“其他”；你知道吗？我们的地球已经有46亿年了\r\n生命最初出现在海洋里面，又是什么原因导致了他们脱离了水域登上陆地呢？\r\n那些曾经称霸地球亿年之久的庞然大物，竟然会全部消失？\r\n                   和他们一起，解锁新的姿势', null, null, '1', '【活动特点】   \r\n①  解读地球的前生今世 \r\n②  聆听自然的神奇音符\r\n③  了解古生物的构造\r\n④  探索生命的起源发展\r\n【行程安排】\r\n下午\r\n13:00—13:20 集合签到 集合整队并进行分组，宣布活动安排和注意事项\r\n    13:30—14:10 同一个地球，不同的“他们” 了解生命诞生之初的地球环境，认识生物的“大变革”\r\n14:10—14:40 奇趣任务大探索 根据学习的知识，组队协作完成任务\r\n14:40—14:50 组队PK，揭秘答案\r\n      15:00—15:30 探索', '111', '乔娜丽', null, null, null, null, '47', '1', '乔娜丽', '仙林小学', '2018-02-05', '南京项目部主管', null, '0.00', null, null, null, null, null, null);
