-- ----------------------------
-- Table structure for `o2o_admin`
-- ----------------------------
DROP TABLE IF EXISTS `o2o_admin`;
CREATE TABLE `o2o_admin` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `username` varchar(40) NOT NULL COMMENT '用户名',
  `password` varchar(40) NOT NULL COMMENT '密码',
  `encrypt` varchar(6) NOT NULL COMMENT '密码校验码',
  `level` int(11) NOT NULL DEFAULT '0' COMMENT '用户等级',
  `avatar` varchar(128) DEFAULT NULL COMMENT '头像',
  `company` varchar(64) DEFAULT NULL COMMENT '单位',
  `department` varchar(32) DEFAULT NULL COMMENT '部门',
  `post` varchar(32) DEFAULT NULL COMMENT '职称',
  `speciality` text COMMENT '专业',
  `name` varchar(40) NOT NULL COMMENT '姓名',
  `sex` varchar(40) NOT NULL COMMENT '性别',
  `mobile` varchar(40) NOT NULL COMMENT '手机号码',
  `email` varchar(80) NOT NULL COMMENT '邮箱',
  `user_group` varchar(128) NOT NULL COMMENT '用户组',
  `input_time` int(10) NOT NULL COMMENT '注册时间',
  `update_time` int(10) NOT NULL COMMENT '资料更新时间',
  `roleid` int(10) NOT NULL COMMENT '角色',
  `parentid` int(10) NOT NULL COMMENT '上级ID',
  `status` int(11) NOT NULL COMMENT '状态，0为正常，1为停用',
  `lock` int(11) NOT NULL DEFAULT '0' COMMENT '是否开启锁屏',
  `last_login_time` int(11) DEFAULT NULL COMMENT '最近登录时间',
  `last_login_ip` int(11) DEFAULT NULL COMMENT '最近登录IP',
  `cour_apply` int(11) DEFAULT '0' COMMENT '课程免审，1为免审，0为需要审核',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=15 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='后台管理员表';

-- ----------------------------
-- Records of o2o_admin
-- ----------------------------

-- ----------------------------
-- Table structure for `o2o_appo_lecture`
-- ----------------------------
DROP TABLE IF EXISTS `o2o_appo_lecture`;
CREATE TABLE `o2o_appo_lecture` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `article_id` int(11) NOT NULL COMMENT '预约课程ID',
  `res_id` int(11) NOT NULL DEFAULT '0' COMMENT '专家ID',
  `article_title` varchar(30) DEFAULT NULL COMMENT '预约讲座名称',
  `mobile` char(11) DEFAULT NULL COMMENT '当前会员的手机号',
  `yq_name` varchar(20) DEFAULT NULL COMMENT '邀请者姓名',
  `unit_name` varchar(100) DEFAULT NULL COMMENT '邀请单位或学校名称 ,,用于手动添加约课信息时填写',
  `manager_name` varchar(20) DEFAULT NULL COMMENT '负责人姓名',
  `call_man` varchar(20) DEFAULT NULL COMMENT '联系人',
  `tel_num` char(11) DEFAULT NULL COMMENT '联系电话',
  `e_mail` varchar(50) DEFAULT NULL COMMENT '电子邮箱',
  `show_addr` varchar(100) DEFAULT NULL COMMENT '讲课地点',
  `listener` varchar(20) DEFAULT NULL COMMENT '听课对象',
  `in_day` datetime DEFAULT NULL COMMENT '讲课日期',
  `in_time` varchar(50) DEFAULT NULL COMMENT '预计讲课时间',
  `status` enum('0','1','2') DEFAULT '0' COMMENT '预约状态  0,预约中; 1,预约成功 ; 2 ,预约结束',
  `vague_keywords` varchar(200) DEFAULT NULL COMMENT '模糊查询关键字',
  `type` tinyint(4) DEFAULT '1' COMMENT '预约类型 1 精确预约 0 模糊预约',
  `pic` varchar(128) DEFAULT NULL COMMENT '对应老科学家的图片',
  `booking_user` int(11) NOT NULL COMMENT '预订者',
  `booking_time` int(11) NOT NULL COMMENT '下单时间',
  `verify_view` varchar(512) DEFAULT NULL COMMENT '审核人意见',
  `verify_name` varchar(32) DEFAULT NULL COMMENT '审核人',
  `verify_time` int(11) NOT NULL DEFAULT '0' COMMENT '审核时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=70 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of o2o_appo_lecture
-- ----------------------------

-- ----------------------------
-- Table structure for `o2o_appo_wots`
-- ----------------------------
DROP TABLE IF EXISTS `o2o_appo_wots`;
CREATE TABLE `o2o_appo_wots` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `task` varchar(64) DEFAULT NULL COMMENT '标题',
  `builddate` date DEFAULT NULL COMMENT '日期',
  `stime` varchar(32) NOT NULL,
  `start_time` datetime DEFAULT NULL COMMENT '开始时间',
  `end_time` datetime DEFAULT NULL COMMENT '结束时间',
  `duration` int(11) NOT NULL DEFAULT '0' COMMENT '预计时长',
  `cour_id` int(11) DEFAULT NULL COMMENT '课件ID',
  `teacher_id` int(11) DEFAULT NULL COMMENT '教师ID',
  `teacher` varchar(128) DEFAULT NULL COMMENT '教师',
  `stu_num` int(11) DEFAULT NULL COMMENT '预计学生人数',
  `site` varchar(128) DEFAULT NULL COMMENT '场地',
  `status` int(11) DEFAULT '0' COMMENT '是否同步，0未同步，1已同步',
  `create_time` int(11) DEFAULT NULL COMMENT '创建时间',
  `remarks` varchar(512) DEFAULT NULL,
  `order_id` int(11) NOT NULL COMMENT '预约记录ID',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=31 DEFAULT CHARSET=utf8 COMMENT='课程表';

-- ----------------------------
-- Records of o2o_appo_wots
-- ----------------------------

-- ----------------------------
-- Table structure for `o2o_article`
-- ----------------------------
DROP TABLE IF EXISTS `o2o_article`;
CREATE TABLE `o2o_article` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `sort` int(20) NOT NULL,
  `res_title` varchar(10) NOT NULL DEFAULT '' COMMENT '对应科学家姓名',
  `title` varchar(200) NOT NULL,
  `fields` varchar(256) DEFAULT NULL,
  `fit` varchar(128) DEFAULT NULL,
  `gongyi` varchar(32) DEFAULT NULL,
  `system` varchar(64) DEFAULT NULL,
  `city` varchar(64) DEFAULT NULL,
  `res_pro` varchar(128) DEFAULT NULL,
  `type` int(11) NOT NULL COMMENT '''0''=>''资讯文章'',''1''=>''专题'',''3''=>''专家讲座''',
  `expert_id` int(11) NOT NULL COMMENT '所属专家ID',
  `editor` varchar(32) NOT NULL,
  `source` varchar(100) NOT NULL,
  `keywords` varchar(200) NOT NULL,
  `showtag` varchar(512) DEFAULT NULL COMMENT '对外展示标签',
  `description` varchar(512) NOT NULL DEFAULT '',
  `content` text NOT NULL,
  `status` int(10) NOT NULL,
  `create_time` int(11) NOT NULL,
  `update_time` int(10) NOT NULL,
  `user_id` varchar(128) NOT NULL,
  `pic` varchar(128) NOT NULL,
  `pic_id` int(11) DEFAULT '0',
  `video` varchar(1024) DEFAULT NULL,
  `video_type` int(11) NOT NULL COMMENT '视频来源，0本地上传，1为第三方转载',
  `click` int(11) DEFAULT '0',
  `col` varchar(512) DEFAULT NULL,
  `org` varchar(256) DEFAULT NULL COMMENT '所属组织',
  `ext_links` varchar(512) DEFAULT NULL COMMENT '外部链接',
  `module` smallint(6) DEFAULT '0' COMMENT '用来区分新闻文章和旅行文章 , 14为旅行文章',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=442 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;

-- ----------------------------
-- Records of o2o_article
-- ----------------------------

-- ----------------------------
-- Table structure for `o2o_attachment`
-- ----------------------------
DROP TABLE IF EXISTS `o2o_attachment`;
CREATE TABLE `o2o_attachment` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `module` int(10) NOT NULL COMMENT '图片类型',
  `releid` int(10) NOT NULL COMMENT '相关ID',
  `catid` smallint(5) unsigned NOT NULL DEFAULT '0',
  `fileurl` varchar(250) NOT NULL DEFAULT '0',
  `filename` varchar(250) NOT NULL,
  `filepath` varchar(500) NOT NULL,
  `filesize` int(10) unsigned NOT NULL DEFAULT '0',
  `fileext` varchar(10) NOT NULL,
  `isimage` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `downloads` mediumint(8) unsigned NOT NULL DEFAULT '0',
  `userid` mediumint(8) unsigned NOT NULL DEFAULT '0',
  `uploadtime` int(10) unsigned NOT NULL DEFAULT '0',
  `uploadip` varchar(15) NOT NULL,
  `status` tinyint(1) NOT NULL DEFAULT '0',
  `hashcode` varchar(32) NOT NULL,
  `main` int(11) NOT NULL,
  `title` varchar(128) NOT NULL,
  `tag` varchar(128) NOT NULL,
  `description` varchar(256) NOT NULL,
  `linkurl` varchar(256) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `authcode` (`hashcode`),
  KEY `releid` (`releid`),
  KEY `module` (`module`)
) ENGINE=MyISAM AUTO_INCREMENT=1770 DEFAULT CHARSET=utf8;

