CREATE TABLE `o2o_config` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `conf_key` varchar(64) NOT NULL,
  `conf_val` text NOT NULL,
  `conf_explain` varchar(50) NOT NULL,
