-- ----------------------------
-- Table structure for `o2o_goods`
-- ----------------------------
DROP TABLE IF EXISTS `o2o_goods`;
CREATE TABLE `o2o_goods` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `sort` int(11) NOT NULL DEFAULT '0',
  `res_title` varchar(10) NOT NULL DEFAULT '' COMMENT '演讲科学家姓名',
  `title` varchar(128) DEFAULT NULL,
  `fields` varchar(256) DEFAULT NULL COMMENT '所属领域',
  `com_date` varchar(64) DEFAULT NULL COMMENT '开课日期',
  `start_date` varchar(32) DEFAULT NULL COMMENT '活动开始日期',
  `end_date` varchar(32) DEFAULT NULL COMMENT '活动结束日期',
  `quota` int(11) DEFAULT '0' COMMENT '每班限额人数',
  `days` varchar(10) NOT NULL DEFAULT '' COMMENT '活动天数',
  `fit` varchar(128) DEFAULT NULL COMMENT '适合学段',
  `target` varchar(1024) DEFAULT NULL COMMENT '课程目标',
  `content` varchar(2048) DEFAULT NULL COMMENT '活动内容',
  `cost` varchar(2048) DEFAULT NULL COMMENT '费用说明',
  `notice` varchar(2048) DEFAULT NULL COMMENT '注意事项',
  `security` varchar(2048) DEFAULT NULL COMMENT '安全说明',
  `tag` varchar(1024) DEFAULT NULL COMMENT '标签是否公益',
  `pic_id` int(11) DEFAULT '0',
  `pic` varchar(128) DEFAULT NULL,
  `create_time` int(11) DEFAULT '0' COMMENT '创建时间',
  `update_time` int(11) DEFAULT '0' COMMENT '最近更新时间',
  `kinds` varchar(512) DEFAULT NULL COMMENT '分类',
  `city` varchar(10) NOT NULL DEFAULT '' COMMENT '产品实施省市',
  `gongyi` varchar(128) DEFAULT NULL,
  `col` varchar(512) DEFAULT NULL,
  `system` varchar(30) DEFAULT NULL COMMENT '所属系统',
  `res_pro` varchar(30) DEFAULT NULL COMMENT '支持项目',
  `org` varchar(256) DEFAULT NULL COMMENT '所属组织',
  `plan` varchar(3000) DEFAULT NULL COMMENT '行程计划',
  `lights` varchar(1024) DEFAULT NULL COMMENT '课程亮点',
  `module` smallint(6) DEFAULT '0' COMMENT '科学课程 表格文章和非表格文章 , 9 表格文章',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=146 DEFAULT CHARSET=utf8;

