  `citys` varchar(256) DEFAULT NULL,
  `content` varchar(1024) DEFAULT NULL,
  `remarks` varchar(256) DEFAULT NULL,
  `module` smallint(6) DEFAULT NULL COMMENT '用来区分研学旅行和旅行文章,14 为旅行文章',
  `type` tinyint(4) DEFAULT '1' COMMENT '区分" 1 ==第1学期课程 和 2==第2学期课程"',
  PRIMARY KEY (`id`)
