  `last_login_time` int(11) DEFAULT NULL COMMENT '最近登录时间',
  `last_login_ip` varchar(128) DEFAULT NULL COMMENT '最近登录IP',
  `level` int(11) DEFAULT '0' COMMENT '用户等级，0普通用户，1支持服务校，2HHH工程校',
  `avatar` varchar(128) DEFAULT NULL COMMENT '头像地址',
  `school` varchar(128) DEFAULT NULL COMMENT '学校名称',
  `province` varchar(64) DEFAULT NULL COMMENT '所在省份',
  `city` varchar(64) DEFAULT NULL COMMENT '所在城市',
  `remark` varchar(256) DEFAULT NULL COMMENT '备注',
