-- ----------------------------
-- Table structure for `o2o_op`
-- ----------------------------
DROP TABLE IF EXISTS `o2o_op`;
CREATE TABLE `o2o_op` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `project` varchar(200) NOT NULL COMMENT '项目名称',
  `op_id` varchar(50) DEFAULT NULL COMMENT '项目序号',
  `group_id` varchar(50) DEFAULT NULL COMMENT '团号',
  `product_id` int(11) DEFAULT NULL COMMENT '产品模块ID',
  `product_name` varchar(128) DEFAULT NULL COMMENT '产品模块名称',
  `number` int(11) DEFAULT NULL COMMENT '人数',
  `departure` date DEFAULT NULL COMMENT '出发日期',
  `days` int(11) DEFAULT NULL COMMENT '天数',
  `destination` varchar(64) DEFAULT '' COMMENT '目的地',
  `create_time` int(11) DEFAULT NULL COMMENT '创建时间',
  `status` int(11) DEFAULT '0',
  `nogroup` varchar(200) DEFAULT NULL COMMENT '不成团原因',
  `context` text COMMENT '项目背景',
  `sale_cost` decimal(11,2) DEFAULT NULL COMMENT '销售单价',
  `peer_cost` decimal(11,2) DEFAULT NULL COMMENT '同行价',
  `audit_status` int(2) DEFAULT NULL,
  `remark` varchar(256) DEFAULT NULL COMMENT '备注',
  `create_user` int(11) DEFAULT NULL COMMENT '发布者ID',
  `create_user_name` varchar(32) DEFAULT NULL COMMENT '发布者',
  `income` decimal(10,2) DEFAULT NULL COMMENT '总收入',
  `expend` decimal(10,2) DEFAULT NULL COMMENT '总支出',
  `profit` int(11) DEFAULT NULL COMMENT '净利润',
  `profit_rate` decimal(10,2) DEFAULT NULL COMMENT '毛利率',
  `kind` int(10) DEFAULT '0' COMMENT '项目类型',
  `speed` int(2) DEFAULT '1',
  `sale_user` varchar(50) DEFAULT NULL COMMENT '销售人员',
  `customer` varchar(128) DEFAULT NULL COMMENT '客户单位',
  `op_create_date` date DEFAULT NULL COMMENT '立项日期',
  `op_create_user` varchar(50) DEFAULT NULL COMMENT '立项人',
  `line_id` int(11) DEFAULT NULL COMMENT '应用行程',
  `costacc` decimal(10,2) DEFAULT '0.00' COMMENT '预计成本',
  `costacc_unit` varchar(50) DEFAULT NULL,
  `costacc_min_price` int(11) DEFAULT NULL,
  `costacc_min_price_unit` varchar(50) DEFAULT NULL,
  `costacc_max_price` int(11) DEFAULT NULL,
  `costacc_max_price_unit` varchar(50) DEFAULT NULL,
  `app_material_time` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `op_id` (`op_id`),
  UNIQUE KEY `group_id` (`group_id`)
) ENGINE=MyISAM AUTO_INCREMENT=1751 DEFAULT CHARSET=utf8;

