CREATE TABLE `o2o_product` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `sort` int(11) NOT NULL DEFAULT '0',
  `title` varchar(128) NOT NULL,
  `fields` varchar(256) DEFAULT NULL,
  `fit` varchar(128) DEFAULT NULL,
  `gongyi` varchar(32) DEFAULT NULL,
  `system` varchar(64) DEFAULT NULL,
  `city` varchar(64) DEFAULT NULL,
  `res_pro` varchar(128) DEFAULT NULL,
  `keywords` varchar(256) NOT NULL,
  `price` decimal(10,2) NOT NULL,
  `pic` varchar(128) DEFAULT NULL,
  `pic_id` int(11) DEFAULT NULL,
  `content` text NOT NULL,
  `type` varchar(64) NOT NULL,
  `tag` varchar(128) NOT NULL,
  `create_time` int(11) NOT NULL,
  `update_time` int(11) NOT NULL,
  `editor` varchar(64) NOT NULL,
  `col` varchar(512) DEFAULT NULL,
  `org` varchar(256) DEFAULT NULL COMMENT '所属组织',
  `target` varchar(1024) DEFAULT NULL COMMENT '课程目标',
  `cost` varchar(2048) DEFAULT NULL COMMENT '所需资料',
  `lights` varchar(1024) DEFAULT NULL COMMENT '课程亮点',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=11 DEFAULT CHARSET=utf8 COMMENT='实物产品';

-- ----------------------------
-- Records of o2o_product
-- ----------------------------

-- ----------------------------
-- Table structure for `o2o_res`
-- ----------------------------
DROP TABLE IF EXISTS `o2o_res`;
CREATE TABLE `o2o_res` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `sort` int(11) NOT NULL DEFAULT '0' COMMENT '排序',
  `title` varchar(256) DEFAULT NULL COMMENT '专家名称',
  `type` int(11) NOT NULL DEFAULT '0',
  `keywords` varchar(256) DEFAULT NULL COMMENT '标签',
  `fields` varchar(256) DEFAULT NULL COMMENT '所在领域',
  `city` varchar(256) DEFAULT NULL COMMENT '地区',
  `system` varchar(256) DEFAULT NULL COMMENT '所属系统',
  `pro` varchar(256) DEFAULT NULL COMMENT '支持项目',
  `content` text COMMENT '介绍',
  `update_time` int(11) NOT NULL DEFAULT '0' COMMENT '更新时间',
  `create_time` int(11) NOT NULL DEFAULT '0' COMMENT '发布时间',
  `pic` varchar(128) DEFAULT NULL,
  `pic_id` int(11) NOT NULL DEFAULT '0',
  `ss_city` varchar(10) NOT NULL DEFAULT '' COMMENT '演讲实施省市',
  `fit` varchar(30) NOT NULL DEFAULT '' COMMENT '适合人群',
  `tag` varchar(5) NOT NULL DEFAULT '' COMMENT '是否公益',
  `col` varchar(512) DEFAULT NULL,
  `cms_exp_id` int(11) NOT NULL DEFAULT '0' COMMENT '关联CMS系统专家ID',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=166 DEFAULT CHARSET=utf8 COMMENT='资源';

-- ----------------------------
-- Records of o2o_res
-- ----------------------------

-- ----------------------------
-- Table structure for `o2o_role`
-- ----------------------------
DROP TABLE IF EXISTS `o2o_role`;
CREATE TABLE `o2o_role` (
  `id` smallint(6) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(64) NOT NULL,
  `pid` smallint(6) DEFAULT NULL,
  `status` tinyint(1) unsigned DEFAULT NULL,
  `remark` varchar(255) DEFAULT NULL,
  `parentid` int(11) DEFAULT NULL COMMENT '父级角色',
  PRIMARY KEY (`id`),
  KEY `pid` (`pid`),
  KEY `status` (`status`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COMMENT='角色表';

-- ----------------------------
-- Records of o2o_role
-- ----------------------------

-- ----------------------------
-- Table structure for `o2o_role_user`
-- ----------------------------
DROP TABLE IF EXISTS `o2o_role_user`;
CREATE TABLE `o2o_role_user` (
  `role_id` mediumint(9) unsigned DEFAULT NULL,
  `user_id` char(32) DEFAULT NULL,
  KEY `group_id` (`role_id`),
  KEY `user_id` (`user_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='用户角色关系表';

-- ----------------------------
-- Records of o2o_role_user
-- ----------------------------

-- ----------------------------
-- Table structure for `o2o_sci`
-- ----------------------------
DROP TABLE IF EXISTS `o2o_sci`;
CREATE TABLE `o2o_sci` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `sort` int(11) NOT NULL DEFAULT '0',
  `title` varchar(128) NOT NULL,
  `fields` varchar(256) DEFAULT NULL,
  `fit` varchar(128) DEFAULT NULL,
  `gongyi` varchar(32) DEFAULT NULL,
  `system` varchar(64) DEFAULT NULL,
  `city` varchar(64) DEFAULT NULL,
  `res_pro` varchar(128) DEFAULT NULL,
  `keywords` varchar(256) NOT NULL,
  `price` decimal(10,2) NOT NULL,
  `pic` varchar(128) DEFAULT NULL,
  `pic_id` int(11) DEFAULT NULL,
  `content` text NOT NULL,
  `type` varchar(64) NOT NULL,
  `tag` varchar(128) NOT NULL,
  `create_time` int(11) NOT NULL,
  `update_time` int(11) NOT NULL,
  `editor` varchar(64) NOT NULL,
  `col` varchar(512) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=138 DEFAULT CHARSET=utf8 COMMENT='实物产品';

-- ----------------------------
-- Records of o2o_sci
-- ----------------------------

-- ----------------------------
-- Table structure for `o2o_sci_lecture`
-- ----------------------------
DROP TABLE IF EXISTS `o2o_sci_lecture`;
CREATE TABLE `o2o_sci_lecture` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `sci_id` varchar(60) DEFAULT NULL COMMENT '科技节ID',
  `sci_title` varchar(30) DEFAULT NULL COMMENT '科技节名称',
  `userid` int(11) NOT NULL DEFAULT '0' COMMENT '当前用户id',
  `mobile` char(11) DEFAULT NULL COMMENT '当前会员的手机号',
  `school_name` varchar(50) DEFAULT NULL COMMENT '预约讲座的学校名称',
  `manager_name` varchar(20) DEFAULT NULL COMMENT '负责人姓名',
  `call_man` varchar(20) DEFAULT NULL COMMENT '联系人',
  `tel_num` char(11) DEFAULT NULL COMMENT '联系电话',
  `e_mail` varchar(50) DEFAULT NULL COMMENT '电子邮箱',
  `show_addr` varchar(100) DEFAULT NULL COMMENT '讲课地点',
  `listener` varchar(20) DEFAULT NULL COMMENT '听课对象',
  `in_day` int(11) DEFAULT NULL COMMENT '讲课日期',
  `in_time` varchar(50) DEFAULT NULL COMMENT '预计讲课时间',
  `status` int(4) DEFAULT '0' COMMENT '预约状态  0,预约中; 1,预约成功 ; 2 ,预约结束',
  `pic` varchar(128) DEFAULT NULL COMMENT '对应科技节的图片',
  `booking_user` int(11) NOT NULL COMMENT '预订者',
  `booking_time` int(11) NOT NULL COMMENT '下单时间',
  `verify_view` varchar(512) DEFAULT NULL COMMENT '审核人意见',
  `verify_name` varchar(32) DEFAULT NULL COMMENT '审核人',
  `verify_time` int(11) NOT NULL DEFAULT '0' COMMENT '审核时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of o2o_sci_lecture
-- ----------------------------

-- ----------------------------
-- Table structure for `o2o_sms_send_record`
-- ----------------------------
DROP TABLE IF EXISTS `o2o_sms_send_record`;
CREATE TABLE `o2o_sms_send_record` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `mobile` varchar(32) DEFAULT NULL COMMENT '手机号',
  `templet` varchar(32) DEFAULT NULL COMMENT '发送模板',
  `content` varchar(128) DEFAULT NULL COMMENT '发送内容',
  `send_time` int(11) DEFAULT NULL COMMENT '发送时间',
  `status` varchar(256) DEFAULT NULL COMMENT '发送状态',
  `send_user` int(11) DEFAULT NULL COMMENT '发送者',
  `send_user_name` varchar(64) DEFAULT NULL COMMENT '发送者姓名',
  `send_type` int(11) DEFAULT '0' COMMENT '0.后台发送，1.o2o前端发送',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=644 DEFAULT CHARSET=utf8 COMMENT='短信发送记录';

-- ----------------------------
-- Records of o2o_sms_send_record
-- ----------------------------

-- ----------------------------
-- Table structure for `o2o_train_plan`
-- ----------------------------
DROP TABLE IF EXISTS `o2o_train_plan`;
CREATE TABLE `o2o_train_plan` (
  `train_id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `plan_id` int(11) NOT NULL,
  `task` varchar(64) DEFAULT NULL COMMENT '标题',
  `builddate` date DEFAULT NULL COMMENT '日期',
  `start_time` datetime DEFAULT NULL COMMENT '开始时间',
  `end_time` datetime DEFAULT NULL COMMENT '结束时间',
  `cour_id` int(11) DEFAULT NULL COMMENT '课件ID',
  `cour_type` int(11) NOT NULL DEFAULT '0' COMMENT '课件来源，0=>排课系统，1=>O2O系统',
  `teacher_id` int(11) DEFAULT NULL COMMENT '教师ID',
  `teacher` varchar(128) DEFAULT NULL COMMENT '教师',
  `stu_num` int(11) DEFAULT NULL COMMENT '预计学生人数',
  `site_id` int(11) DEFAULT NULL COMMENT '场地ID',
  `site` varchar(128) DEFAULT NULL COMMENT '场地',
  `to_num` int(11) DEFAULT NULL COMMENT '实到人数',
  `status` int(11) DEFAULT '0' COMMENT '状态，是否开课',
  `create_time` int(11) DEFAULT NULL COMMENT '创建时间',
  `remarks` varchar(512) DEFAULT NULL,
  `sign_code` varchar(64) DEFAULT NULL COMMENT '签到密钥',
  `duration` int(11) NOT NULL COMMENT '时长',
  `confirm_user_id` int(11) DEFAULT NULL COMMENT '确认者ID',
  `confirm_user_name` varchar(128) DEFAULT NULL COMMENT '确认者',
  `confirm_time` int(11) DEFAULT NULL COMMENT '确认时间',
  `cost_course` int(11) DEFAULT NULL COMMENT '课程研发费用',
  `cost_teacher` int(11) DEFAULT NULL COMMENT '讲师费用',
  `cost_ex_user` int(11) DEFAULT NULL COMMENT '费用审核者ID',
  `cost_ex_user_name` varchar(128) DEFAULT NULL COMMENT '费用审核者姓名',
  `cost_ex_time` int(11) DEFAULT NULL COMMENT '费用审核时间',
  `settle_time` int(11) NOT NULL COMMENT '结算时间',
  `order_id` int(11) NOT NULL DEFAULT '0' COMMENT 'O2O预约记录编号',
  PRIMARY KEY (`train_id`)
) ENGINE=MyISAM AUTO_INCREMENT=31 DEFAULT CHARSET=utf8 COMMENT='课程表';

-- ----------------------------
-- Records of o2o_train_plan
-- ----------------------------

-- ----------------------------
-- Table structure for `o2o_tra_lecture`
-- ----------------------------
DROP TABLE IF EXISTS `o2o_tra_lecture`;
CREATE TABLE `o2o_tra_lecture` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `goods_id` int(11) NOT NULL COMMENT '预约商品ID',
  `goods_title` varchar(30) DEFAULT NULL COMMENT '预约产品名称',
  `userid` int(11) DEFAULT NULL COMMENT '当前会员的id',
  `yq_name` varchar(20) DEFAULT NULL COMMENT '邀请单位',
  `manager_name` varchar(20) DEFAULT NULL COMMENT '负责人姓名',
  `call_man` varchar(20) DEFAULT NULL COMMENT '联系人',
  `tel_num` char(11) DEFAULT NULL COMMENT '联系电话',
  `e_mail` varchar(50) DEFAULT NULL COMMENT '电子邮箱',
  `listener` varchar(20) DEFAULT NULL COMMENT '听课对象',
  `in_day` int(11) DEFAULT NULL COMMENT '讲课日期',
  `status` enum('0','1','2') DEFAULT '0' COMMENT '预约状态  0,审核中; 1,审核成功 ; 2 ,审核失败',
  `type` tinyint(4) DEFAULT '1' COMMENT '预约类型 1 课程类 2 研学类',
  `pic` varchar(128) DEFAULT NULL COMMENT '对应老科学家的图片',
  `booking_time` int(11) NOT NULL COMMENT '下单时间',
  `verify_view` varchar(512) DEFAULT NULL COMMENT '审核人意见',
  `verify_name` varchar(32) DEFAULT NULL COMMENT '审核人',
  `verify_time` int(11) NOT NULL DEFAULT '0' COMMENT '审核时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 COMMENT='预约研学+科学课程表';

-- ----------------------------
-- Records of o2o_tra_lecture
-- ----------------------------

-- ----------------------------
-- Table structure for `o2o_zkj_counter`
-- ----------------------------
DROP TABLE IF EXISTS `o2o_zkj_counter`;
CREATE TABLE `o2o_zkj_counter` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `date` char(10) DEFAULT NULL COMMENT '日期',
  `o2o_counter` int(11) DEFAULT '0' COMMENT 'o2o网站流量',
  `u_ip` longtext COMMENT '访问者IP',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=31 DEFAULT CHARSET=utf8 COMMENT='中科教网站统计流量表';

